      "content": "pragma solidity 0.8.6;

import "ERC721.sol";
import "Ownable.sol";
import "ReentrancyGuard.sol";
import "Strings.sol";
import "Base64.sol";

/**
       //////////////////   \\\            ///    ///////////\\\    ///         \\\    //////////////   ////////////\\\
       /////////////////     \\\          ///    /////////////\\\   ///          ///   /////////////    /////////////\\\
       ///          ///       \\\        ///     ///          ///   ///          ///   ///              ///          ///
       ///         ///         \\\      ///      ///         ///    ///          ///   ///              ///         ///
       ///        ///           \\\    ///       ///        ///     ///          ///   ///              ///        ///
       ///                       \\\  ///        ///       ///      ///          ///   ///              ///       ///
       ///                        \\v///         ////////////       ////////////////   //////////////   ////////////
