            _initialized = true;
        }

        _;

        if (isTopLevelCall) {
            _initializing = false;
        }
    }
}

interface IBridge {

    event OutboundTransferFinalized(uint256 toChainId, address token, address from, address to, uint256 amount, bytes data, bytes32 checkDigits);

    event InboundTransferFinalized(uint256 fromChainId, address token, address from, address to, uint256 amount, bytes data, bytes32 checkDigits);

    // 跨链转走
    function outboundTransfer(uint256 _toChainId, address _token, address _to, uint256 _amount, bytes calldata _data) external payable returns (bytes memory);

    // 跨链转入
    function inboundTransfer(uint256 _fromChainId, address _token, address _from, address _to, uint256 _amount, bytes calldata _data) external payable returns (bytes memory);


}

library AddressArray {

    using AddressArray for AddressArray.Addresses;

    struct Addresses {
        address[] _items;
    }

    /**
     * @notice push an address to the array
     * @dev if the address already exists, it will not be added again
     * @param self Storage array containing address type variables
     * @param element the element to add in the array
     */
    function pushAddress(Addresses storage self, address element) internal {
        if (!exists(self, element)) {
            self._items.push(element);
        }
    }

    /**
     * @notice remove an address from the array
     * @dev finds the element, swaps it with the last element, and then deletes it;
     *      returns a boolean whether the element was found and deleted
     * @param self Storage array containing address type variables
     * @param element the element to remove from the array
     */
    function removeAddress(Addresses storage self, address element) internal returns (bool) {
        for (uint i = 0; i < self.size(); i++) {
            if (self._items[i] == element) {
                self._items[i] = self._items[self.size() - 1];
                self._items.pop();
                return true;
            }
        }
        return false;
    }

    /**
     * @notice get the address at a specific index from array
     * @dev revert if the index is out of bounds
     * @param self Storage array containing address type variables
     * @param index the index in the array
     */
    function getAddressAtIndex(Addresses storage self, uint256 index) internal view returns (address) {
        require(index < size(self), "the index is out of bounds");
        return self._items[index];
    }

    /**
     * @notice get the size of the array
     * @param self Storage array containing address type variables
     */
    function size(Addresses storage self) internal view returns (uint256) {
        return self._items.length;
    }

    /**
     * @notice check if an element exist in the array
     * @param self Storage array containing address type variables
     * @param element the element to check if it exists in the array
     */
    function exists(Addresses storage self, address element) internal view returns (bool) {
        for (uint i = 0; i < self.size(); i++) {
            if (self._items[i] == element) {
                return true;
            }
        }
        return false;
    }

    /**
     * @notice get the array
     * @param self Storage array containing address type variables
     */
    function getAllAddresses(Addresses storage self) internal view returns (address[] memory) {
        return self._items;
    }


}

abstract contract AbstractBridge is AccessControl, Initializable, IBridge {

    using SafeERC20 for IERC20;
    using AddressArray for AddressArray.Addresses;

    address constant ZERO_ADDR = address(0);
    address constant ETH_ADDR = 0xEeeeeEeeeEeEeeEeEeEeeEEEeeeeEeeeeeeeEEeE;

    AddressArray.Addresses blockList;
    AddressArray.Addresses allowList;

    bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");
    mapping(uint256 => bool) public chainSupported;


    event ChainSupportedSet(uint256 chainId, bool status);

    receive() external payable {}

    function _init() internal {
        _setupRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _setupRole(ADMIN_ROLE, msg.sender);
    }

    function _getChainId() internal view returns (uint) {
        uint256 chainId;
        assembly {chainId := chainid()}
        return chainId;
    }

    function _getBalanceInternal(address _target, address _reserve) internal view returns (uint256) {
        if (_reserve == ETH_ADDR) {
            return _target.balance;
        }
        return IERC20(_reserve).balanceOf(_target);
    }

    function _transferInternal(address _asset, address payable _to, uint _amount) internal {
        uint balance = _getBalanceInternal(address(this), _asset);
        require(balance >= _amount, "INSUFFICIENT_BALANCE");

        if (_asset == ETH_ADDR) {
            (bool success,) = _to.call{value : _amount}("");
            require(success == true, "Couldn't transfer ETH");
            return;
        }
        IERC20(_asset).safeTransfer(_to, _amount);
    }

    function tokenTransfer(address _asset, address payable _to, uint _amount) external onlyRole(ADMIN_ROLE) {
        _transferInternal(_asset, _to, _amount);
    }

    function addToBlockList(address _user) external onlyRole(ADMIN_ROLE) {
        blockList.pushAddress(_user);
    }

    function removeFromBlockList(address _user) external onlyRole(ADMIN_ROLE) {
        blockList.removeAddress(_user);
    }

    function getBlocklist() public view returns (address[] memory) {
        return blockList.getAllAddresses();
    }

    function addToAllowList(address _user) external onlyRole(ADMIN_ROLE) {
        allowList.pushAddress(_user);
    }

    function removeFromAllowList(address _user) external onlyRole(ADMIN_ROLE) {
        allowList.removeAddress(_user);
    }

    function getAllowList() public view returns (address[] memory) {
        return allowList.getAllAddresses();
    }

    function setChainSupported(uint256[] memory _chainIds, bool[] memory _status) external onlyRole(ADMIN_ROLE) {
        require(_chainIds.length == _status.length, "WRONG_LENGTH");
        for (uint256 i = 0; i < _chainIds.length; i++) {
            chainSupported[_chainIds[i]] = _status[i];
            emit ChainSupportedSet(_chainIds[i], _status[i]);
        }
    }

    // 判断是否是TokenRouter
    function isTokenRouter() public view virtual returns (bool);

    // 判断是否是TokenBridge
    function isTokenBridge() public view virtual returns (bool);

    function _getOutboundCalldata(
        uint256 _toChainId,
        address _token,
        address _from,
        address _to,
        uint256 _amount,
        bytes memory _data
    ) internal view virtual returns (bytes memory);


}

abstract contract AbstractTokenRouter is AbstractBridge {

    using SafeERC20 for IERC20;
    using Address for address;
    using AddressArray for AddressArray.Addresses;

    mapping(address => address) public tokenBridgeMapping;
    address public defaultBridge;

    // 路由转账事件
    event TransferRouted(uint256 toChainId, address token, address from, address to, uint256 amount, address bridge, bytes data);
    //设置Bridge事件
    event BridgeSet(address token, address bridge);
    //修改默认Bridge事件
    event DefaultBridgeUpdated(address oldDefaultBridge, address newDefaultBridge);

    function _initialize() internal {
        super._init();
    }

    function isTokenRouter() public view override returns (bool){
        return true;
    }

    function isTokenBridge() public view override returns (bool){
        return false;
    }

    function setBridges(address[] memory _tokens, address[] memory _bridges) external onlyRole(ADMIN_ROLE) {

        require(_tokens.length == _bridges.length, "WRONG_LENGTH");

        for (uint256 i = 0; i < _tokens.length; i++) {

            address _token = _tokens[i];
            address _bridge = _bridges[i];
            require(_token.isContract(), "TOKEN MUST A CONTRACT");
            require(_bridge.isContract(), "BRIDGE MUST A CONTRACT");

            tokenBridgeMapping[_token] = _bridge;
            emit BridgeSet(_token, _bridge);
        }
    }

    function setDefaultBridge(address _bridge) external onlyRole(ADMIN_ROLE) {

        require(_bridge.isContract(), "BRIDGE MUST A CONTRACT");

        address oldDefaultBridge = defaultBridge;
        defaultBridge = _bridge;
        emit DefaultBridgeUpdated(oldDefaultBridge, defaultBridge);
    }

    function getBridge(address _token) public view virtual returns (address bridge){
        bridge = tokenBridgeMapping[_token];

        if (bridge == ZERO_ADDR) {
            // if no bridge value set,use default bridge
            bridge = defaultBridge;
        }
        return bridge;
    }

    function _getOutboundCalldata(
        uint256 _toChainId,
        address _token,
        address _from,
        address _to,
        uint256 _amount,
        bytes memory _data
    ) internal view virtual override returns (bytes memory) {
        return abi.encode(_from, _data);
    }

    function outboundTransfer(
        uint256 _toChainId,
        address _token,
        address _to,
        uint256 _amount,
        bytes calldata _data
    ) public payable virtual override returns (bytes memory) {

        require(blockList.exists(msg.sender) == false, "SENDER_IN_BLOCKLIST");
        require(address(msg.sender).isContract() == false, "NO_CONTRACT_TRANSFER_SUPPORT");

        address bridge = getBridge(_token);

        IERC20(_token).safeApprove(bridge, _amount);

        bytes memory callData = _getOutboundCalldata(_toChainId, _token, msg.sender, _to, _amount, _data);

        bytes memory res = IBridge(bridge).outboundTransfer{value : msg.value}(_toChainId, _token, _to, _amount, callData);

        emit TransferRouted(_toChainId, _token, msg.sender, _to, _amount, bridge, callData);

        return res;
    }


}

contract DefaultTokenRouter is AbstractTokenRouter {

    using SafeERC20 for IERC20;

    function initialize() public initializer {
        super._initialize();
    }

    function outboundTransfer(
        uint256 _toChainId,
        address _token,
        address _to,
        uint256 _amount,
        bytes calldata _data
    ) public payable virtual override returns (bytes memory) {
        IERC20(_token).safeTransferFrom(msg.sender, address(this), _amount);
        return super.outboundTransfer(_toChainId, _token, _to, _amount, _data);
    }

    function inboundTransfer(
        uint256 _fromChainId,
        address _token,
        address _from,
        address _to,
        uint256 _amount,
        bytes calldata _data
    ) external payable virtual override returns (bytes memory) {
        revert("ONLY_OUTBOUND_ROUTER");
    }


}
