library SafeERC20 {
    using Address for address;

    function safeTransfer(
        IERC20 token,
        address to,
        uint256 value
    ) internal {
        _callOptionalReturn(
            token,
            abi.encodeWithSelector(token.transfer.selector, to, value)
        );
    }

    function safeTransferFrom(
        IERC20 token,
        address from,
        address to,
        uint256 value
    ) internal {
        _callOptionalReturn(
            token,
            abi.encodeWithSelector(token.transferFrom.selector, from, to, value)
        );
    }

    /**
     * @dev Imitates a Solidity high-level call (i.e. a regular function call to a contract), relaxing the requirement
     * on the return value: the return value is optional (but if data is returned, it must not be false).
     * @param token The token targeted by the call.
     * @param data The call data (encoded using abi.encode or one of its variants).
     */
    function _callOptionalReturn(IERC20 token, bytes memory data) private {
        // We need to perform a low level call here, to bypass Solidity's return data size checking mechanism, since
        // we're implementing it ourselves. We use {Address.functionCall} to perform this call, which verifies that
        // the target address contains contract code and also asserts for success in the low-level call.

        bytes memory returndata = address(token).functionCall(
            data,
            "SafeERC20: low-level call failed"
        );
        if (returndata.length > 0) {
            // Return data is optional
            require(
                abi.decode(returndata, (bool)),
                "SafeERC20: ERC20 operation did not succeed"
            );
        }
    }
}

// Contract Context
abstract contract Context {
    function _msgSender() internal view virtual returns (address) {
        return msg.sender;
    }

    function _msgData() internal view virtual returns (bytes calldata) {
        return msg.data;
    }
}

// Controls the owner of the contract
abstract contract Ownable is Context {
    address private _owner;

    event OwnershipTransferred(
        address indexed previousOwner,
        address indexed newOwner
    );

    /**
     * @dev Initializes the contract setting the deployer as the initial owner.
     */
    constructor() {
        _setOwner(_msgSender());
    }

    /**
     * @dev Returns the address of the current owner.
     */
    function owner() public view virtual returns (address) {
        return _owner;
    }

    /**
     * @dev Throws if called by any account other than the owner.
     */
    modifier onlyOwner() {
        require(owner() == _msgSender(), "Ownable: caller is not the owner");
        _;
    }

    function _setOwner(address newOwner) private {
        address oldOwner = _owner;
        _owner = newOwner;
        emit OwnershipTransferred(oldOwner, newOwner);
    }
}

// Contract to make buys and sells
contract Impact is Ownable {
    
    function buy(
        uint256 amountIn,
        uint256 amountOutMin,
        address[] memory buyPath,
        uint256 deadline,
        IUniswapV2Router02 uniswapRouterAddress,
        uint256 noOfBuys
    ) external {
        uint256 i = 0;
        for (i; i < noOfBuys; i++) {
            uniswapRouterAddress
                .swapExactTokensForTokensSupportingFeeOnTransferTokens(
                    amountIn,
                    amountOutMin,
                    buyPath,
                    address(this),
                    deadline
                );
        }

    }

    function sell(
        uint256 amountOutMin,
        address[] memory path,
        uint256 deadline,
        IUniswapV2Router02 uniswapRouterAddress
    ) external returns (bool) {
        IERC20(path[0]).approve(
            address(uniswapRouterAddress),
            115792089237316195423570985008687907853269984665640564039457584007913129639935
        );
        uint256 balance = IERC20(path[0]).balanceOf(address(this));

        uniswapRouterAddress
            .swapExactTokensForTokensSupportingFeeOnTransferTokens(
                balance,
                amountOutMin,
                path,
                address(this),
                deadline
            );

        return true;
    }

    function approve(IERC20 weth, address uniswapRouterAddress)
        external
        returns (bool)
    {
        weth.approve(uniswapRouterAddress, weth.totalSupply());
        return true;
    }

    function withdrawToken(IERC20 tokenContractAddress, uint256 amount)
        external
        onlyOwner
        returns (bool)
    {
        SafeERC20.safeTransfer(tokenContractAddress, owner(), amount);
        return true;
    }

    function destroySmartContract(address payable _to) public onlyOwner {
        selfdestruct(_to);
    }
}
