// File: contracts/TribeStaker.sol


pragma solidity ^0.8.0;
pragma experimental ABIEncoderV2;




interface DAC {
    function originalDacInfoOfId(uint256 _id)
        external
        returns (
            address originalOwner,
            uint256 id,
            uint256 code,
            uint256 power
        );

    function originalPieceInfoOfId(uint256 _id)
        external
        returns (
            address originalOwner,
            uint256 id,
            uint256 code,
            uint256 power
        );

    function isApprovedForAll(address account, address operator) external view returns (bool);

    function safeTransferFrom(
        address from,
        address to,
        uint256 id,
        uint256 amount,
        bytes calldata data
    ) external;
}

contract TribeStaker is Ownable, ERC1155Receiver {
    using SafeMath for uint256;

    struct NFTInfo {
        address nft;
        uint256 id;
        uint256 code;
        uint256 power;
    }

    mapping(address => bool) public stakedCreator;
    mapping(address => NFTInfo) private stakedNFT;
    DAC public babelTowerDAC;
    DAC public normalDAC;

    constructor(DAC _babelTowerDAC, DAC _normalDAC) {
        babelTowerDAC = _babelTowerDAC;
        normalDAC = _normalDAC;
    }

    function checkCreatorStakedInfo(address creator)
        external
        view
        returns (
            address nft,
            uint256 id,
            uint256 code,
            uint256 power
        )
    {
        nft = stakedNFT[creator].nft;
        id = stakedNFT[creator].id;
        code = stakedNFT[creator].code;
        power = stakedNFT[creator].power;
    }

    function stakeBabel(address creator, uint256 _id) external returns (bool) {
        require(!stakedCreator[creator], "TribeStaker: staked creator");
        require(babelTowerDAC.isApprovedForAll(msg.sender, address(this)), "TribeStaker: not approved");
        (, uint256 id, uint256 code, uint256 power) = babelTowerDAC.originalPieceInfoOfId(_id);
        stakedCreator[creator] = true;
        stakedNFT[creator] = NFTInfo({ nft: address(babelTowerDAC), id: id, code: code, power: power });
        babelTowerDAC.safeTransferFrom(msg.sender, address(this), id, 1, bytes("1"));
        emit TribeStaked(msg.sender, creator, address(babelTowerDAC), id, power);
        return true;
    }

    function stakeDAC(address creator, uint256 _id) external returns (bool) {
        require(!stakedCreator[creator], "TribeStaker: staked creator");
        require(normalDAC.isApprovedForAll(msg.sender, address(this)), "TribeStaker: not approved");
        (, uint256 id, uint256 code, uint256 power) = normalDAC.originalDacInfoOfId(_id);
        stakedCreator[creator] = true;
        stakedNFT[creator] = NFTInfo({ nft: address(normalDAC), id: id, code: code, power: power });
        normalDAC.safeTransferFrom(msg.sender, address(this), id, 1, bytes("1"));
        emit TribeStaked(msg.sender, creator, address(normalDAC), id, power);
        return true;
    }

    function onERC1155Received(
        address operator,
        address from,
        uint256 id,
        uint256 value,
        bytes calldata data
    ) external pure override returns (bytes4) {
        return this.onERC1155Received.selector;
    }

    function onERC1155BatchReceived(
        address operator,
        address from,
        uint256[] calldata ids,
        uint256[] calldata values,
        bytes calldata data
    ) external pure override returns (bytes4) {
        return this.onERC1155BatchReceived.selector;
    }

    event TribeStaked(
        address indexed sender,
        address indexed creator,
        address indexed nft,
        uint256 id,
        uint256 initialPower
    );
}
