    * @dev level checking   
    */
    function checkOniLevel(uint256[] calldata tokens, uint8 level) public view returns(bool) {
        uint256 validTokenCount = 0;
        for(uint256 i  = 0; i < tokens.length; i++) {
            if(oni[tokens[i]] == level) {
                validTokenCount++;
            }
        }
        
        if(tokens.length == validTokenCount) {
            return true;
        } else {
            return false;
        }
    }
