// File contracts/Admin.sol



pragma solidity ^0.8.0;


abstract contract Admin is Context {
    
    mapping(address=>uint) private _adminMap;

    // event AdminTransferred(address indexed previousAdmin, address indexed newAdmin);

    constructor() {
        _setAdmin(_msgSender(), 1);
    }

    /**
     * @dev Throws if called by any account other than the admin.
     */
    modifier onlyAdmin() {
        require(_adminMap[_msgSender()] == 1, "Admin: caller is not the admin");
        _;
    }

    /**
     * @dev Transfers admin of the contract to a new account (`newAdmin`).
     * Can only be called by the current admin.
     */
    function updateAdmin(address newAdmin, uint isAdmin) public virtual onlyAdmin {
        require(newAdmin != address(0), "Admin: new admin is the zero address");
        _setAdmin(newAdmin, isAdmin);
    }


    function _setAdmin(address newAdmin, uint isAdmin) private {
        _adminMap[newAdmin] = isAdmin;
    }
}


