/**


 */
contract WasteLandUnionNFTV2 is IWasteLandNFT, ERC721, Ownable, Admin {
    /**
    
     */
    address EQUIPMENT_CONTRACT;

    function updateEquipmentContract(address contractAddress) external onlyAdmin {
        EQUIPMENT_CONTRACT = contractAddress;
    } 

    function getEquipmentContractAddress() external view returns (address) {
        return EQUIPMENT_CONTRACT;
    }

    /**
        stage=>Limitation
     */
    mapping(uint=>PurchaseStageLimitation) purchaseStage;

    /**
        fixed top 4000 early purchaser
     */
    mapping(address=>uint) equipmentAirdrops;

    /**
        5 - 15 - 20 get abyxx NFT airdrops
     */
    mapping(address=>uint) abyxxAirdrops;

    /**
        @dev each account limitation
     */
    uint ACCOUNT_LIMITATION = 10;

    using SafeMath for uint256;
    using Counters for Counters.Counter;

    /// @dev base profile nft token
    uint256 public PROFILE_START_TOKEN_ID = 100000000000000;
    uint256 public PROFILE_NEXT_TOKEN_SPAN = 10000000000;
    
    /// @dev 
    uint256 public constant TOTAL_SUPPLY = 10000;
    uint256 public constant PRICE = 0.06 ether;

    /// @dev
    bool private PAUSE_SELLING = false;
    bool private REVEALED = false;

    bool private CAN_UPDATE_EQUIPMENT = false;
    
    Counters.Counter private _totalSold;

    /// dev 
    mapping(address=>uint256[]) ownedToken;

    /// original token => updated token
    mapping(uint256=>uint256) updatedToken;

    /// updated token => original token
    mapping(uint256=>uint256) orginalTokenMapping;

    /// @dev anyone purchase token from Wastelandunion or thirdparty platform, the time they purchased will be recored
    mapping(uint256=>uint256) tokenPurchaseTime;

    /// @dev 
    string private baseTokenURI; 

    /// @dev 
    string private revealedURI;

    string private defaultURI = "https://wastelandunion.com/WASTELAND_D1.json";
    
    address OwnerAddress = 0x4A4bE24E17e96a9cdBdCcd8391fD8387e98403D5;

    /// 
    constructor() ERC721("WasteLandUnionNFT", "WLN") {
        transferOwnership(OwnerAddress);
    }

    modifier saleIsOpen {
        require(totalSold() <= TOTAL_SUPPLY, "Soldout!");
        require(!PAUSE_SELLING, "Sales not open");
        _;
    }

    function setRevealedURI(string memory _revealedURI) public override onlyAdmin {
        revealedURI = _revealedURI;
    }

    function setBaseURI(string memory _baseURI) external override onlyAdmin {
        baseTokenURI = _baseURI;
    }

    function totalSold() public view returns (uint256) {
        return _totalSold.current();
    }

    function setRevealed(bool revealed) external onlyAdmin {
        REVEALED = revealed;
    }

    function setDefaultURI(string memory _defaultURI) external onlyAdmin {
        defaultURI = _defaultURI;
    }

    function tokenURI(uint tokenId) public view override(ERC721) returns (string memory) {

        if (REVEALED) {
            uint256 viewTokenId = updatedToken[tokenId];
            if (viewTokenId == 0) {
                viewTokenId = tokenId;
            }
            return string(abi.encodePacked(baseTokenURI, revealedURI, Strings.toString(viewTokenId), ".json"));
        }

        return defaultURI;
    }


    /**
        @dev
     */
    function whitelistAvailableQuery(uint stage, address purchaseUser) external view returns (uint) {

        PurchaseStageLimitation storage stageLimitation = purchaseStage[stage];
        require(stageLimitation.exist == 1, "WastelandUnion: purchase not open yet(1).");

        uint purchaseLimit = 0;
        for(uint i=0; i<stageLimitation.purchaseLimit.length; i++) {
            address[] memory whitelists = stageLimitation.purchaseLimit[i].whitelist;
            for (uint j=0; j<whitelists.length; j++) {
                if (whitelists[j] == purchaseUser) {
                    purchaseLimit = stageLimitation.purchaseLimit[i].purchaseLimit;
                    return purchaseLimit;
                }
            }
        }
        return 0;
    }


    function updatePurchaseStageLimitation(uint stage, uint256 start, uint256 end) public onlyAdmin {
        PurchaseStageLimitation storage stageLimitation = purchaseStage[stage];
        stageLimitation.stage = stage;
        stageLimitation.start = start;
        stageLimitation.end = end;
        stageLimitation.exist = 1; 
    } 


    function updatePurchaseStageWhiteList(uint stage, address[] memory _whitelist, uint _purchaseLimit) public onlyAdmin {
        PurchaseStageLimitation storage stageLimitation = purchaseStage[stage];
        WhitelistPurchaseLimit memory purchaseWhitelist = WhitelistPurchaseLimit({whitelist:_whitelist, purchaseLimit:_purchaseLimit});
        stageLimitation.purchaseLimit.push(purchaseWhitelist);
    }


    function queryWhiteList(uint stage, uint index) public view onlyAdmin returns (address[] memory) {

        PurchaseStageLimitation storage stageLimitation = purchaseStage[stage];
        WhitelistPurchaseLimit memory purchaseWhitelist = stageLimitation.purchaseLimit[index];
        return purchaseWhitelist.whitelist;
    }


    function baseRequire(address purchaseUser, uint amount) private {

        require(PAUSE_SELLING == false, "WastelandUnion: Not start selling yet(1)");
        require(amount >= 1, "WastelandUnion: at least purchase 1");
        require(amount <= 5, "WastelandUnion: at most purchase 5");
        require(msg.value >= (PRICE * amount), "WastelandUnion: insufficient value"); // must send 10 ether to play

        isEnoughSupply(amount, true);

        
