        }

        */

    }

    /**
     */
    function isEnoughSupply(uint amount, bool needReportError) private view returns (bool) {

        uint256 solded = totalSold();

        uint256 afterPurchase = solded + amount;
        if (needReportError) {
            require(afterPurchase <= TOTAL_SUPPLY, "WastelandUnion: Max limit");
            return true;
        } else {
            if (afterPurchase <= TOTAL_SUPPLY) {
                return true;
            } else {
                return false;
            }
        }
    }


    /**

     */
    function claimAirdropAmount(uint claimType) public view returns(uint) {
        address claimAddress = msg.sender;
        uint amount = 0;
        if (claimType == 1) {
            amount = equipmentAirdrops[claimAddress];

        } else if (claimType == 2) {
            amount = abyxxAirdrops[claimAddress];
        }

        // console.log(amount);
        return amount;
    }


    function removeToken(address owner, uint256 profileTokenId) private {
        uint[] storage tokens = ownedToken[owner];
        bool findIndex = false;
        for (uint i=0; i<tokens.length-1; i++){
            console.log("compare : %d, %d, %s", profileTokenId, tokens[i], findIndex);
            if (findIndex == true) {
                tokens[i] = tokens[i+1];
            } else {
                if (tokens[i] == profileTokenId) {
                    tokens[i] = tokens[i+1];
                    findIndex = true;
                }
            }
        }

        // check the last one
        if (!findIndex) {
            if (tokens[tokens.length - 1] == profileTokenId) {
                findIndex = true;
            }
        }

        if (findIndex) {
            // remove
            tokens.pop(); 
        }
    }


    /// @dev inner method to verify the owner of the token
    function isOwner(uint256 tokenId, address owner) private view returns(bool) {
        uint256[] memory ownedTokens = ownedToken[owner];
        for (uint i = 0; i<ownedTokens.length; i++) {
            if (ownedTokens[i] == tokenId) {
                return true;
            }

            uint256 mappedUpdatedToken = updatedToken[ownedTokens[i]];
            if (mappedUpdatedToken == tokenId) {
                return true;
            }

        }
        return false;
    } 


    /// @dev 
    function isTokenOwner(uint256 tokenId, address owner) external view override returns(bool) { 
        return isOwner(tokenId, owner);
    } 


    function purchase(uint amount) external payable returns (bool) {
        // console.log("purchase user: %s", msg.sender); 
        address purchaseUser = msg.sender;

        baseRequire(purchaseUser, amount);

        uint[] storage tokens = ownedToken[purchaseUser];
        if (tokens.length + amount > ACCOUNT_LIMITATION) {
            require(false, "WastelandUnion: purchase limited(3) 10 NFTs at most");
        } 

        for (uint256 i = 0; i < amount; i++) {
            _mintOne(purchaseUser, PROFILE_START_TOKEN_ID);
            tokens.push(PROFILE_START_TOKEN_ID);
            PROFILE_START_TOKEN_ID = PROFILE_START_TOKEN_ID + PROFILE_NEXT_TOKEN_SPAN;

            tokenPurchaseTime[PROFILE_START_TOKEN_ID] = block.timestamp;
            emit Purchase(PROFILE_START_TOKEN_ID, purchaseUser);

            /// @dev for airdrop
            if (totalSold() <= 4000) { 
                equipmentAirdrops[purchaseUser] ++ ;
            }

            if (totalSold() % 5 == 0 && totalSold() > 1 ) {
                // console.log("%d, %d, %d", abyxxAirdrops.length, totalSold() % 5, totalSold());
                abyxxAirdrops[purchaseUser]++;
            }

        }
        ownedToken[purchaseUser] = tokens;
        return true;
    }


    function listMine() external view returns (uint256[] memory) {
        return ownedToken[msg.sender];
    }

    function _mintOne(address _to, uint256 _tokenId) private { 
        _totalSold.increment();
        _safeMint(_to, _tokenId);
    }


    function setPause(bool _pause) public onlyAdmin {
        PAUSE_SELLING = _pause;
        emit PauseEvent(PAUSE_SELLING);
    }


    function withdraw() public override onlyOwner {
        payable(msg.sender).transfer(address(this).balance);
    }

    /**
    
     */
    function batchMint(address wallet, uint amount) external onlyAdmin {

        isEnoughSupply(amount, true);

        uint[] storage tokens = ownedToken[wallet];

        for (uint256 i = 0; i < amount; i++) {
            _mintOne(wallet, PROFILE_START_TOKEN_ID);
            tokens.push(PROFILE_START_TOKEN_ID);
            PROFILE_START_TOKEN_ID = PROFILE_START_TOKEN_ID + PROFILE_NEXT_TOKEN_SPAN;
        }

        ownedToken[wallet] = tokens;
    } 

    function transferFrom(
        address from,
        address to,
        uint256 tokenId
    ) public override {

        removeToken(from, tokenId);
        super.transferFrom(from, to, tokenId);
        uint[] storage tokens = ownedToken[to];
        tokens.push(tokenId);
        tokenPurchaseTime[tokenId] = block.timestamp;

    }

    function specialMint(uint256 tokenId, address wallet) public onlyAdmin {
        uint[] storage tokens = ownedToken[wallet];
        _safeMint(wallet, tokenId);
        tokens.push(tokenId);
    }


    function getTokenPurchaseTime(uint256 tokenId) external override view returns(uint256) {
        return tokenPurchaseTime[tokenId];
    }

    function myUpdatedToken(uint256 tokenId) public view returns(uint256) {
        return updatedToken[tokenId];
    }

    function myOriginalToken(uint256 tokenId) public view returns(uint256) {
        return orginalTokenMapping[tokenId];
    }

    function updateEquipment(uint256 profileTokenId, uint256 equipmentTokenId) public {

        address requireUserAddress = msg.sender;
        // console.log(EQUIPMENT_CONTRACT);

        bool isEquipmentTokenOwner = INFTEquipment(EQUIPMENT_CONTRACT).isTokenOwner(equipmentTokenId, requireUserAddress);

        // console.log(isEquipmentTokenOwner);
        require(isEquipmentTokenOwner, "WastelandUnion: should be owner1.");

        bool isProfileOwner = isOwner(profileTokenId, requireUserAddress);
        require(isProfileOwner, "WastelandUnion: should be owner2.");

        uint256 origialProfile = orginalTokenMapping[profileTokenId];
        if (origialProfile == 0) {
            origialProfile = profileTokenId;
        }

        uint256 currentUpdatedProfile = updatedToken[origialProfile];
        if (currentUpdatedProfile == 0) { 
            currentUpdatedProfile = profileTokenId;
        }

        require(profileTokenId == currentUpdatedProfile, "WastelandUnion: Should be the same");
        uint256 nowUpdatedToken = INFTEquipment(EQUIPMENT_CONTRACT).doEquipment(profileTokenId, equipmentTokenId, requireUserAddress);

        updatedToken[origialProfile] = nowUpdatedToken;
        orginalTokenMapping[nowUpdatedToken] = origialProfile;

        delete orginalTokenMapping[currentUpdatedProfile];

    } 

    function getOwnedTokenAmount(address useraddress) external override view returns(uint) {
        return ownedToken[useraddress].length;
    }

}
