contract Token is ERC20, Authorizable {
    IUniswapV2Router02 public immutable uniswapV2Router;
    address public immutable uniswapV2Pair;

    uint256 public feePct;
    uint256 public feeMinimumForSwap;

    bool public feeEnabled;
    bool public swapEnabled;
    bool public inSwapAndLiquify;

    mapping(address => bool) public excluded;

    event Burned(address account, uint256 amount);
    event Enabled(uint256 time, bool state);

    event SwapAndLiquify(
        uint256 tokensSwapped,
        uint256 ethReceived,
        uint256 tokensIntoLiqudity
    );

    modifier lockTheSwap() {
        inSwapAndLiquify = true;
        _;
        inSwapAndLiquify = false;
    }

    constructor() ERC20("Shillings", "$SHILLINGS") {
        //fee stuff ...
        feePct = 5;
        feeMinimumForSwap = 10 ether;

        //initial recipient is the creator for redistribution
        _mint(_msgSender(), 100_000_000 ether);

        //setup Uniswap dependencies for automated LP add ...
        IUniswapV2Router02 _uniswapV2Router = IUniswapV2Router02(
            0x7a250d5630B4cF539739dF2C5dAcb4c659F2488D
        );

        uniswapV2Pair = IUniswapV2Factory(_uniswapV2Router.factory())
            .createPair(address(this), _uniswapV2Router.WETH());

        uniswapV2Router = _uniswapV2Router;

        excluded[owner()] = true;
        excluded[address(this)] = true;
    }

    function burn(address account, uint256 amount)
        external
        onlyAuthorized
        returns (bool)
    {
        _burn(account, amount);

        emit Burned(account, amount);
        return true;
    }

    function exclude(address account) external onlyOwner returns (bool) {
        require(!excluded[account], "must not be excluded already");
        excluded[account] = true;
        return true;
    }

    function feeSwitch(bool state) external onlyOwner returns (bool) {
        require(feeEnabled != state, "must not be in the state already");
        feeEnabled = state;
        return true;
    }

    function SwapSwitch(bool state) external onlyOwner returns (bool) {
        require(swapEnabled != state, "must not be in the state already");
        swapEnabled = state;
        return true;
    }

    function changeMinimumForSwap(uint256 minimum)
        external
        onlyOwner
        returns (bool)
    {
        feeMinimumForSwap = minimum;
        return true;
    }

    function checkSwapConditions(address sender) private {
        uint256 feesForSwap = balanceOf(address(this));
        bool overMinimumFeeForSwap = feesForSwap >= feeMinimumForSwap;

        if (
            swapEnabled &&
            overMinimumFeeForSwap &&
            !inSwapAndLiquify &&
            sender != uniswapV2Pair
        ) {
            feesForSwap = feeMinimumForSwap;
            swapAndLiquify(feesForSwap);
        }
    }

    function swapAndLiquify(uint256 amount) private lockTheSwap {
        uint256 half = amount / 2;
        uint256 otherHalf = amount - half;

        uint256 initialBalance = address(this).balance;
        swapTokensForETH(half);

        uint256 newBalance = address(this).balance - initialBalance;
        addLiquidity(otherHalf, newBalance);
        emit SwapAndLiquify(half, newBalance, otherHalf);
    }

    function swapTokensForETH(uint256 tokenAmount) private {
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = uniswapV2Router.WETH();
        _approve(address(this), address(uniswapV2Router), tokenAmount);
        uniswapV2Router.swapExactTokensForETHSupportingFeeOnTransferTokens(
            tokenAmount,
            0,
            path,
            address(this),
            block.timestamp
        );
    }

    function addLiquidity(uint256 tokenAmount, uint256 ethAmount) private {
        _approve(address(this), address(uniswapV2Router), tokenAmount);
        uniswapV2Router.addLiquidityETH{value: ethAmount}(
            address(this),
            tokenAmount,
            0,
            0,
            owner(),
            block.timestamp
        );
    }

    function calculateFee(uint256 amount) private view returns (uint256) {
        return (amount * feePct) / 100;
    }

    function takeFee(uint256 fee, address sender) private {
        _balances[address(this)] = _balances[address(this)] + fee;
        emit Transfer(sender, address(this), fee);
    }

    function _transfer(
        address sender,
        address recipient,
        uint256 amount
    ) internal override {
        require(sender != address(0), "ERC20: transfer from the zero address");
        require(recipient != address(0), "ERC20: transfer to the zero address");

        checkSwapConditions(sender);

        uint256 senderBalance = _balances[sender];
        require(
            senderBalance >= amount,
            "ERC20: transfer amount exceeds balance"
        );

        uint256 value = amount;

        if (feeEnabled && !excluded[sender]) {
            uint256 fee = calculateFee(amount);
            takeFee(fee, sender);
            value -= fee;
        }

        unchecked {
            _balances[sender] = senderBalance - amount;
        }
        _balances[recipient] += value;

        emit Transfer(sender, recipient, value);
    }

    receive() external payable {}
}
