    "src/facets/EscrowRegistryFacet.sol": {
      "content": "// SPDX-License-Identifier: MIT\npragma solidity ^0.7.6;\n\nimport \"../libraries/AppStorage.sol\";\n\ncontract EscrowRegistryFacet {\n\n    AppStorage internal s;\n\n    function getEscrow(\n        bytes memory _id,\n        address _buyer,\n        address _seller,\n        uint256 _amount,\n        uint256 _fee\n    )\n        public\n        view\n        returns (address)\n    {\n        bytes32 escrowID = keccak256(abi.encodePacked(_id,_buyer,_seller,_amount,_fee));\n        if (s.escrows[escrowID] != address(0)) {\n            return s.escrows[escrowID];\n        }\n\n        return address(0);\n    }\n}"
    },
    "src/libraries/AppStorage.sol": {
      "content": "// SPDX-License-Identifier: MIT\npragma solidity 0.7.6;\n\nstruct AppStorage {\n    mapping (bytes32 => address) escrows;\n}"
    }
