    "/contracts/Exchange.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity ^0.8.0;

import "openzeppelin-solidity/contracts/access/AccessControl.sol";
import "openzeppelin-solidity/contracts/security/ReentrancyGuard.sol";
import "openzeppelin-solidity/contracts/utils/cryptography/ECDSA.sol";
import "openzeppelin-solidity/contracts/token/ERC721/IERC721.sol";
import "openzeppelin-solidity/contracts/token/ERC721/IERC721Receiver.sol";
import "openzeppelin-solidity/contracts/token/ERC1155/IERC1155.sol";
import "openzeppelin-solidity/contracts/token/ERC1155/IERC1155Receiver.sol";
import "openzeppelin-solidity/contracts/token/ERC20/IERC20.sol";

contract Exchange is
    AccessControl,
    IERC721Receiver,
    IERC1155Receiver,
    ReentrancyGuard
{

    struct NftTokenInfo {
        address tokenAddress;
        uint256 id;
        uint256 amount;
    }

    bytes32 public SIGNER_ROLE = keccak256("SIGNER_ROLE");
    mapping (bytes32 => bool) private _orderIds;

    event ExchangeMadeErc721(
        address seller,
        address buyer,
        address sellTokenAddress,
        uint256 sellId,
        uint256 sellAmount,
        address buyTokenAddress,
        uint256 buyId,
        uint256 buyAmount,
        address[] feeAddresses,
        uint256[] feeAmounts
    );
    event ExchangeMadeErc1155(
        address seller,
        address buyer,
        address sellTokenAddress,
        uint256 sellId,
        uint256 sellAmount,
        address buyTokenAddress,
        uint256 buyId,
        uint256 buyAmount,
        address[] feeAddresses,
        uint256[] feeAmounts
    );

    constructor() {
        _setupRole(DEFAULT_ADMIN_ROLE, _msgSender());
        _setupRole(SIGNER_ROLE, _msgSender());
    }

    function makeExchangeERC721(
        bytes32 idOrder,
        address[2] calldata SellerBuyer,
        NftTokenInfo calldata tokenToBuy,
        NftTokenInfo calldata tokenToSell,
        address[] calldata feeAddresses,
    	uint256[] calldata feeAmounts,
        bytes calldata signature
    ) external payable nonReentrant {
        require(
            tokenToBuy.tokenAddress != address(0) && tokenToBuy.amount == 0,
            "Exchange: Wrong tokenToBuy"
        );
        require(
            tokenToSell.tokenAddress != address(0) && tokenToSell.id == 0,
            "Exchange: Wrong tokenToSell"
        );
        require(
            feeAddresses.length == feeAmounts.length,
            "Exchange: Wrong fees"
        );
        _verifySigner(
            idOrder,
            SellerBuyer,
            tokenToBuy,
            tokenToSell,
            feeAddresses,
            feeAmounts,
            signature
        );
        _addOrderId(idOrder);

        uint256 tokenToSeller = tokenToSell.amount;
        if (tokenToSell.tokenAddress == 0xEeeeeEeeeEeEeeEeEeEeeEEEeeeeEeeeeeeeEEeE) {
            require(tokenToSeller == msg.value, "Wrong amount");
            for (uint256 i = 0; i < feeAddresses.length; i++) {
                tokenToSeller = tokenToSeller - feeAmounts[i];
                payable(feeAddresses[i]).transfer(feeAmounts[i]);
            }
            payable(SellerBuyer[0]).transfer(tokenToSeller);
        }
        else {
            for (uint256 i = 0; i < feeAddresses.length; i++) {
                tokenToSeller = tokenToSeller - feeAmounts[i];
                IERC20(tokenToSell.tokenAddress).transferFrom(
                    SellerBuyer[1],
                    feeAddresses[i],
                    feeAmounts[i]
                );
            }

            IERC20(tokenToSell.tokenAddress).transferFrom(
                SellerBuyer[1],
                SellerBuyer[0],
                tokenToSeller
            );
        }

        IERC721(tokenToBuy.tokenAddress).safeTransferFrom(
            SellerBuyer[0],
            SellerBuyer[1],
            tokenToBuy.id
        );

        emit ExchangeMadeErc721(
            SellerBuyer[0],
            SellerBuyer[1],
            tokenToBuy.tokenAddress,
            tokenToBuy.id,
            tokenToBuy.amount,
            tokenToSell.tokenAddress,
            tokenToSell.id,
            tokenToSell.amount,
            feeAddresses,
            feeAmounts
        );
    }

    function makeExchangeERC1155(
        bytes32 idOrder,
        address[2] calldata SellerBuyer,
        NftTokenInfo calldata tokenToBuy,
        NftTokenInfo calldata tokenToSell,
        address[] calldata feeAddresses,
    	uint256[] calldata feeAmounts,
        bytes calldata signature
    ) external payable nonReentrant {
        require(
            tokenToBuy.tokenAddress != address(0),
            "Exchange: Wrong tokenToBuy"
        );
        require(
            tokenToSell.tokenAddress != address(0) && tokenToSell.id == 0,
            "Exchange: Wrong tokenToSell"
        );
        require(
            feeAddresses.length == feeAmounts.length,
            "Exchange: Wrong fees"
        );
        _verifySigner(
            idOrder,
            SellerBuyer,
            tokenToBuy,
            tokenToSell,
            feeAddresses,
            feeAmounts,
            signature
        );
        _addOrderId(idOrder);

        uint256 tokenToSeller = tokenToSell.amount;
        if (tokenToSell.tokenAddress == 0xEeeeeEeeeEeEeeEeEeEeeEEEeeeeEeeeeeeeEEeE) {
            require(tokenToSeller == msg.value, "Wrong amount");
            for (uint256 i = 0; i < feeAddresses.length; i++) {
                tokenToSeller = tokenToSeller - feeAmounts[i];
                payable(feeAddresses[i]).transfer(feeAmounts[i]);
            }
            payable(SellerBuyer[0]).transfer(tokenToSeller);
        }
        else {
            for (uint256 i = 0; i < feeAddresses.length; i++) {
                tokenToSeller = tokenToSeller - feeAmounts[i];
                IERC20(tokenToSell.tokenAddress).transferFrom(
                    SellerBuyer[1],
                    feeAddresses[i],
                    feeAmounts[i]
                );
            }

            IERC20(tokenToSell.tokenAddress).transferFrom(
                SellerBuyer[1],
                SellerBuyer[0],
                tokenToSeller
            );
        }

        IERC1155(tokenToBuy.tokenAddress).safeTransferFrom(
            SellerBuyer[0],
            SellerBuyer[1],
            tokenToBuy.id,
            tokenToBuy.amount,
            ""
        );

        emit ExchangeMadeErc1155(
            SellerBuyer[0],
            SellerBuyer[1],
            tokenToBuy.tokenAddress,
            tokenToBuy.id,
            tokenToBuy.amount,
            tokenToSell.tokenAddress,
            tokenToSell.id,
            tokenToSell.amount,
            feeAddresses,
            feeAmounts
        );
    }

    function _verifySigner(
        bytes32 idOrder,
        address[2] calldata SellerBuyer,
        NftTokenInfo calldata tokenToBuy,
        NftTokenInfo calldata tokenToSell,
        address[] calldata feeAddresses,
    	uint256[] calldata feeAmounts,
        bytes calldata signature
    ) private view {
        bytes memory message =
            abi.encodePacked(
                idOrder,
                SellerBuyer[0],
                tokenToBuy.tokenAddress,
                tokenToBuy.id,
                tokenToBuy.amount
            );
        message = abi.encodePacked(
            message,
            tokenToSell.tokenAddress,
            //tokenToSell.id,
            tokenToSell.amount
        );
        message = abi.encodePacked(
            message,
            feeAddresses,
            feeAmounts,
            SellerBuyer[1]
        );
        address messageSigner = ECDSA.recover(keccak256(message), signature);
        require(
            hasRole(SIGNER_ROLE, messageSigner),
            "Exchange: Signer should sign transaction"
        );
    }

    function _addOrderId(bytes32 orderId) private {
        require(!_orderIds[orderId]);
        _orderIds[orderId] = true;
    }

    function onERC1155Received(
        address,
        address,
        uint256,
        uint256,
        bytes calldata
    ) external pure override returns (bytes4) {
        return IERC1155Receiver.onERC1155Received.selector;
    }

    function onERC1155BatchReceived(
        address,
        address,
        uint256[] calldata,
        uint256[] calldata,
        bytes calldata
    ) external pure override returns (bytes4) {
        return IERC1155Receiver.onERC1155BatchReceived.selector;
    }

    function onERC721Received(
        address,
        address,
        uint256,
        bytes calldata
    ) external pure override returns (bytes4) {
        return IERC721Receiver.onERC721Received.selector;
    }
}
"
