contract Ownable{
    address private _owner;
    address private _previousOwner;
    uint256 private _lockTime;

    event OwnershipTransferred(
        address indexed previousOwner,
        address indexed newOwner
    );

    /**
     * @dev Initializes the contract setting the deployer as the initial owner.
     */
    constructor() {
        address msgSender = msg.sender;
        _owner = msgSender;
        emit OwnershipTransferred(address(0), msgSender);
    }

    /**
     * @dev Returns the address of the current owner.
     */
    function owner() public view returns (address) {
        return _owner;
    }

    /**
     * @dev Throws if called by any account other than the owner.
     */
    modifier onlyOwner() {
        require(_owner == msg.sender, "Ownable: caller is not the owner");
        _;
    }

}

contract InfinityClaim is Ownable {
    using SafeMath for uint256;
    using Address for address;

    address teamAddress = address(0);
    address v1TokenAddress = address(0);
    address v2TokenAddress = address(0);

    mapping(address => uint256) whitelisted;
    
    uint16 multiplier = 1100;
    uint16 denominator = 1000;

    uint256 gasRepayAmount = 0;

    constructor() {
        teamAddress = msg.sender;
    }

    event addressChanged(address oldAddress, address newAddress, string addressType);
    event multiplierChanged(uint16 oldMultiplier, uint16 newMultiplier, uint16 oldDenominator, uint16 newDenominator);
    event tokensClaimed(address claimer, uint256 amount, uint256 multipliedAmount);

    function changeMultiplier(uint16 newMultiplier, uint16 newDenominator) external onlyOwner {
        uint16 oldMultiplier = multiplier;
        uint16 oldDenominator = denominator;

        multiplier = newMultiplier;
        denominator = newDenominator;

        emit multiplierChanged(oldMultiplier, newMultiplier, oldDenominator, newDenominator);
    }

    function setTeamAddressaddress(address newAddress) external onlyOwner {
        address old = teamAddress;
        teamAddress = newAddress;
        emit addressChanged(old, newAddress, "newAddress");
    }

    function setV1TokenAddress(address newAddress) external onlyOwner {
        address old = v1TokenAddress;
        v1TokenAddress = newAddress;
        emit addressChanged(old, newAddress, "v2TokenAddress");
    }

    function setV2TokenAddress(address newAddress) external onlyOwner {
        address old = v2TokenAddress;
        v2TokenAddress = newAddress;
        emit addressChanged(old, newAddress, "v2TokenAddress");
    }

    function whitelistClaimant(address claimant, uint256 balance) external onlyOwner {
        whitelisted[claimant] = balance;
    }

    function whitelistBulk(address [] calldata claimants, uint256 [] calldata balance) external onlyOwner {
        uint arrayLength = claimants.length;
        for (uint i=0; i<arrayLength; i++) {
            whitelisted[claimants[i]] = balance[i];
        }
    }

    function setGasRepayAmount(uint256 amount) external onlyOwner {
        gasRepayAmount = amount;
    }

    function claimTokens(uint256 amount) external payable {
        require(address(this).balance > gasRepayAmount, "Contract has insufficient eth");
        require(amount > 0, "Transfer amount must be greater than zero");
        require(amount == whitelisted[msg.sender], "Claimant must withdraw their entire whitelisted balance");
        payable(msg.sender).transfer(gasRepayAmount);
        whitelisted[msg.sender] = whitelisted[msg.sender] - amount;
        uint256 multipliedAmount = (amount * multiplier) / denominator;
        IERC20(v1TokenAddress).transferFrom(msg.sender, teamAddress, amount);
        IERC20WithClaim(v2TokenAddress).claimTransfer(teamAddress, msg.sender, multipliedAmount);
        emit tokensClaimed(msg.sender, amount, multipliedAmount);
    }

    function extractTokens(address tokenAddress, uint256 amount) external onlyOwner {
        IERC20(tokenAddress).transfer(teamAddress, amount);
    }

    receive() external payable { }

    function extractEth() external onlyOwner {
        payable(msg.sender).transfer(address (this).balance);
    }


}
