contract SenatorWarren is ERC721, AccessControl {
    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");

    string private uri;

    constructor(string memory _uri) ERC721("Senator Warren", "WARREN") {
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(MINTER_ROLE, msg.sender);
        uri = _uri;
    }

    function baseURI() public view virtual returns (string memory) {
        return _baseURI();
    }

    function _baseURI() internal view virtual override returns (string memory) {
        return uri;
    }

    function safeMint(address to, uint256 id) public onlyRole(MINTER_ROLE) {
        _safeMint(to, id);
    }

    // The following functions are overrides required by Solidity.
    function supportsInterface(bytes4 interfaceId)
        public
        view
        override(ERC721, AccessControl)
        returns (bool)
    {
        return super.supportsInterface(interfaceId);
    }
}

contract TheManagoooor is Ownable, ReentrancyGuard {
    using SafeERC20 for IERC20;

    uint8 public constant MAX_AMOUNT = 100;
    uint256 public constant STEP = 15 minutes;

    IERC20 public immutable token;
    SenatorWarren public immutable nft;
    uint256 public immutable auctionStartTime;
    uint256 public immutable auctionEndTime;

    struct AuctionInfo {
        address highestBidder;
        uint256 highestBid;
        bool claimed;
    }

    // Auction Info
    // NFT -> Info
    mapping(uint256 => AuctionInfo) public auctionInfo;

    // Events
    event HighestBidIncreased(uint256 id, address bidder, uint256 amount);

    // Errors
    error EOA();
    error BiddingForOutdatedAuction();
    error AuctionNotActive();
    error AuctionActive();
    error BidNotHighEnough(uint256 highestBid);
    error AlreadyClaimed();
    error BidPending();
    error OutOfRange();

    // Modifyooooor
    modifier auctionActive() {
        if (
            block.timestamp < auctionStartTime ||
            block.timestamp > auctionEndTime
        ) revert AuctionNotActive();
        _;
    }

    // Constructoooor
    constructor(
        uint256 _startTime,
        address _token,
        address _nft
    ) {
        auctionStartTime = _startTime;
        auctionEndTime = _startTime + (MAX_AMOUNT * STEP);
        token = IERC20(_token);
        nft = SenatorWarren(_nft);
    }

    function currentId() public view returns (uint256) {
        return (block.timestamp - auctionStartTime) / STEP;
    }

    /// Bid on the auction with the amount sent
    /// The value will only be refunded if the auction is not won.
    function bid(uint256 _id, uint256 _amount)
        external
        nonReentrant
        auctionActive
    {
        if (Address.isContract(msg.sender)) revert EOA();

        // Get current auction info
        uint256 id = this.currentId();
        AuctionInfo storage currentAuction = auctionInfo[id];

        if (_id != id) revert BiddingForOutdatedAuction();
        if (id > MAX_AMOUNT) revert OutOfRange();

        // Check if last NFT was claimed, otherwise try to claim it
        // Ignore if first
        if (id > 0) {
            _internalClaim(id - 1);
        }

        // Actual amount for tokens with tax
        uint256 beforeDeposit = token.balanceOf(address(this));
        token.safeTransferFrom(msg.sender, address(this), _amount);
        uint256 afterDeposit = token.balanceOf(address(this));
        _amount = afterDeposit - beforeDeposit;

        // If the bid is not higher the revert statement
        if (_amount <= currentAuction.highestBid)
            revert BidNotHighEnough(currentAuction.highestBid);

        if (currentAuction.highestBid != 0) {
            // Refund the last highest bidder
            token.safeTransfer(
                currentAuction.highestBidder,
                currentAuction.highestBid
            );
        }

        currentAuction.highestBidder = msg.sender;
        currentAuction.highestBid = _amount;

        emit HighestBidIncreased(id, msg.sender, _amount);
    }

    function _internalClaim(uint256 id) internal {
        // Get auction info
        AuctionInfo storage auction = auctionInfo[id];
        if (!auction.claimed && auction.highestBidder != address(0)) {
            // Mint NFT
            nft.safeMint(auction.highestBidder, id);
            auction.claimed = true;
        }
    }

    /// Claim
    function claim(uint8 id) external nonReentrant {
        uint256 lastest = this.currentId();
        if (id >= lastest) revert BidPending();
        if (id > MAX_AMOUNT) revert OutOfRange();

        _internalClaim(id);
    }

    function burn() external onlyOwner {
        if (block.timestamp < auctionEndTime) revert AuctionActive();

        uint256 balance = token.balanceOf(address(this));
        token.safeTransfer(0x000000000000000000000000000000000000dEaD, balance);
    }
}
