interface INFT {
    function mint(address _to) external;
    function mintBatch(address _to, uint _amount) external;
}

contract PoligoonzSale is Ownable {

  using SafeMath for uint;
  using SafeMath for uint64;

    uint public constant SALE_PRICE = 0.1 ether;
    INFT public nft;
    
    address public verifyAddress = 0x26C3b144F5c2369eB8Cb78579925EB9c701dCE0f;

    struct Buyer {
        uint64 startTime;
        uint64 endTime;
        uint64 maxPerWallet;
        uint64 bought;
    }

    mapping(address => Buyer) public buyers;
    
    address payable public receiver;

    constructor(address _nftAddress, address payable _receiverAddress) {
        nft = INFT(_nftAddress);
        
        receiver = _receiverAddress;
    }

    /*
     * @dev function to buy tokens. Can be bought only 1. 
     * @param _amount how much tokens can be bought.
     * @param _signature Signed message from verifyAddress private key
     */
    function buy(uint _amount, uint64 _startTime, uint64 _endTime, uint64 _maxPerWallet, bytes memory _signature) external payable {
      if(buyers[msg.sender].bought == 0) {
        require(verify(_startTime, _endTime, _maxPerWallet, _signature), "invalid signature");
        buyers[msg.sender] = Buyer({
            startTime: _startTime,
            endTime: _endTime,
            maxPerWallet: _maxPerWallet,
            bought: 0
        });
      }

      require(buyers[msg.sender].startTime < block.timestamp && block.timestamp < buyers[msg.sender].endTime, "sale is closed");
      require(buyers[msg.sender].bought.add(_amount) <= buyers[msg.sender].maxPerWallet, "cannot buy more than limit");
      require(msg.value == _amount.mul(SALE_PRICE), "each NFT costs 0.1 ETH");
      
      buyers[msg.sender].bought = uint64(buyers[msg.sender].bought.add(_amount));
      nft.mintBatch(msg.sender, _amount);
      (bool sent, ) = receiver.call{value: address(this).balance}("");
      require(sent, "Something wrong with receiver");
    }

    function setMaxPerWallet(address _userAddress, uint64 _maxPerWallet) public onlyOwner {
        buyers[_userAddress].maxPerWallet = _maxPerWallet;
    }
    
    /*
     * @dev function to withdraw all tokens
     * @param _to ETH receiver address
     */
    function cashOut(address _to) public onlyOwner {
        // Call returns a boolean value indicating success or failure.
        // This is the current recommended method to use.
        
        (bool sent, ) = _to.call{value: address(this).balance}("");
        require(sent, "Failed to send Ether");
    }

    /// signature methods.
    function verify(uint64 _startTime, uint64 _endTime, uint64 _maxPerWallet, bytes memory _signature) internal view returns(bool) {
        bytes32 message = prefixed(keccak256(abi.encodePacked(msg.sender, _startTime, _endTime, _maxPerWallet, address(this))));
        return (recoverSigner(message, _signature) == verifyAddress);
    }

    function recoverSigner(bytes32 message, bytes memory sig)
        internal
        pure
        returns (address)
    {
        (uint8 v, bytes32 r, bytes32 s) = abi.decode(sig, (uint8, bytes32, bytes32));

        return ecrecover(message, v, r, s);
    }

    /// builds a prefixed hash to mimic the behavior of eth_sign.
    function prefixed(bytes32 hash) internal pure returns (bytes32) {
        return keccak256(abi.encodePacked("\x19Ethereum Signed Message:\n32", hash));
    }
}
