// File: contracts/YuGiOhCardPurchase.sol



pragma solidity ^0.8.5;






contract YuGiOhCardPurchase is Ownable {
    using EnumerableSet for EnumerableSet.AddressSet;

    uint256 public constant MAX_AIRDROP = 3613; // claim for free
    uint256 public constant MAX_PURCHASE = 20;
    uint256 public price = 0.05 * 1e18; // 0.05 ETH
    uint256 public startTime = 1640908800; // Fri Dec 31 2021 00:00:00 GMT+0000

    address public ygo;
    
    uint256 public totalClaimed;
    EnumerableSet.AddressSet private allowSet;
    mapping(address => uint256) public allowLimit;
    mapping(address => bool) public claimedAccounts;

    constructor(address ygo_) {
        ygo = ygo_;
       
        addAllow(0x7A58c0Be72BE218B41C608b7Fe7C5bB630736C71, 10000 * 1e18); // We the people, for the people.
    }

    function mint(uint256 amount) public payable {
        require(block.timestamp >= startTime, "YuGiOhCardPurchase: purchase has not started yet");
        require(amount > 0, "YuGiOhCardPurchase: cannot buy zero");
        require(amount <= MAX_PURCHASE, "YuGiOhCardPurchase: too many to purchase at once");
        require(msg.value >= (price * amount), "YuGiOhCardPurchase: ether value error");

        IYuGiOhCard(ygo).mint(msg.sender, amount);
    }

    function claim() public {
        require(block.timestamp >= startTime, "YuGiOhCardPurchase: purchase has not started yet");
        require(totalClaimed < MAX_AIRDROP, "YuGiOhCardPurchase: all claimed");
        require(totalClaimed + 1 < MAX_AIRDROP, "YuGiOhCardPurchase: exceeds MAX_AIRDROP");
        require(canClaim(msg.sender), "YuGiOhCardPurchase: can NOT claim");

        claimedAccounts[msg.sender] = true;

        IYuGiOhCard(ygo).mint(msg.sender, 1);

        totalClaimed += 1;
    }

    function canClaim(address account) public view returns (bool) {
        if (claimedAccounts[account]) {
            return false;
        }
        for (uint i = 0; i < allowSet.length(); i++) {
            address token = allowSet.at(i);
            // for ERC721 or ERC20
            if (IToken(token).balanceOf(account) >= allowLimit[token]) {
                return true;
            }
        }

        return false;
    }


    // The following functions are system manager.

    function setPurchasePrice(uint256 newPrice) public onlyOwner {
        price = newPrice;
    }

    function setStartTime(uint256 newStartTime) public onlyOwner {
        startTime = newStartTime;
    }

    function addAllow(address token, uint256 amount) public onlyOwner {
        // ignore check
        allowLimit[token] = amount;
        allowSet.add(token);
    }

    function removeAllow(address token) public onlyOwner {
        allowSet.remove(token);
    }
    function getAllAllow() public view returns (address[] memory) {
        return allowSet.values();
    }

    function withdraw() public {
        Address.sendValue(payable(owner()), address(this).balance);
    }
}
