        "lightgray"
    ];
    string[] private timeOfDay = [
        "During the day of ",
        "In the early evening on ",
        "On the evening of ",
        "During the night of ",
        "In the early morning on ",
        "On the morning of "
    ];

    string[] private timeOfDayMeta = [
        "Day",
        "Early Evening",
        "Evening",
        "Night",
        "Early Morning",
        "Morning"
    ];

    string[] private months = [
        "January",
