        // Vesting data for team category
        vesting[teamCategory].limit = 50000000 ether;
        vesting[teamCategory].released = 21000000 ether;
        vesting[teamCategory].scheme = [
            /* initial amount */
            0 ether,
            /* M+1 M+2 M+3 M+4 M+5 M+6 M+7 M+8 M+9 M+10 M+11 M+12 */
