// File: contracts/ArtMineToken.sol


pragma solidity ^0.8.0;










contract ArtMineToken is Initializable, ERC721Upgradeable, ERC721URIStorageUpgradeable, PausableUpgradeable, AccessControlUpgradeable, RoyaltiesV2 {

    bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");
    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");
    uint32 public constant maxSupplyPerProject = 100_000_000;

    event Mint(address artist, address by, uint256 code, uint256 price);
    address public playformAddress;
    address public owner;

    uint16 public initialSaleFeeInBP;
    uint16 public resaleFeeInBP;
    uint16 public resaleArtistFeeInBp;

    mapping(uint64 => address) public projectIdToArtistAddress;

    uint16 public test;

    function initialize(address admin, address playform) initializer public {
        __ERC721_init("ArtMineToken", "AMT");
        __ERC721URIStorage_init();
        __Pausable_init();
        __AccessControl_init();
        _grantRole(DEFAULT_ADMIN_ROLE, _msgSender());
        _grantRole(ADMIN_ROLE, _msgSender());
        _grantRole(MINTER_ROLE, _msgSender());
        grantRole(MINTER_ROLE, admin);
        grantRole(ADMIN_ROLE, admin);
        updateInitialSaleFeeInBP(1500);
        updateResaleFeeInBP(150);
        updateResaleArtistFeeInBp(850);
        updatePlayformAddress(playform);
        updateOwner(_msgSender());
    }

    function mint(uint256 tokenId, address to, string memory uri, uint256 price) public onlyRole(MINTER_ROLE) returns (uint256) {
        address artist = _tokenIdToArtistAddress(tokenId);
        _safeMint(artist, tokenId);
        _setTokenURI(tokenId, uri);

        emit Mint(artist, to, tokenId, price);

        if (artist != to) {
            _safeTransfer(artist, to, tokenId, "");
        }

        return tokenId;
    }

    function getRaribleV2Royalties(uint256 tokenId) external view returns (LibPart.Part[] memory r) {
        // Rarible code confirmed, they only make transfer then fee is bigger than 0
        require(_exists(tokenId), "ArtMineToken: Royalties query for nonexistent token");
        address artist = _tokenIdToArtistAddress(tokenId);
        r = new LibPart.Part[](2);
        r[0] = LibPart.Part(payable(artist), uint96(resaleArtistFeeInBp));
        r[1] = LibPart.Part(payable(playformAddress), uint96(resaleFeeInBP));
    }

    function updatePlayformAddress(address pa) public onlyRole(ADMIN_ROLE) {
        playformAddress = pa;
    }

    function updateInitialSaleFeeInBP(uint16 bp) public onlyRole(ADMIN_ROLE) {
        initialSaleFeeInBP = bp;
    }

    function updateResaleFeeInBP(uint16 bp) public onlyRole(ADMIN_ROLE) {
        resaleFeeInBP = bp;
    }

    function updateResaleArtistFeeInBp(uint16 bp) public onlyRole(ADMIN_ROLE) {
        resaleArtistFeeInBp = bp;
    }

    function addArtist(uint64 projectId, address artist) public onlyRole(ADMIN_ROLE) {
        projectIdToArtistAddress[projectId] = artist;
    }

    function updateTokenURI(uint256 code, string memory uri) public onlyRole(ADMIN_ROLE) {
        _setTokenURI(code, uri);
    }

    function updateOwner(address _o) public onlyRole(DEFAULT_ADMIN_ROLE) {
        // only used to support Ownable
        owner = _o;
    }

    function tokenIdToArtistAddress(uint256 tokenId) public view returns (address ad) {
        require(_exists(tokenId), "ArtMineToken: Artist query for nonexistent token");
        return _tokenIdToArtistAddress(tokenId);
    }

    function _tokenIdToArtistAddress(uint256 tokenId) internal view returns (address ad) {
        ad = projectIdToArtistAddress[uint64(tokenId) / maxSupplyPerProject];
        require(ad != address(0), "ArtMineToken: Artist doesn't exist.");
    }


    function _baseURI() internal pure override returns (string memory) {
        return "ipfs://";
    }

    function pause() public onlyRole(ADMIN_ROLE) {
        _pause();
    }

    function unpause() public onlyRole(ADMIN_ROLE) {
        _unpause();
    }

    function _beforeTokenTransfer(address from, address to, uint256 tokenId)
    internal
    whenNotPaused
    override
    {
        super._beforeTokenTransfer(from, to, tokenId);
    }

    // The following functions are overrides required by Solidity.

    function supportsInterface(bytes4 interfaceId) public view override(AccessControlUpgradeable, ERC721Upgradeable) returns (bool) {
        return interfaceId == LibRoyaltiesV2._INTERFACE_ID_ROYALTIES || super.supportsInterface(interfaceId);
    }

    function _burn(uint256 tokenId) internal override(ERC721URIStorageUpgradeable, ERC721Upgradeable) {
        super._burn(tokenId);
    }

    function tokenURI(uint256 tokenId)
    public
    view
    override(ERC721Upgradeable, ERC721URIStorageUpgradeable)
    returns (string memory)
    {
        return super.tokenURI(tokenId);
    }
}

// File: contracts/Minter.sol


pragma solidity ^0.8.0;







contract Minter is OwnableUpgradeable, PausableUpgradeable {

    using AddressUpgradeable for address payable;

    ArtMineToken public token;

    address public signer;
    mapping(address => uint256) private _deposits;

    event Deposited(address indexed payee, uint256 weiAmount);
    event Withdrawn(address indexed payee, uint256 weiAmount);

    function initialize(address _signer, address artmineAddress) initializer public {
        __Ownable_init();
        __Pausable_init();
        updateSigner(_signer);
        token = ArtMineToken(artmineAddress);
    }

    function mintTo(uint256 tokenId, string memory uri, uint256 price, uint256 expireAt, bytes memory signature) external payable whenNotPaused returns (uint256) {
        require(verifySignature(tokenId, uri, price, expireAt, signature), "ArtMineToken: Invalid signature.");
        require(msg.value == price, "ArtMineToken: Incorrect payment amount.");
        token.mint(tokenId, _msgSender(), uri, price);
        _splitPayment(price, tokenId);
        return 0;
    }

    function _splitPayment(uint256 amount, uint256 tokenId) private {
        if (amount <= 0) {
            return;
        }
        uint256 feeInBP = token.initialSaleFeeInBP();
        uint256 playformAmount = amount * feeInBP / 10000;
        if (playformAmount > 0) {
            address playformAddress = token.playformAddress();
            _deposit(playformAddress, playformAmount);
        }
        uint256 artistAmount = amount - playformAmount;
        if (artistAmount > 0) {
            address artistAddress = token.tokenIdToArtistAddress(tokenId);
            _deposit(artistAddress, amount - playformAmount);
        }

    }

    function verifySignature(uint256 tokenId, string memory uri, uint256 price, uint256 expireAt, bytes memory signature) public view returns (bool) {
        if (expireAt < block.timestamp) return false;
        bytes32 messageHash = keccak256(abi.encodePacked(tokenId, uri, price, expireAt));
        bytes32 ethSignedMessageHash = ECDSAUpgradeable.toEthSignedMessageHash(messageHash);
        return SignatureCheckerUpgradeable.isValidSignatureNow(signer, ethSignedMessageHash, signature);
    }

    function depositsOf(address payee) public view returns (uint256) {
        return _deposits[payee];
    }

    function updateSigner(address _s) public onlyOwner {
        signer = _s;
    }

    function pause() public onlyOwner {
        _pause();
    }

    function unpause() public onlyOwner {
        _unpause();
    }

    function withdraw(address payable payee) public whenNotPaused returns (uint256) {
        uint256 payment = _deposits[payee];
        require(payment > 0, "ArtMineToken: withdraw 0 amount.");
        _deposits[payee] = 0;
        payee.sendValue(payment);
        emit Withdrawn(payee, payment);
        return payment;
    }

    function _deposit(address payee, uint256 amount) private {
        _deposits[payee] += amount;
        emit Deposited(payee, amount);
    }

}
