        if(_customURIContract != address(0)) {
            return ITokenURICustom(_customURIContract).constructTokenURI(tokenId);
        }
        else { //default token URI section
             //Le Anime and Spirits Section
            if(tokenId > OFFSETAN2) { //&& !heroLocked[heroId]
                if(uriSelection[tokenId]) { //just possible for Le Anime OE - cannot flipURI in spirits
                    return string(abi.encodePacked(baseURI_OE, tokenId.toString()));
                }
                return string(abi.encodePacked(baseURI, tokenId.toString()));
            }
            else {
                if(mergeActive) {
                    return IMerger(mergeContract).heroURI(tokenId);
                }
                else {
