            transferFrom(msg.sender, address(this), tokenId[i] + OFFSETAN2);
            mainToken.safeTransferFrom(address(this), msg.sender, tokenId[i] + OFFSETNG);
            
            //resets the uriSelection to default
            uriSelection[tokenId[i] + OFFSETAN2] = false;
        }
