pragma solidity ^0.8.4;




contract MetaAccount is ERC1155, Ownable {
    
    using EnumerableMap for EnumerableMap.UintToAddressMap;

    mapping(address => string) public users;
    mapping(string => Account) private _accounts;
    EnumerableMap.UintToAddressMap private _tokenOwners;
    mapping(uint => string) private _tokenUris;

    string name_;
    string symbol_;   
    string public contractURI;

    uint256 public constant META_TOKENS_ID = 0;
    
    uint public max_supply;
    uint public supply;
    uint public registerPrice;

    uint registrationBonusAmount;

    enum SocialType {
        Telegram,
        Discord,
        Twitter,
        Facebook,
        Instagram
    }
    
     struct Account {
        address owner; 
        uint userNFT;
        mapping(SocialType => string) socials;
    }

    mapping(string => address) public telegram;
    mapping(string => address) public discord;
    mapping(string => address) public twitter;
    mapping(string => address) public facebook;
    mapping(string => address) public instagram;
    mapping(string => address) public imageHash;
    

    //events
    event Registration(address indexed user, string name, string userNFTUri);
    event RegistrationBonus(uint amount);
    event SocialChanged(string username, uint socialType, string socialValue);

    constructor(string memory _name, string memory _symbol, uint maxSupply, uint initSupply, uint bonusAmount, uint price, string memory uri, string memory contractUri) ERC1155("") {
        name_ = _name;
        symbol_ = _symbol;
        max_supply = maxSupply;
        supply = initSupply;
        registerPrice = price;
        registrationBonusAmount = bonusAmount;
        _tokenUris[0] = uri;
        contractURI = contractUri;
        _mintTokens(owner(), 10**5);
        //__ERC1155_init_unchained("");
    }

    function uri(uint256 tokenId) public view virtual override returns (string memory) {
        if(tokenId == 0) {
            return _tokenUris[0];
        }
        else{
            return _tokenUris[tokenId];
        }
    }

    function _mintTokens(address to, uint amount) internal {
        if((max_supply - supply) < amount) {
            revert("Max supply reached");
        }
        else{
            _mint(to, META_TOKENS_ID, amount, "Meta Tokens");
        }        
    }

    function name() public view returns (string memory) {
        return name_;
    }

    function symbol() public view returns (string memory) {
        return symbol_;
    }  

    function _mintUserNFT(address to, string calldata username, string calldata userNFTUri) internal {
        uint tokenId = _tokenOwners.length() + 1;
        _accounts[username].userNFT = tokenId;
        _tokenUris[tokenId] = userNFTUri;
        _tokenOwners.set(tokenId, to);
        _mint(to, tokenId, 1, "User NFT");
    }

    function register(address _address, string calldata username, string calldata userNFTUri) public payable  {
        require(_address != address(0), "0 account registration is not allowed");
        require (_accounts[username].owner == address(0), "Username already registered"); 
        require (bytes(users[_address]).length == 0, "Address already registered"); 
        require (msg.value >= registerPrice, "Not enought ether");

        users[_address] = username;
        _accounts[username].owner = _address;
        
        if(registrationBonusAmount > 0){
            _mintTokens( _address, registrationBonusAmount);
        }
        _mintUserNFT(_address, username, userNFTUri);
        emit Registration(_address, username, userNFTUri);        
    }

    function changeRegistrationBonus( uint256 amount) public onlyOwner {
        registrationBonusAmount = amount;
        emit RegistrationBonus(amount);
    }

    function setSocial( string calldata username, uint socialType, string calldata socialValue) public {
        require(msg.sender == _accounts[username].owner || msg.sender == owner(), "Only owners can set social");
        SocialType social = getSocialType(socialType);

        _accounts[username].socials[social] = socialValue;

        if(social == SocialType.Telegram){
            telegram[socialValue] = msg.sender;
        }
        if(social == SocialType.Discord){
            discord[socialValue] = msg.sender;
        }
        if(social == SocialType.Twitter){
            twitter[socialValue] = msg.sender;
        }
        if(social == SocialType.Facebook){
            facebook[socialValue] = msg.sender;
        }
        if(social == SocialType.Instagram){
            instagram[socialValue] = msg.sender;
        }
        
        emit SocialChanged( username, socialType, socialValue);
    }

    function getSocial( string calldata username, uint socialType) public view returns (string memory){
        return _accounts[username].socials[getSocialType(socialType)];
    }

    function getUsernameOwner(string calldata username) public view returns (address){
        return _accounts[username].owner;
    }

    function getSocialType(uint _social) internal pure returns (SocialType) {
        if (_social == 0) return SocialType.Telegram;
        if (_social == 1) return SocialType.Discord;
        if (_social == 2) return SocialType.Twitter;
        if (_social == 3) return SocialType.Facebook;
        if (_social == 4) return SocialType.Instagram;
    }

    function _beforeTokenTransfer(
        address operator,
        address from,
        address to,
        uint256[] memory ids,
        uint256[] memory amounts,
        bytes memory data
    ) internal virtual override  {
        super._beforeTokenTransfer(operator, from, to, ids, amounts, data);
        
        for(uint i = 0; i < ids.length; i++) {
            if(ids[i] > 0) {

                string memory username = users[from];
                if(bytes(username).length > 0) {
                    require(msg.sender == _accounts[username].owner , "Only owner can transfer Account");        
                    users[to] = username;
                    _accounts[username].owner = to;
                    delete users[msg.sender];
                }
                
            }
        }
    }


    function setTokenURI(uint tokenId, string memory _tokenURI)
    public
    onlyOwner
    {
        require(
            _tokenOwners.get(tokenId) != address(0),
            "ERC721Metadata: URI set of nonexistent token"
        );
        _tokenUris[tokenId] = _tokenURI;
    }

    function burn(
        address from,
        uint256 id,
        uint256 amount
    ) public onlyOwner {
        require(from != address(0), "ERC1155: burn from the zero address");
        _burn(from, id, amount);
    }

    function withdrawMoney() external onlyOwner{
        payable(msg.sender).transfer(address(this).balance);
    }

    function setRegisterPrice(uint price) public onlyOwner {
        registerPrice = price;
    }

}
