// File contracts/Lock.sol

pragma solidity >=0.7.0 <0.9.0;


contract Lock is Ownable {

    mapping(address => uint) public lockedValue;
    uint constant deadline = 1738339200;
    address token = 0x315dc1b524de57AE8e809A2e97699dbc895B8a21;

    constructor() {
        lockedValue[0xc552bee2e34016fA78b288422F593A2D83F7dbb5] = 10000000 * 10**9;
        lockedValue[0x16b939306D2e30E721853b474dE46ca0e4311205] = 5000000 * 10**9;
        lockedValue[0xb6101713f5867Ef22ae2FE085F75C12c6d257dE1] = 5000000 * 10**9;
    }
    
    function lock(address to, uint value) public onlyOwner {
        require(block.timestamp < deadline);
        lockedValue[to] = value;
    }

    function withdraw() public {
        require(block.timestamp > deadline, "locked");

        uint value = lockedValue[msg.sender];
        lockedValue[msg.sender] = 0;

        require(IERC20(token).transfer(msg.sender, value), "transfer failed");
    }
}
