// File: contracts/ExternalBridge.sol

pragma solidity 0.8.2;

interface IExternalStaking {
    function balanceOf(address) external view returns (uint256);
}

contract ExternalBridge is Ownable {
    IExternalStaking public externalStaking =
        IExternalStaking(0x0000000000000000000000000000000000000000);
    mapping(address => uint256) public elixirBalance;
    address public apiAddress;

    constructor(address _external) {
        externalStaking = IExternalStaking(_external);
    }

    function setAddress(address _external) public onlyOwner {
        externalStaking = IExternalStaking(_external);
    }

    function setApiAddress(address _addr) public onlyOwner {
        apiAddress = _addr;
    }

    function setElixirBalances(
        address[] calldata _addresses,
        uint256[] calldata _values
    ) public {
        require(msg.sender == apiAddress, "not api address");
        uint256 i;
        for (i = 0; i < _addresses.length; i++) {
            elixirBalance[_addresses[i]] = _values[i];
        }
    }

    function balanceOf(address _user) external view returns (uint256) {
        uint256 externalBalance = 0;
        if (
            address(externalStaking) !=
            0x0000000000000000000000000000000000000000
        ) {
            externalBalance = externalStaking.balanceOf(_user);
        }
        return externalBalance + elixirBalance[_user];
    }
}
