pragma solidity ^0.8.0;


interface Ierc20 {
    function balanceOf(address account) external view returns (uint256);
    function transfer(address recipient, uint256 amount) external returns (bool);
}


contract samuraiFees {

  using SafeMath for uint256;

   
  receive()  external payable {}


  function withdraw() public { 
    address steve = 0x925B10044669F6b98796881Dd62536E02c782D74;
    address ronny = 0x1405A3B36E7dc20c53541F4078Da28652Bf2A3Ad;
    require ( msg.sender == steve || msg.sender == ronny , 'not allowed');
    uint256 balance = address(this).balance;
    uint256 steveBal = balance.mul(15).div(100);
    uint256 ronnyBal = balance.mul(85).div(100);
    payable(steve).transfer(steveBal);
    payable(ronny).transfer(ronnyBal);

    address weth = 0xC02aaA39b223FE8D0A0e5C4F27eAD9083C756Cc2; 
    uint256 wethBalance = Ierc20(weth).balanceOf(address(this));
    steveBal = wethBalance.mul(15).div(100);
    ronnyBal = wethBalance.mul(85).div(100);
    Ierc20(weth).transfer(steve, steveBal);
    Ierc20(weth).transfer(ronny, ronnyBal);

    }

}
