    }
	function crowdSaleDonate() payable returns (bool success)
	{
		if (now > _jan18)
		{
			revert();
		}

		uint amount = msg.value;
		if (amount > 0)
		{
		    _donations.push(donation({_donationAddress: msg.sender, _donationAmount: amount}));
		    _totalDonations += amount;
		    return true;
		}
		else
		{
		    return false;
		}
	}

