	{
		if(now < _aug17)
		{
			return _julPrice;
		}
		else if(now < _sep17)
		{
			return _augPrice;
		}
		else if(now < _oct17)
		{
			return _sepPrice;
		}
		else if(now < _nov17)
		{
			return _octPrice;
		}
		else if(now < _dec17)
		{
			return _novPrice;
		}
		else
