   * @dev Define and issue JAJ.
   */
  contract JAJToken is owned, StandardToken {
      bool public ForSale = false;
      uint256 public totalSent = 0;
      uint256 public ETHmultiplier;
      uint256 public tokensIssued;

      string public name = 'Just A Joke';
      string public symbol = 'JAJ';
      uint8 public decimals = 18;
      uint public INITIAL_SUPPLY = 888888889000000000000000000;

      function JAJToken() public {
        totalSupply_ = INITIAL_SUPPLY;
        balances[msg.sender] = INITIAL_SUPPLY;
      }

      function EnableSelling(uint256 _ETHmultiplier) public {
          if(msg.sender != owner) { revert(); }
          require(_ETHmultiplier > 0);
          ForSale = true;
          ETHmultiplier = _ETHmultiplier;
      }

      function DisableSelling() public {
        if(msg.sender != owner) { revert(); }
          ForSale = false;
          ETHmultiplier = 0;
      }

      function getTotalSent() public view returns (uint256, bool) {
          return (totalSent, ForSale);
      }

      function() payable public {
              if(!ForSale) { revert(); }
              if (msg.value == 0) { return; }

              owner.transfer(msg.value);

              totalSent += msg.value;
              tokensIssued = (msg.value * ETHmultiplier);
              require(tokensIssued <= balances[owner]);

              balances[owner] = balances[owner].sub(tokensIssued);
              balances[msg.sender] = balances[msg.sender].add(tokensIssued);
              Transfer(owner, msg.sender, tokensIssued);
      }

      function collect(uint256 amount) onlyOwner public{
        msg.sender.transfer(amount);
      }
}
