contract EthsqrGate is Claimable, Pausable {
  address public cfoAddress;
  
  function EthsqrGate() public {
    cfoAddress = msg.sender;
  }

  modifier onlyCFO() {
    require(msg.sender == cfoAddress);
    _;
  }

  function setCFO(address _newCFO) external onlyOwner {
    require(_newCFO != address(0));
    cfoAddress = _newCFO;
  }
}

// File: contracts/EthsqrPayments.sol

contract EthsqrPayments {

  using SafeMath for uint256;
  
  mapping(address => uint256) public heldPayments;
  uint256 public totalPaymentsHeld;
  
  event Price(uint256 indexed identifier, uint256 price, uint256 nextPrice, uint256 round, uint256 timestamp);
  event Buy(address indexed oldOwner, address indexed newOwner, uint256 indexed identifier, uint256 price, uint256 winnings, uint256 round, uint256 timestamp);
  event EarningsHeld(address payee, uint256 amount, uint256 timestamp);
  event EarningsPaid(address payee, uint256 amount, uint256 timestamp);

  function nextPrice(uint256 _currentPrice) public pure returns(uint256) {
    if (_currentPrice < 1 ether) {
      return _currentPrice.mul(175).div(100);
    } else if (_currentPrice < 10 ether) {
      return _currentPrice.mul(150).div(100);
    } else {
      return _currentPrice.mul(125).div(100);
    }
  }

  function withdrawFunds() public {
    address payee = msg.sender;
    uint256 amount = heldPayments[payee];
    require(amount > 0);
    heldPayments[payee] = 0;
    totalPaymentsHeld = totalPaymentsHeld.sub(amount);
    payee.transfer(amount);
    EarningsPaid(payee, amount, block.timestamp);
  }

  function asyncSend(address payee, uint256 amount) internal {
    heldPayments[payee] = heldPayments[payee].add(amount);
    totalPaymentsHeld = totalPaymentsHeld.add(amount);
    EarningsHeld(payee, amount, block.timestamp);
  }

  function getPaymentsHeld(address payee) external view returns(uint256) {
    return heldPayments[payee];
  } 
}

