// File: contracts/EthsqrCore.sol

contract EthsqrCore is EthsqrGate, EthsqrPayments, Random, usingOraclize {
  using SafeMath for uint256;

  event Birth(uint256 identifier, uint256 timestamp);
  event DeterminingWinner(uint256 round, uint256 timestamp);
  event EarningsCleared(uint256 amount, address participant, uint256 timestamp);
  event Join(uint256 identifier, address participant, uint256 round, uint256 timestamp);
  event OraclizeCallbackReceived(uint256 round, uint256 random, uint256 timestamp);
  event OraclizeQuerySent(uint256 round, uint256 timestamp);
  event RoundEnded(uint256 round, address participant, uint256 timestamp);
  event RoundStarted(uint256 round, address participant, uint256 roundStart, uint256 roundEnd, uint256 timestamp);
  event TimeChanged(uint256 roundEnd, address player, uint256 timestamp);
  event UsernameUpdated(address player, string username);
  event WinnerDetermined(uint256 winner, uint256 round, uint256 amount, uint256 timestamp);
  event WinningEntry(address player, uint256 amount);

  struct Entry {
    address participant;
  }

  struct Ethsqr {
    uint256 identifier;
    uint256 parentIdentifier;
    address owner;
    uint256 price;
    uint256 trait;
  }

  struct Round {
    uint256 start;
    address starter;
    address ender;
    address[] winners;
    bool ended;
    uint256 entryPrice;
    uint256 entryCount;
    mapping(uint256 => Entry[]) sqrEntries;
    mapping(address => bool) clearedForEarnings;
    mapping(address => uint256) prizeForParticipant;
  }

  mapping(uint256 => Round) rounds;
  mapping(uint256 => Ethsqr) squares;
  mapping(uint256 => uint256) oracleResult;
  mapping(address => string) usernames;
  
  uint256 private entryPrice;
  uint256 private startPrice;
  uint256 private initialDuration;
  uint256 private oraclizeAllowance;
  uint256 private oraclizeCallbackGas;
  uint256 public roundStart;
  uint256 public roundEnd;
  uint256 public currentRoundNumber;
  bool public pendingOraclize;
  bool public pendingNewRound;
  bool public determiningWinner;

  function EthsqrCore() public {
    paused = true;
    entryPrice = .01 ether;
    startPrice = .005 ether;
    oraclizeAllowance = .012 ether;
    oraclizeCallbackGas = 1500000;
    currentRoundNumber = 0;
    determiningWinner = false;
    pendingNewRound = true;
    pendingOraclize = false;
    initialDuration = 10 minutes;
  }

  /**************************
    public funcs
  ***************************/

  function buySqr(uint256 _identifier) external payable whenNotPaused {
    require(!_isContract(msg.sender));
    Ethsqr storage sqr = squares[_identifier];
    address oldOwner = sqr.owner;
    address newOwner = msg.sender;
    uint256 price = sqr.price;
    uint256 newPrice = nextPrice(price);
    require(oldOwner != newOwner);
    require(msg.value >= price);
    uint256 twoPercent = uint256(SafeMath.div(SafeMath.mul(price, 2), 100));
    uint256 threePercent = uint256(SafeMath.div(SafeMath.mul(price, 3), 100));
    // 2% of transaction goes to dev 
    asyncSend(owner, twoPercent);
    // 3% of transaction goes to mastersqr owner
    asyncSend(squares[0].owner, threePercent);
    uint256 winnings = price.sub(threePercent + twoPercent);
    // send the winnings to the old owner, if the owner is not the contract, otherwise, add to contract balance
    if (oldOwner != address(this)) {
      asyncSend(oldOwner, winnings);
    }
    // calculate and refund any excess wei
    uint256 excess = msg.value - price;
    if (excess > 0) {
      asyncSend(newOwner, excess);
    }
    // update sqr attributes
    sqr.price = newPrice;
    sqr.owner = newOwner;
    // emit buyout events
    Buy(oldOwner, newOwner, _identifier, price, winnings, currentRoundNumber, block.timestamp);
    Price(_identifier, newPrice, nextPrice(newPrice), currentRoundNumber, block.timestamp);
  }

  function createSqr(uint256 _identifier, address _owner, uint256 _parentIdentifier, 
    uint256 _price) external onlyOwner {
    _mintSqr(_identifier, _owner, _parentIdentifier, _price);
  }

  function getRoundDividendShare() public view returns(uint256 share) {
    if(this.balance >= oraclizeAllowance){
      uint256 totalPossible = this.balance.sub(oraclizeAllowance).sub(totalPaymentsHeld);
      return uint256(SafeMath.div(SafeMath.mul(totalPossible, 10), 100));
    }
    return 0;
  }

  function getRoundPrizeShare() public view returns(uint256 share) {
    if(this.balance > oraclizeAllowance){
      uint256 totalPossible = this.balance.sub(oraclizeAllowance).sub(totalPaymentsHeld);
      return uint256(SafeMath.div(SafeMath.mul(totalPossible, 90), 100));
    }
    return 0;
  }

  function getRound(uint256 _identifier) external view returns(
    uint256 start, bool ended, address roundStarter, address roundEnder, 
    uint256 entryCount, uint256 joinPrice) {
    Round storage round = rounds[_identifier];
    ended = round.ended;
    start = round.start;
    roundStarter = round.starter;
    roundEnder = round.ender;
    entryCount = round.entryCount;
    joinPrice = round.entryPrice;
  }

  function getSqr(uint256 _identifier) external view returns(
    uint256 id, address owner, uint256 buyPrice, uint256 nextBuyPrice, 
    uint256 trait) {
    Ethsqr storage sqr = squares[_identifier];
    id = _identifier;
    owner = sqr.owner;
    buyPrice = sqr.price;
    nextBuyPrice = nextPrice(buyPrice);
    trait = sqr.trait;
  }

  function getUsername(address _player) external view returns(string) {
    return usernames[_player];
  }

  function joinSqr(uint256 _identifier) external payable whenNotPaused {
    // players can not join the mastersqr
    require(_identifier > 0);
    // ensure a contract isn't calling this function
    require(!_isContract(msg.sender));
    require(!pendingNewRound);
    require(!pendingOraclize);
    if (block.timestamp < roundEnd) {
      Round storage currentRound = rounds[currentRoundNumber];
      require(msg.value == currentRound.entryPrice);
      require(!determiningWinner);
      address participant = msg.sender;
      uint256 threePercent = uint256(SafeMath.div(SafeMath.mul(entryPrice, 3), 100));
      Ethsqr storage sqr = squares[_identifier];
      uint256 trait = sqr.trait;
      // send 3% dividend to mastersqr owner
      if(squares[0].owner != address(this)){
        asyncSend(squares[0].owner, threePercent);
      }
      // send 3% dividend to sqr owner
      if(squares[_identifier].owner != address(this)){
        asyncSend(squares[_identifier].owner, threePercent);
      }
      // create new entry and add it to the user entries
      currentRound.sqrEntries[_identifier].push(Entry({ participant: participant }));
      currentRound.entryCount = currentRound.entryCount.add(1);
      Join(_identifier, participant, currentRoundNumber, block.timestamp);
      // increase time trait || runs only if time left is less than 1 minute less than the initial duration
      if (trait == 1 && _timeRemaining() < (initialDuration - 1 minutes)) {
        roundEnd = roundEnd.add(1 minutes);
        TimeChanged(roundEnd, participant, block.timestamp);
      }
      // decrease time trait || runs only if there is more than one minute left
      if (trait == 2 && _timeRemaining() > 1 minutes) {
        roundEnd = roundEnd.sub(1 minutes);
        TimeChanged(roundEnd, participant, block.timestamp);
      }
    } else {
      // ensure we aren't currently waiting for a result from Oracle and there is no result yet
      require(!pendingOraclize);
      // ensure there is no result for this round yet
      require(oracleResult[currentRoundNumber] == 0);
      // set the player as the round ender
      currentRound.ender = participant;
      // ensure we can have enough to pay everyone after contacting oraclize
      if(getRoundPrizeShare() > 0){
        // make the call to Oraclize to get the random winner
        _useOraclize();
      } else {
        currentRound.ended = true;
        determiningWinner = false;
        pendingNewRound = true;
        RoundEnded(currentRoundNumber, participant, block.timestamp);
        WinnerDetermined(0, currentRoundNumber, 0, block.timestamp);
      }
      // send the round user half of the join price, back
      asyncSend(participant, entryPrice.div(2));
    }
  }

  modifier roundFinished() {
    require(block.timestamp > roundEnd);
    _;
  }

  function seedGame() external onlyOwner {
    for (uint256 i = 0; i < 26; i++) {
      _mintSqr(i, address(this), 0, 0.01 ether);
    }
    oraclize_setCustomGasPrice(10000000000); // set oraclize gas price to 10 gwei
  }

  function setEntryPrice(uint256 _price) external onlyOwner {
    // cfo can only decrease the join price, never increase it
    require(_price < entryPrice);
    entryPrice = _price;
  }

  function setInitialDuration(uint256 _seconds) external onlyOwner {
    initialDuration = _seconds;
  }

  function setOraclizeAllowance(uint256 _allowance) external onlyOwner {
    oraclizeAllowance = _allowance;
  }

  function setOraclizeCallbackGas(uint256 _gas) external onlyOwner {
    oraclizeCallbackGas = _gas;
  }

  function setOraclizeGasPrice(uint256 _gas) external onlyOwner {
    oraclize_setCustomGasPrice(_gas);
  }

  function setStartPrice(uint256 _price) external onlyOwner {
    startPrice = _price;
  }

  function setUsername(address _player, string _username) public {
    require(_player == msg.sender);
    usernames[_player] = _username;
    UsernameUpdated(_player, _username);
  }

  function startRound() external payable roundFinished whenNotPaused {
    require(pendingNewRound);
    require(msg.value >= startPrice);
    uint256 newRoundNumber = currentRoundNumber.add(1);
    address starter = address(msg.sender);
    // reset all traits every round
    if(newRoundNumber > 1) {
      for (uint256 i = 1; i < 26; i++) {
        Ethsqr storage sqr = squares[i];
        sqr.trait = random(3);
      }
    }
    roundStart = block.timestamp;
    roundEnd = block.timestamp.add(initialDuration);
    pendingNewRound = false;
    Round memory newRound = Round({
      start: block.timestamp,
      ender: address(0x0),
      ended: false,
      starter: starter,
      winners: new address[](0),
      entryCount: 0,
      entryPrice: entryPrice
    });
    rounds[newRoundNumber] = newRound;
    currentRoundNumber = newRoundNumber;
    RoundStarted(newRoundNumber, starter, roundStart, roundEnd, block.timestamp);
  }

  function ownerOf(uint256 _identifier) external view returns (address owner) {
    Ethsqr storage sqr = squares[_identifier];
    owner = sqr.owner;
  }

  /**************************
    internal funcs
  ***************************/

  function __callback(bytes32 myid, string result) public {
    require(msg.sender == oraclize_cbAddress());
    uint256 winner = uint256(parseInt(result));
    oracleResult[currentRoundNumber] = winner;
    pendingOraclize = false;
    OraclizeCallbackReceived(currentRoundNumber, winner, block.timestamp);
    _rewardWinners();
  }

  function _isContract(address _addr) internal view returns (bool) {
    uint size;
    assembly { size := extcodesize(_addr) }
    return size > 0;
  }

  function _mintSqr(uint256 _identifier, address _owner, uint256 _parentIdentifier, uint256 _price) internal {
    require(_identifier >= 0);
    require(squares[_identifier].price == 0);
    address initialOwner = _owner;
    if (_owner == 0x0) {
      initialOwner = address(this);
    }
    Ethsqr memory sqr = Ethsqr({
      owner: initialOwner,
      identifier: _identifier,
      parentIdentifier: _parentIdentifier,
      price: _price,
      trait: _identifier == 0 ? 0 : random(3)
    });
    squares[_identifier] = sqr;
    Birth(_identifier, block.timestamp);
    Price(_identifier, _price, nextPrice(_price), currentRoundNumber, block.timestamp);
  }

  function _rewardWinners() internal roundFinished {
    require(determiningWinner);
    require(oracleResult[currentRoundNumber] != 0);
    Round storage curRound = rounds[currentRoundNumber];
    Entry[] storage winningEntries = curRound.sqrEntries[oracleResult[currentRoundNumber]];
    // 90% of the prize goes to the random square participants
    uint256 prizeShare = getRoundPrizeShare(); 
    // 10% of the prize goes to the round dividend earners
    uint256 divShare = getRoundDividendShare();
    // if there was at least one winner, send funds to everyone!
    if ((winningEntries.length > 0) && (prizeShare > 0) && (divShare > 0)) {
      // final payout per winning entry
      uint256 winningEntryShare = uint256(SafeMath.div(prizeShare, winningEntries.length));
      // iterate over the winning entries to determine which addresses should get a payout for the random square
      for (uint256 i = 0; i < winningEntries.length; i++) {
        address curParticipant = winningEntries[0].participant;
        curRound.prizeForParticipant[curParticipant] = curRound.prizeForParticipant[curParticipant].add(winningEntryShare);
        if (curRound.clearedForEarnings[curParticipant] == false) {
          curRound.winners.push(curParticipant);
          curRound.clearedForEarnings[curParticipant] = true;
          WinningEntry(curParticipant, curRound.entryPrice);
        }
      }
      // iterate over the winners to update their funds 
      for (uint j = 0; j < curRound.winners.length; j++) {
        uint256 amount = curRound.prizeForParticipant[curRound.winners[j]];
        EarningsCleared(amount, curRound.winners[j], block.timestamp);
        asyncSend(curRound.winners[j], amount);
      }
      // send funds to the dividend earners
      uint256 tenPercent = uint256(SafeMath.div(SafeMath.mul(divShare, 10), 100));
      uint256 twentyPercent = uint256(SafeMath.div(SafeMath.mul(divShare, 20), 100));
      uint256 thirtyPercent = uint256(SafeMath.div(SafeMath.mul(divShare, 30), 100));
      // 30% of the div share goes to the owner of the mastersqr
      asyncSend(squares[0].owner, thirtyPercent);
      // 30% of the div share goes to the owner of the random sqr
      asyncSend(squares[oracleResult[currentRoundNumber]].owner, thirtyPercent);
      // 20% of the div share goes to the developer of the contract
      asyncSend(squares[0].owner, twentyPercent);
      // 10% of the div share goes to the round starter
      asyncSend(curRound.starter, tenPercent);
      // 10% of the div share goes to the round ender
      asyncSend(curRound.ender, tenPercent);
    }
    // reset state variables
    curRound.ended = true;
    determiningWinner = false;
    pendingNewRound = true;
    RoundEnded(currentRoundNumber, curRound.ender, block.timestamp);
    WinnerDetermined(oracleResult[currentRoundNumber], currentRoundNumber, prizeShare, block.timestamp);
  }

  function _timeRemaining() internal view returns(uint256 remainder) {
    return roundEnd > block.timestamp ? roundEnd - block.timestamp : 0;
  }

  function _useOraclize() internal {
    require(!pendingOraclize);
    pendingOraclize = true;    
    determiningWinner = true;
    DeterminingWinner(currentRoundNumber, block.timestamp);
    oraclize_query("WolframAlpha", "random number between 1 and 25", oraclizeCallbackGas);
    OraclizeQuerySent(currentRoundNumber, block.timestamp);
  }
}
