    }

    function getPredictions() public view returns (uint24[], uint[] amounts, uint[] user_amounts) {
        amounts = new uint[](ym_index.length);
        user_amounts = new uint[](ym_index.length);
        for (uint24 i = 0; i < ym_index.length; i++) {
            uint24 ym = ym_index[i];
            amounts[i] = ym_totals[ym].amount;
            user_amounts[i] = predictions[ym][msg.sender].total_amount;
        }
        return (ym_index, amounts, user_amounts);
    }

    /*
     * price ===================================================================
     */

    function __callback(bytes32 myid, string result) public {
        require(msg.sender == oraclize_cbAddress());
        CRYPTO_USD = parseInt(result, 0); // discard cents

        if (CRYPTO_USD >= price_target) {
            winning_month = getMonth(now);
            winning_year = getYear(now);

            target_hit = true;
            can_fetch_price = false;
            end_date = now - (now % DAY_IN_SECONDS) + DAY_IN_SECONDS;
        } else {
            _fetch(getNextFetchTime());
        }
        if (CRYPTO_USD >= cutoff) {
            closed = true;
        } else {
            closed = false;
        }
    }

    function _fetch(uint delay) public payable {
        require(msg.sender == owner || msg.sender == oraclize_cbAddress());
        require(can_fetch_price);
        if (is_btc) {
            oraclize_query(delay, "URL", "json(https://api.bitfinex.com/v1/pubticker/btcusd).high");
        } else {
            oraclize_query(delay, "URL", "json(https://api.bitfinex.com/v1/pubticker/ethusd).high");
        }
    }

    /*
     * payout ==================================================================
     */

    function calculatePayoutVariables() public canPayout {
        // idempotent.. but only needs to be run once
        uint contract_duration = (end_date - start_date) / DAY_IN_SECONDS;
        uint24 ym = concatYearMonth(winning_year, winning_month);
        Totals storage ymt = ym_totals[ym];

        // calculate normalizer using only winning predictions
        for (uint16 i = 0; i < ymt.days_with_predictions.length; i++) {
            uint16 day = ymt.days_with_predictions[i];
            uint amount_on_day = ymt.amount_on_day[day];
            uint multiple_for_day = ((contract_duration - day) * amount_on_day);
            payout_multiples[day] = multiple_for_day;
            payout_normalizer += multiple_for_day;
        }
    }

    function calculatePayout(uint amount, uint16 days_since_start) public view returns(uint) {
        uint payout_multiple = payout_multiples[days_since_start];
        uint weight_total = ( payout_multiple * 10**18 ) / payout_normalizer;

        uint24 ym = concatYearMonth(winning_year, winning_month);
        uint winning_pot = ym_totals[ym].amount;
        uint losing_pot = pot_total - winning_pot;

        uint available_pot = ( weight_total * losing_pot ) / 10**18;
        // calculate correct reward percentage of available pot
        uint amount_on_day = ym_totals[ym].amount_on_day[days_since_start];
        uint weight_winning = ( amount * 10**18) / amount_on_day;
        return amount + ( ( weight_winning * available_pot ) / 10**18 );
    }

    function collect() public payable canPayout {
        uint24 ym = concatYearMonth(winning_year, winning_month);
        Predictor storage predictor = predictions[ym][msg.sender];
        require(!predictor.withdrawn);
        uint predictor_payout;

        for(uint8 i = 0; i < predictor.amounts.length; i++) {
            uint amount = predictor.amounts[i];
            if (amount == 0) {
                return;
            }

            uint payout = calculatePayout(amount, predictor.days_since_start[i]);
            if (payout > 0) {
                predictor_payout += payout;
            }
        }
        predictor.withdrawn = true;
        msg.sender.transfer(predictor_payout);
    }

    function refund() public payable {
        require(can_refund);
        uint predictor_refund;
        Predictor storage predictor = user_predictions[msg.sender];
        require(!predictor.withdrawn);
        for (uint16 i = 0; i < predictor.amounts.length; i++){
            predictor_refund += predictor.amounts[i];
        }
        predictor.withdrawn = true;
        msg.sender.transfer(predictor_refund);
    }

