    function getNextFetchTime() internal view returns(uint next_time) {
        if(check_hourly) {
            next_time = now - (now % HOUR_IN_SECONDS) + HOUR_IN_SECONDS;
        } else {
            next_time = now - (now % DAY_IN_SECONDS) + DAY_IN_SECONDS;
        }
        return next_time;
    }

    function concatYearMonth(uint16 year, uint8 month) internal pure returns (uint24) {
        return ( uint24(year) * 100 ) + month;
    }

    function transferLeftover() public adminOnly canPayout {
        // if there is any balance left over in the contract
        uint days_since_end = (now - end_date) / DAY_IN_SECONDS;
        if(days_since_end > 30) {
            owner.transfer(this.balance);
        }
    }

    /*
