contract RefundVault is Ownable {
  using SafeMath for uint256;

  enum State { Active, Refunding, Closed }

  mapping (address => uint256) public deposited;
  address public wallet;
  State public state;

  event Closed();
  event RefundsEnabled();
  event Refunded(address indexed beneficiary, uint256 weiAmount);

  function RefundVault(address _wallet) public {
    require(_wallet != address(0));
    wallet = _wallet;
    state = State.Active;
  }

  function deposit(address investor) onlyOwner public payable {
    require(state == State.Active);
    deposited[investor] = deposited[investor].add(msg.value);
  }

  function close() onlyOwner public {
    require(state == State.Active);
    state = State.Closed;
    Closed();
    wallet.transfer(this.balance);
  }

  function enableRefunds() onlyOwner public {
    require(state == State.Active);
    state = State.Refunding;
    RefundsEnabled();
  }

  function refund(address investor) public {
    require(state == State.Refunding);
    uint256 depositedValue = deposited[investor];
    deposited[investor] = 0;
    investor.transfer(depositedValue);
    Refunded(investor, depositedValue);
  }
}

contract Crowdsale {
  using SafeMath for uint256;

  // The token being sold
  MintableToken public token;

  // start and end timestamps where investments are allowed (both inclusive)
  uint256 public startTime;
  uint256 public endTime;

  // address where funds are collected
  address public wallet;

  // how many token units a buyer gets per wei
  uint256 public rate;

  // amount of raised money in wei
  uint256 public weiRaised;

  /**
   * event for token purchase logging
   * @param purchaser who paid for the tokens
   * @param beneficiary who got the tokens
   * @param value weis paid for purchase
   * @param amount amount of tokens purchased
   */
  event TokenPurchase(address indexed purchaser, address indexed beneficiary, uint256 value, uint256 amount);


  function Crowdsale(uint256 _startTime, uint256 _endTime, uint256 _rate, address _wallet) public {
    // require(_startTime >= now);
    
    require(_endTime >= _startTime);
    require(_rate > 0);
    require(_wallet != address(0));

    token = createTokenContract();
    startTime = _startTime;
    endTime = _endTime;
    rate = _rate;
    wallet = _wallet;
  }

  // creates the token to be sold.
  // override this method to have crowdsale of a specific mintable token.
  function createTokenContract() internal returns (MintableToken) {
    return new MintableToken();
  }


  // fallback function can be used to buy tokens
  function () external payable {
    buyTokens(msg.sender);
  }

  // low level token purchase function
  function buyTokens(address beneficiary) public payable {
    require(beneficiary != address(0));
    require(validPurchase());

    uint256 weiAmount = msg.value;

    // calculate token amount to be created
    uint256 tokens = weiAmount.mul(rate);

    // update state
    weiRaised = weiRaised.add(weiAmount);

    token.mint(beneficiary, tokens);
    TokenPurchase(msg.sender, beneficiary, weiAmount, tokens);

    forwardFunds();
  }

  // send ether to the fund collection wallet
  // override to create custom fund forwarding mechanisms
  function forwardFunds() internal {
    wallet.transfer(msg.value);
  }

  // @return true if the transaction can buy tokens
  function validPurchase() internal view returns (bool) {
    bool withinPeriod = now >= startTime && now <= endTime;
    bool nonZeroPurchase = msg.value != 0;
    return withinPeriod && nonZeroPurchase;
  }

  // @return true if crowdsale event has ended
  function hasEnded() public view returns (bool) {
    return now > endTime;
  }


}

contract FinalizableCrowdsale is Crowdsale, Ownable {
  using SafeMath for uint256;

  bool public isFinalized = false;

  event Finalized();

  /**
   * @dev Must be called after crowdsale ends, to do some extra finalization
   * work. Calls the contract's finalization function.
   */
  function finalize() onlyOwner public {
    require(!isFinalized);
    require(hasEnded());

    finalization();
    Finalized();

    isFinalized = true;
  }

  /**
   * @dev Can be overridden to add finalization logic. The overriding function
   * should call super.finalization() to ensure the chain of finalization is
   * executed entirely.
   */
  function finalization() internal {
  }
}

contract CappedCrowdsale is Crowdsale {
  using SafeMath for uint256;

  uint256 public cap;

  function CappedCrowdsale(uint256 _cap) public {
    require(_cap > 0);
    cap = _cap;
  }

  // overriding Crowdsale#validPurchase to add extra cap logic
  // @return true if investors can buy at the moment
  function validPurchase() internal view returns (bool) {
    bool withinCap = weiRaised.add(msg.value) <= cap;
    return super.validPurchase() && withinCap;
  }

  // overriding Crowdsale#hasEnded to add cap logic
  // @return true if crowdsale event has ended
  function hasEnded() public view returns (bool) {
    bool capReached = weiRaised >= cap;
    return super.hasEnded() || capReached;
  }

}

contract RefundableCrowdsale is FinalizableCrowdsale {
  using SafeMath for uint256;

  // minimum amount of funds to be raised in weis
  uint256 public goal;

  // refund vault used to hold funds while crowdsale is running
  RefundVault public vault;

  function RefundableCrowdsale(uint256 _goal) public {
    require(_goal > 0);
    vault = new RefundVault(wallet);
    goal = _goal;
  }

  // We're overriding the fund forwarding from Crowdsale.
  // In addition to sending the funds, we want to call
  // the RefundVault deposit function
  function forwardFunds() internal {
    vault.deposit.value(msg.value)(msg.sender);
  }

  // if crowdsale is unsuccessful, investors can claim refunds here
  function claimRefund() public {
    require(isFinalized);
    require(!goalReached());

    vault.refund(msg.sender);
  }

  // vault finalization task, called when owner calls finalize()
  function finalization() internal {
    if (goalReached()) {
      vault.close();
    } else {
      vault.enableRefunds();
    }

    super.finalization();
  }

  function goalReached() public view returns (bool) {
    return weiRaised >= goal;
  }

}

contract NacContract is CappedCrowdsale, RefundableCrowdsale {


  // Token Decimals and Distribution
  // ===========
  uint8 public decimals = 18;
  uint256 public maxTokens                        =  1000000000 * 10 ** uint256(decimals); //    1 billion tokens max
  uint256 public totalTokensForSale               =   700000000 * 10 ** uint256(decimals); //  700 million tokens in total
  uint256 public tokensForTeam                    =   200000000 * 10 ** uint256(decimals); //  200 million tokens for team
  uint256 public tokensBounty                     =    15000000 * 10 ** uint256(decimals); //   15 million tokens for bounty
  uint256 public tokensAirdrop                    =    85000000 * 10 ** uint256(decimals); //   85 million tokens for airdrop
  // ==============================



  event EthTransferred(string text);
  event EthRefunded(string text);


  // Constructor
  // ============
  function NacContract(            uint256 _startTime, 
                                   uint256 _endTime, 
                                   uint256 _rate, 
                                   address _wallet, 
                                   uint256 _goal, 
                                   uint256 _cap) 
    CappedCrowdsale(_cap*10**18) FinalizableCrowdsale() RefundableCrowdsale(_goal*10**18) 
    Crowdsale(_startTime, _endTime, _rate, _wallet) 
    public 
    {
      require(_goal <= _cap);
  }
  


  // Token Deployment
  // =================
  function createTokenContract() internal returns (MintableToken) {
    return new TokenContract();  // binding the token contract to this crowdsale
  }
  // ==================

  // Mint bounty tokens
  function mintBountytokens(address _bountyWallet) public onlyOwner {
    token.mint(_bountyWallet,tokensBounty);
  }

  // Change the current rate
  function setCurrentRate(uint256 _rate)  public onlyOwner {
      rate = _rate;
  }

  //change start time
  function setNewStartTime(uint256 _startTime) public onlyOwner {
      startTime = _startTime;
  }

  //change end time
  function setNewEndTime(uint256 _endTime) public onlyOwner {
      endTime = _endTime;
  }



  // Token Purchase
  // =========================
  function () external payable {
    uint256 mintAfterPurchase = msg.value.mul(rate);
      if ((token.totalSupply() + mintAfterPurchase > totalTokensForSale)) {
        msg.sender.transfer(msg.value); // Refund them
        EthRefunded("sale Limit Hit");
        return;
      }

      buyTokens(msg.sender);
  }

  function forwardFunds() internal {
      EthTransferred("Pushing funds to smartEscrow");
      super.forwardFunds();
      
  }
 

  // Finish: Mint Extra Tokens as needed before finalizing the Crowdsale.
  // ====================================================================

  function finish(address _teamFund, address _airdropFund) public onlyOwner {
      
      require(!isFinalized);

      uint256 alreadyMintedTokens = token.totalSupply();
      require(alreadyMintedTokens < maxTokens);
      
      uint256 unsoldTokens = totalTokensForSale - alreadyMintedTokens;
      if (unsoldTokens > 0) {
        tokensAirdrop = tokensAirdrop + unsoldTokens;
      }

      token.mint(_airdropFund,tokensAirdrop);
      token.mint(_teamFund,tokensForTeam);

      finalize();
  }
  // ===============================

  function showTokenBalance(address sender) public constant returns (uint) {
      return token.balanceOf(sender);
  }
  function getWeiRaised() public constant returns (uint) {
      return weiRaised;
  }
  
}
