/**
 * @title AirDrop
 * @dev used for token airdrop purposes
 */
contract AirDrop is Ownable {
    using SafeMath for uint256;
    mapping (address => bool) public claimedAirdropTokens;

    uint256 public numDrops;
    uint256 public dropAmount;
    uint256 public constant AIRDROP_SHARE = 200000000e18; // 200M

    REBToken public reb;

    event TokenDrop(address indexed receiver, uint256 amount);

    /**
     * @dev Constructor function
     * @param token ERC20 token address
     */
    constructor(address token) public {
        require(token != address(0));

        reb = REBToken(token);
    }

    modifier hasBalance() {
        uint256 balance = reb.balanceOf(this);
        require(balance > 0);
        _;
    }

    /**
     * @dev Distributes tokens to recipient addresses
     * @param recipients List of addresses to receive token
     */
    function triggerAirDrop
        (
            address[] recipients,
            uint256 amount
        )
        external
        onlyOwner
        hasBalance
    {
        for (uint256 i = 0; i < recipients.length; i++) {
            dropAmount = dropAmount.add(amount);
            require(dropAmount <= AIRDROP_SHARE && !claimedAirdropTokens[recipients[i]]);

            claimedAirdropTokens[recipients[i]] = true;

            reb.transfer(recipients[i], amount);
            emit TokenDrop(recipients[i], amount);
        }

        numDrops = numDrops.add(recipients.length);
    }

    /**
     * @dev Emergency transfer tokens to contract owner
     * @param amount Number of tokens to transfer
     */
    function emergencyTokenDrain(uint256 amount) public onlyOwner hasBalance {
        reb.transfer(owner, amount);
    }
}
