        require(pausedToPublic);
        pausedToPublic = false;
    }

    // returns the number of tokens for an user
    function balanceOf(address _user) public view returns(uint256) {
        return tokenBalance[_user];
    }

    function buyPrice() public view returns(uint256) {
        if(tokenSupply == 0) {
            return initialTokenPrice + tokenIncrement;
        } else {
            uint256 ethereum = _tokensToEthereum(1e18);
            uint256 dividends = SafeMath.div(ethereum, dividendFee);
            uint256 taxedEther = SafeMath.add(ethereum, dividends);
            return taxedEther;
        }
    }

    // an administrator can repeal the ambassador mode in case of an 
    // emergency / lack of funding
    function disableAmbassadorMode() public {
        require(administrators[msg.sender] == true);
        require(ambassadorMode);
        ambassadorMode = false;
    }

    // primary purchasing function which allows for a masternode referral
    function fund(address _referrer) public payable {
        require(getCodeSize(msg.sender) == 0);
        require(msg.value > minPurchaseAmount && msg.value < maxPurchaseAmount);
        contractBalance = SafeMath.add(contractBalance, msg.value);
        purchaseTokens(msg.value, _referrer);
    }

    // frontend function to get the referral balance of a user
    function getReferralBalance(address _user) public view returns (uint256) {
        return referralBalance[_user];
    }

    // returns the user's accumulated dividends, including the referral earnings
    function getUserDividends(address _user) public view returns (uint256) {
        return ((uint256) ((int256)(earningsPerToken * tokenBalance[_user]) - payouts[_user]) / scaleFactor) + (referralBalance[_user]);
    }
    
    // public function that allows a token holder to manually trigger the latest lottery
    // this saves on gas and potential failures during the purchase of tokens
    function invokeLottery() public {
        require(tokenBalance[msg.sender] > 0);
        Lottery storage lotto = lotteries[lottoQueue];
        require(lotto.instantiated);
        require(!lotto.completed);
        require(!pendingOraclize);
        pendingOraclize = true;
        emit RandomRequested(lotto.identifier);
        // generate an oraclize query by calling wolfram alpha
        // both the min and the max are inclusive
        oraclize_query("WolframAlpha", "random number between 0 and 18");
    }

    function purchaseTokens(uint256 _ethValue, address _referrer) contributionPhase() internal returns(uint256) {
        uint256 lottoFunds = SafeMath.div(_ethValue, lottoFee);
        uint256 undividedDividends = SafeMath.div(SafeMath.sub(_ethValue, lottoFunds), dividendFee);
        uint256 referralFunds = SafeMath.div(undividedDividends, referralFee);
        uint256 dividends = SafeMath.sub(undividedDividends, referralFunds);
        uint256 taxedEther = SafeMath.sub(_ethValue, dividends);
        uint256 tokenAmount = _ethereumToTokens(taxedEther);
        uint256 fee = dividends * scaleFactor;

        // if no referrer has been passed to the contract, check to see
        // if the user has already been referred by someone, previously.
        // this will also override a new referral attempt and use the initial referrer
        if(referrer[msg.sender] != 0x0000000000000000000000000000000000000000){
            _referrer = referrer[msg.sender];
        }
        // ensure the referrer has at least 100 tokens; otherwise set the
        // referrer to nilll
        if(tokenBalance[_referrer] < 100e18){
            _referrer = 0x0000000000000000000000000000000000000000;
        }
        // masternode referral check
        if(_referrer != 0x0000000000000000000000000000000000000000 && _referrer != msg.sender && 
            tokenBalance[_referrer] >= 100e18){ // 100 token masternode requirement
            // referrer receives their bonus
            referralBalance[_referrer] = SafeMath.add(referralBalance[_referrer], referralFunds);
            // store the referral for life
            referrer[msg.sender] = _referrer;
        } else {
            // there was no referrer, or the referrer does not have enough tokens
            dividends = SafeMath.add(dividends, referralFunds);
            // recalculate fee 
            fee = dividends * scaleFactor;
        }
        if(tokenSupply > 0){
            tokenSupply = SafeMath.add(tokenSupply, tokenAmount);
            earningsPerToken += (dividends * scaleFactor / (tokenSupply));
            fee = fee - (fee-(tokenAmount * (dividends * scaleFactor / (tokenSupply))));
        } else {
            tokenSupply = tokenAmount;
        }
        // add the lotto funds to the lotto balance
        lottoBalance = SafeMath.add(lottoBalance, lottoFunds);
        tokenBalance[msg.sender] = SafeMath.add(tokenBalance[msg.sender], tokenAmount);
        int256 purchasePayout = (int256)((earningsPerToken * tokenAmount) - fee);
        payouts[msg.sender] += purchasePayout;
        // if the msg.sender contributes .5 ether or more, they are eligible for the lottery
        if(_ethValue >= lottoMin) {
            Lottery storage lotto = lotteries[lottoIdentifier];
            lotto.ticketHolders.push(msg.sender);
            // every 19th lottery entry should trigger the drawing
            if(lotto.ticketHolders.length == requiredLottoParticipants){
                uint256 lottoReward = lottoBalance;
                lottoBalance = 0;
                // set lottery reward
                lotto.reward = lottoReward;
                // increment lottery identifier and create a fresh, empty one
                lottoIdentifier++;
                _createLottery();
            }
        }  
        emit TokensPurchased(msg.sender, tokenAmount, _ethValue, _referrer);
        return tokenAmount;
    }

    function reinvest() public {
        address user = msg.sender;
        uint256 dividends = SafeMath.sub(getUserDividends(user), referralBalance[user]);
        require(dividends > 0);
        payouts[user] += (int256)(dividends * scaleFactor);
        dividends += referralBalance[user];
        referralBalance[user] = 0;
        uint256 tokenAmount = purchaseTokens(dividends, address(0x0));
        emit DividendsReinvested(user, dividends, tokenAmount);
    }

    function sell(uint256 _tokenAmount) public {
        address user = msg.sender;
        require(tokenBalance[user] > 0);
        require(_tokenAmount <= tokenBalance[user]);
        uint256 ethValue = _tokensToEthereum(_tokenAmount);
        uint256 lottoFunds = SafeMath.div(ethValue, lottoFee);
        // add the lotto funds to the lotto balance
        lottoBalance = SafeMath.add(lottoBalance, lottoFunds);
        uint256 dividends = SafeMath.div(SafeMath.sub(ethValue, lottoFunds), dividendFee);
        uint256 taxedEther = SafeMath.sub(ethValue, dividends);
        tokenSupply = SafeMath.sub(tokenSupply, _tokenAmount);
        tokenBalance[user] = SafeMath.sub(tokenBalance[user], _tokenAmount);
        int256 payout = (int256) (earningsPerToken * _tokenAmount + (taxedEther * scaleFactor));
        payouts[user] -= payout;      
        if (tokenSupply > 0) {
            earningsPerToken = SafeMath.add(earningsPerToken, (dividends * scaleFactor) / tokenSupply);
        }
        emit TokensSold(user, _tokenAmount, taxedEther);
    }

    function sellPrice() public view returns(uint256) {
        if(tokenSupply == 0){
            return initialTokenPrice - tokenIncrement;
        } else {
            uint256 ethereum = _tokensToEthereum(1e18);
            uint256 dividends = SafeMath.div(ethereum, dividendFee);
            uint256 taxedEther = SafeMath.sub(ethereum, dividends);
            return taxedEther;
        }
    }

    // administrator can whitelist an administrator
    function setAdministrator(address _user) public {
        require(administrators[msg.sender] == true);
        administrators[_user] = true;
    }

    // administrator can whitelist an ambassador
    function setAmbassador(address _user) public {
        require(administrators[msg.sender] == true);
        ambassadors[_user] = true;
    }

    function totalSupply() public view returns(uint256) {
        return tokenSupply;
    }

    function withdraw() public {
        address user = msg.sender;
        uint256 dividends = SafeMath.sub(getUserDividends(user), referralBalance[user]);
        require(dividends > 0);
        payouts[user] += (int256) (dividends * scaleFactor);
        // reset accumulated referral earnings
        referralBalance[user] = 0;
        dividends += referralBalance[user];
        // update the contract balance
        contractBalance = SafeMath.sub(contractBalance, dividends);
        user.transfer(dividends);
        emit DividendsWithdrawn(user, dividends);
    }

    // purchase tokens by sending funds directly to the contract
    function () payable public {
        require(getCodeSize(msg.sender) == 0);
        // call funding function with a blank referrer
        fund(address(0x0000000000000000000000000000000000000000));
    }

    /*
        MODIFIERS
    */

    // modifier function that checks if only ambassadors can currently contribute
    modifier contributionPhase(){
        if(ambassadorMode) {
            if((contractBalance < ambassadorQuota)) {
                require(ambassadors[msg.sender]);
                _;
            } else {
                ambassadorMode = false;
                _;
            }
        } else {
            require(!pausedToPublic);
            _;
        }
    }

    /*
        INTERNAL FUNCTIONS
    */

    function __callback(bytes32 _queryId, string _result) public {
        require(msg.sender == oraclize_cbAddress());
        require(lotteryRandomed[_queryId] == false);
        Lottery storage lotto = lotteries[lottoQueue];
        require(!lotto.completed);
        uint256 triggererReward;
        uint256 winnerReward;
        address winner;
        address triggerer;
        // parseInt function is available in usingOraclize contract
        uint256 random = uint256(parseInt(_result));
        (winnerReward, triggererReward, winner, triggerer) = _rewardLotteryWinners(lottoQueue, random);
        lottoQueue++;
        lotteryRandomed[_queryId] = true;
        pendingOraclize = false;
        emit LotteryWinnerDetermined(winner, winnerReward, triggerer, triggererReward, random);
    }

    function _createLottery() internal {
        Lottery memory newLotto = Lottery(true, lottoIdentifier, new address[](0), false, 0);
        lotteries[lottoIdentifier] = newLotto;
        emit LotteryCreated(lottoIdentifier);
    }

    function _ethereumToTokens(uint256 _ethereum) internal view returns(uint256) {
        uint256 _tokenPriceInitial = initialTokenPrice * 1e18;
        uint256 tokenAmount = 
         (
            (
                SafeMath.sub(
                    (_sqrt
                        (
                            (_tokenPriceInitial**2)
                            +
                            (2*(tokenIncrement * 1e18)*(_ethereum * 1e18))
                            +
                            (((tokenIncrement)**2)*(tokenSupply**2))
                            +
                            (2*(tokenIncrement)*_tokenPriceInitial*tokenSupply)
                        )
                    ), _tokenPriceInitial
                )
            )/(tokenIncrement)
        )-(tokenSupply)
        ;
        return tokenAmount;
    }

    function _rewardLotteryWinners(uint256 _identifier, uint256 _result) internal returns (uint256, uint256, address, address) {
        Lottery storage lotto = lotteries[_identifier];
        // reward for being the 19th participant
        uint256 triggererFunds = SafeMath.div(lotto.reward, 5);
        // reward for the lucky ticket holder
        uint256 luckyFunds = SafeMath.sub(lotto.reward, triggererFunds);
        // the triggerer is the last index of the array
        address triggerer = lotto.ticketHolders[requiredLottoParticipants - 1];
        address winner = lotto.ticketHolders[_result];
        winner.transfer(luckyFunds);
        triggerer.transfer(triggererFunds);
        contractBalance = SafeMath.sub(contractBalance, lotto.reward);
        lotto.completed = true;
        return (luckyFunds, triggererFunds, winner, triggerer);
    }

    function _sqrt(uint x) internal pure returns (uint y) {
        uint z = (x + 1) / 2;
        y = x;
        while (z < y) {
            y = z;
            z = (x / z + z) / 2;
        }
    }

    function _tokensToEthereum(uint256 _tokens) public view returns(uint256) {
        uint256 tokens = (_tokens + 1e18);
        uint256 supply = (tokenSupply + 1e18);
        uint256 ethValue =
        (
            SafeMath.sub(
                (
                    (
                        (
                            initialTokenPrice +(tokenIncrement * (supply/1e18))
                        )-tokenIncrement
                    )*(tokens - 1e18)
                ),(tokenIncrement*((tokens**2-tokens)/1e18))/2
            )
        /1e18);
        return ethValue;
    }
}
