contract VCCToken is owned, TokenERC20 {

    uint256 public sellPrice;
    uint256 public buyPrice;
    
     //1 Billion token for new user registration
    uint256 public constant newUserReward = 100;
    uint256 public newUserRewardPool=1000000000;
    
    //500 Million token for video tweet
    uint256 public constant reTweetReward = 1;
    uint256 public reTweetRewardPool=500000000;
    
    //100 Million token for reCommentReward 
    uint256 public constant reCommentReward = 10;
    uint256 public reCommentRewardPool=100000000;
    
    // define watch video cost
    uint256 public constant aVideoCost = 1;
    uint256 public constant bVideoCost = 10;
    uint256 public constant cVideoCost = 100;
    

    mapping (address => bool) public frozenAccount;

    /* This generates a public event on the blockchain that will notify clients */
    event FrozenFunds(address target, bool frozen);

    /* Initializes contract with initial supply tokens to the creator of the contract */
    function VCCToken(
        uint256 initialSupply,
        string tokenName,
        string tokenSymbol
    ) TokenERC20(initialSupply, tokenName, tokenSymbol) public {}
    
    //when user register, they will be rewarded VCCTokens
    event newRegister(address newUser);
    function registerNewUser(address newUser) public onlyOwner whenNotPaused {
        //we are not check if this is indeed a new user, the app side should check
        if(balanceOf[newUser] == 0 && newUserRewardPool >= newUserReward){
            balanceOf[newUser] = newUserReward;
            newUserRewardPool -= newUserReward;
        }
    }
    //when user retweet our videos they will be rewarded VCCTokens
    event reTweeted(address user);
    function rewardRetweet(address user) public onlyOwner whenNotPaused{
        if (reTweetRewardPool >= reTweetReward) {
            balanceOf[user] += reTweetReward;
            reTweetRewardPool -= reTweetReward;
        } 
    }
    
    //when user recomment our videos they will be rewarded VCCTokens 
    event reCommented(address user);
    function rewardComment(address user) public onlyOwner whenNotPaused{
        if(reCommentRewardPool >= reCommentReward) {
            balanceOf[user] += reCommentReward;
            reCommentRewardPool -= reCommentReward;
        }
    }
    
  //Website user watch video will be taken away VCCTokens accordingly
    event aVideoWatched(address user);
    function watchVideoA(address user) public onlyOwner whenNotPaused{
        balanceOf[user] -= aVideoCost;
        reTweetRewardPool += aVideoCost;
    }
   event bVideoWatched(address user);
    function watchVideoB(address user) public  onlyOwner whenNotPaused{
        balanceOf[user] -= bVideoCost;
        reTweetRewardPool += bVideoCost;
    } 
    event cVideoWatched(address user);
    function watchVideoC(address user) public  onlyOwner whenNotPaused{
        balanceOf[user] -= cVideoCost;
        reTweetRewardPool += cVideoCost;
    }
    
    /* Internal transfer, only can be called by this contract */
    function _transfer(address _from, address _to, uint _value) internal {
        require (_to != 0x0);                               // Prevent transfer to 0x0 address. Use burn() instead
        require (balanceOf[_from] >= _value);               // Check if the sender has enough
        require (balanceOf[_to] + _value >= balanceOf[_to]); // Check for overflows
        require(!frozenAccount[_from]);                     // Check if sender is frozen
        require(!frozenAccount[_to]);                       // Check if recipient is frozen
        balanceOf[_from] -= _value;                         // Subtract from the sender
        balanceOf[_to] += _value;                           // Add the same to the recipient
        emit Transfer(_from, _to, _value);
    }

    /// @notice Create `mintedAmount` tokens and send it to `target`
    /// @param target Address to receive the tokens
    /// @param mintedAmount the amount of tokens it will receive
    function mintToken(address target, uint256 mintedAmount) onlyOwner public {
        balanceOf[target] += mintedAmount;
        totalSupply += mintedAmount;
        emit Transfer(0, this, mintedAmount);
        emit Transfer(this, target, mintedAmount);
    }

    /// @notice `freeze? Prevent | Allow` `target` from sending & receiving tokens
    /// @param target Address to be frozen
    /// @param freeze either to freeze it or not
    function freezeAccount(address target, bool freeze) onlyOwner public {
        frozenAccount[target] = freeze;
        emit FrozenFunds(target, freeze);
    }

    /// @notice Allow users to buy tokens for `newBuyPrice` eth and sell tokens for `newSellPrice` eth
    /// @param newSellPrice Price the users can sell to the contract
    /// @param newBuyPrice Price users can buy from the contract
    function setPrices(uint256 newSellPrice, uint256 newBuyPrice) onlyOwner public {
        sellPrice = newSellPrice;
        buyPrice = newBuyPrice;
    }

    /// @notice Buy tokens from contract by sending ether
    function buy() payable public {
        uint amount = msg.value / buyPrice;               // calculates the amount
        _transfer(this, msg.sender, amount);              // makes the transfers
    }

    /// @notice Sell `amount` tokens to contract
    /// @param amount amount of tokens to be sold
    function sell(uint256 amount) public {
        
        require(address(this).balance >= amount * sellPrice);      // checks if the contract has enough ether to buy
        _transfer(msg.sender, this, amount);              // makes the transfers
        msg.sender.transfer(amount * sellPrice);          // sends ether to the seller. It's important to do this last to avoid recursion attacks
    }
}
