/**
 * @title UpdatableCrowdsale
 * @dev Extension of AllowanceCrowdsale contract that allows updates to the exchange rate used to 
 * calculate the token sale price (rate of tokens to issue per wei contributed).  
 * Note that what should be provided to the constructor is the _initialRate (price per token in wei),
 * and the _tokenWallet that is going to provide an allowance from which the token sale can distribute
 * the tokent.
 */
contract UpdatableCrowdsale is AllowanceCrowdsale, Ownable {
  // using SafeMath for uint256; // using from parent

  event RateUpdated(uint256 oldRate, uint256 newRate);

  // The number of decimals for the rate
  uint256 public rateDecimals = 9;

  /**
   * @dev Constructor, takes the rate of tokens received per wei contributed, the
   * wallet address which will hold the authorized tokens for the sale (and also
   * is the recipient of the Ether contrubited), and the ERC20 token contract
   * for which we will be selling tokens.
   * @param _rate Number of tokens a buyer gets per wei at the start of the crowdsale
   * @param _wallet The address of the wallet from which tokens will be sold
   * @param _token The ERC20 token contract that holds the tokens we're selling
   */
  constructor (uint256 _rate, address _wallet, ERC20 _token) public
    AllowanceCrowdsale(_wallet)
    Crowdsale(_rate, _wallet, _token)
  {
    // Everything handled in parents
  }

  /**
   * @dev Returns the rate of tokens per wei at the present time. 
   * @return The number of tokens a buyer gets per wei at a given time
   */
  function setCurrentRate(uint256 _newRate) public onlyOwner returns (uint256) {
    require(_newRate > 0); 
    uint256 oldRate_ = rate;
    rate = _newRate;
    emit RateUpdated(oldRate_, rate);
  }

  /**
   * @dev Overrides parent method taking into account variable rate.
   * @param _weiAmount The value in wei to be converted into tokens
   * @return The number of tokens _weiAmount wei will buy at present time
   */
  function _getTokenAmount(uint256 _weiAmount) internal view returns (uint256) {
    return rate.mul(_weiAmount).div(10**rateDecimals);
  }

  /**
   * @dev Get token estimate based on current rate
   * @param _weiAmount The amount of wei that will be sent
   */ 
  function estimate(uint256 _weiAmount) public view returns (uint256) {
    return _getTokenAmount(_weiAmount);
  }

}
