    mapping(bytes32=>bytes32) oraclize_randomDS_args;
    mapping(bytes32=>bool) oraclize_randomDS_sessionKeysHashVerified;

    function verifySig(bytes32 tosignh, bytes dersig, bytes pubkey) internal returns (bool){
        bool sigok;
        address signer;

        bytes32 sigr;
        bytes32 sigs;

        bytes memory sigr_ = new bytes(32);
        uint offset = 4+(uint(dersig[3]) - 0x20);
        sigr_ = copyBytes(dersig, offset, 32, sigr_, 0);
        bytes memory sigs_ = new bytes(32);
        offset += 32 + 2;
        sigs_ = copyBytes(dersig, offset+(uint(dersig[offset-1]) - 0x20), 32, sigs_, 0);

        assembly {
            sigr := mload(add(sigr_, 32))
            sigs := mload(add(sigs_, 32))
        }


        (sigok, signer) = safer_ecrecover(tosignh, 27, sigr, sigs);
        if (address(keccak256(pubkey)) == signer) return true;
        else {
            (sigok, signer) = safer_ecrecover(tosignh, 28, sigr, sigs);
            return (address(keccak256(pubkey)) == signer);
        }
    }

    function oraclize_randomDS_proofVerify__sessionKeyValidity(bytes proof, uint sig2offset) internal returns (bool) {
        bool sigok;

        // Step 6: verify the attestation signature, APPKEY1 must sign the sessionKey from the correct ledger app (CODEHASH)
        bytes memory sig2 = new bytes(uint(proof[sig2offset+1])+2);
        copyBytes(proof, sig2offset, sig2.length, sig2, 0);

        bytes memory appkey1_pubkey = new bytes(64);
        copyBytes(proof, 3+1, 64, appkey1_pubkey, 0);

        bytes memory tosign2 = new bytes(1+65+32);
        tosign2[0] = byte(1); //role
        copyBytes(proof, sig2offset-65, 65, tosign2, 1);
        bytes memory CODEHASH = hex"fd94fa71bc0ba10d39d464d0d8f465efeef0a2764e3887fcc9df41ded20f505c";
        copyBytes(CODEHASH, 0, 32, tosign2, 1+65);
        sigok = verifySig(sha256(tosign2), sig2, appkey1_pubkey);

        if (sigok == false) return false;


        // Step 7: verify the APPKEY1 provenance (must be signed by Ledger)
        bytes memory LEDGERKEY = hex"7fb956469c5c9b89840d55b43537e66a98dd4811ea0a27224272c2e5622911e8537a2f8e86a46baec82864e98dd01e9ccc2f8bc5dfc9cbe5a91a290498dd96e4";

        bytes memory tosign3 = new bytes(1+65);
        tosign3[0] = 0xFE;
        copyBytes(proof, 3, 65, tosign3, 1);

        bytes memory sig3 = new bytes(uint(proof[3+65+1])+2);
        copyBytes(proof, 3+65, sig3.length, sig3, 0);

        sigok = verifySig(sha256(tosign3), sig3, LEDGERKEY);

        return sigok;
    }

    modifier oraclize_randomDS_proofVerify(bytes32 _queryId, string _result, bytes _proof) {
        // Step 1: the prefix has to match 'LP\x01' (Ledger Proof version 1)
        require((_proof[0] == "L") && (_proof[1] == "P") && (_proof[2] == 1));

        bool proofVerified = oraclize_randomDS_proofVerify__main(_proof, _queryId, bytes(_result), oraclize_getNetworkName());
        require(proofVerified);

        _;
    }

    function oraclize_randomDS_proofVerify__returnCode(bytes32 _queryId, string _result, bytes _proof) internal returns (uint8){
        // Step 1: the prefix has to match 'LP\x01' (Ledger Proof version 1)
        if ((_proof[0] != "L")||(_proof[1] != "P")||(_proof[2] != 1)) return 1;

        bool proofVerified = oraclize_randomDS_proofVerify__main(_proof, _queryId, bytes(_result), oraclize_getNetworkName());
        if (proofVerified == false) return 2;

        return 0;
    }

    function matchBytes32Prefix(bytes32 content, bytes prefix, uint n_random_bytes) internal pure returns (bool){
        bool match_ = true;

        require(prefix.length == n_random_bytes);

        for (uint256 i=0; i< n_random_bytes; i++) {
            if (content[i] != prefix[i]) match_ = false;
        }

        return match_;
    }

    function oraclize_randomDS_proofVerify__main(bytes proof, bytes32 queryId, bytes result, string context_name) internal returns (bool){

        // Step 2: the unique keyhash has to match with the sha256 of (context name + queryId)
        uint ledgerProofLength = 3+65+(uint(proof[3+65+1])+2)+32;
        bytes memory keyhash = new bytes(32);
        copyBytes(proof, ledgerProofLength, 32, keyhash, 0);
        if (!(keccak256(keyhash) == keccak256(sha256(context_name, queryId)))) return false;

        bytes memory sig1 = new bytes(uint(proof[ledgerProofLength+(32+8+1+32)+1])+2);
        copyBytes(proof, ledgerProofLength+(32+8+1+32), sig1.length, sig1, 0);

        // Step 3: we assume sig1 is valid (it will be verified during step 5) and we verify if 'result' is the prefix of sha256(sig1)
        if (!matchBytes32Prefix(sha256(sig1), result, uint(proof[ledgerProofLength+32+8]))) return false;

        // Step 4: commitment match verification, keccak256(delay, nbytes, unonce, sessionKeyHash) == commitment in storage.
        // This is to verify that the computed args match with the ones specified in the query.
        bytes memory commitmentSlice1 = new bytes(8+1+32);
        copyBytes(proof, ledgerProofLength+32, 8+1+32, commitmentSlice1, 0);

        bytes memory sessionPubkey = new bytes(64);
        uint sig2offset = ledgerProofLength+32+(8+1+32)+sig1.length+65;
        copyBytes(proof, sig2offset-64, 64, sessionPubkey, 0);

        bytes32 sessionPubkeyHash = sha256(sessionPubkey);
        if (oraclize_randomDS_args[queryId] == keccak256(commitmentSlice1, sessionPubkeyHash)){ //unonce, nbytes and sessionKeyHash match
            delete oraclize_randomDS_args[queryId];
        } else return false;


        // Step 5: validity verification for sig1 (keyhash and args signed with the sessionKey)
        bytes memory tosign1 = new bytes(32+8+1+32);
        copyBytes(proof, ledgerProofLength, 32+8+1+32, tosign1, 0);
        if (!verifySig(sha256(tosign1), sig1, sessionPubkey)) return false;

        // verify if sessionPubkeyHash was verified already, if not.. let's do it!
        if (oraclize_randomDS_sessionKeysHashVerified[sessionPubkeyHash] == false){
            oraclize_randomDS_sessionKeysHashVerified[sessionPubkeyHash] = oraclize_randomDS_proofVerify__sessionKeyValidity(proof, sig2offset);
        }

        return oraclize_randomDS_sessionKeysHashVerified[sessionPubkeyHash];
    }

    // the following function has been written by Alex Beregszaszi (@axic), use it under the terms of the MIT license
    function copyBytes(bytes from, uint fromOffset, uint length, bytes to, uint toOffset) internal pure returns (bytes) {
        uint minLength = length + toOffset;

        // Buffer too small
        require(to.length >= minLength); // Should be a better way?

        // NOTE: the offset 32 is added to skip the `size` field of both bytes variables
        uint i = 32 + fromOffset;
        uint j = 32 + toOffset;

        while (i < (32 + fromOffset + length)) {
            assembly {
                let tmp := mload(add(from, i))
                mstore(add(to, j), tmp)
            }
            i += 32;
            j += 32;
        }

        return to;
    }

    // the following function has been written by Alex Beregszaszi (@axic), use it under the terms of the MIT license
    // Duplicate Solidity's ecrecover, but catching the CALL return value
    function safer_ecrecover(bytes32 hash, uint8 v, bytes32 r, bytes32 s) internal returns (bool, address) {
        // We do our own memory management here. Solidity uses memory offset
        // 0x40 to store the current end of memory. We write past it (as
        // writes are memory extensions), but don't update the offset so
        // Solidity will reuse it. The memory used here is only needed for
        // this context.

        // FIXME: inline assembly can't access return values
        bool ret;
        address addr;

        assembly {
            let size := mload(0x40)
            mstore(size, hash)
            mstore(add(size, 32), v)
            mstore(add(size, 64), r)
            mstore(add(size, 96), s)

            // NOTE: we can reuse the request memory because we deal with
            //       the return code
            ret := call(3000, 1, 0, size, 128, size, 32)
            addr := mload(size)
        }

        return (ret, addr);
    }

    // the following function has been written by Alex Beregszaszi (@axic), use it under the terms of the MIT license
    function ecrecovery(bytes32 hash, bytes sig) internal returns (bool, address) {
        bytes32 r;
        bytes32 s;
        uint8 v;

        if (sig.length != 65)
          return (false, 0);

        // The signature format is a compact form of:
        //   {bytes32 r}{bytes32 s}{uint8 v}
        // Compact means, uint8 is not padded to 32 bytes.
        assembly {
            r := mload(add(sig, 32))
            s := mload(add(sig, 64))

            // Here we are loading the last 32 bytes. We exploit the fact that
            // 'mload' will pad with zeroes if we overread.
            // There is no 'mload8' to do this, but that would be nicer.
            v := byte(0, mload(add(sig, 96)))

            // Alternative solution:
            // 'byte' is not working due to the Solidity parser, so lets
            // use the second best option, 'and'
            // v := and(mload(add(sig, 65)), 255)
        }

        // albeit non-transactional signatures are not specified by the YP, one would expect it
        // to match the YP range of [27, 28]
        //
        // geth uses [0, 1] and some clients have followed. This might change, see:
        //  https://github.com/ethereum/go-ethereum/issues/2053
        if (v < 27)
          v += 27;

        if (v != 27 && v != 28)
            return (false, 0);

        return safer_ecrecover(hash, v, r, s);
    }

    function safeMemoryCleaner() internal pure {
        assembly {
            let fmem := mload(0x40)
            codecopy(fmem, codesize, sub(msize, fmem))
        }
    }

}
contract safeApi{
    
   modifier safe(){
        address _addr = msg.sender;
        require (_addr == tx.origin,'Error Action!');
        uint256 _codeLength;
        assembly {_codeLength := extcodesize(_addr)}
        require(_codeLength == 0, "Sender not authorized!");
            _;
    }


    
 function toBytes(uint256 _num) internal returns (bytes _ret) {
   assembly {
        _ret := mload(0x10)
        mstore(_ret, 0x20)
        mstore(add(_ret, 0x20), _num)
    }
}

function subStr(string _s, uint start, uint end) internal pure returns (string){
        bytes memory s = bytes(_s);
        string memory copy = new string(end - start);
//        string memory copy = new string(5);
          uint k = 0;
        for (uint i = start; i < end; i++){ 
            bytes(copy)[k++] = bytes(_s)[i];
        }
        return copy;
    }
     

 function safePercent(uint256 a,uint256 b) 
      internal
      constant
      returns(uint256)
      {
        assert(a>0 && a <=100);
        return  div(mul(b,a),100);
      }
      
  function mul(uint256 a, uint256 b) internal pure returns (uint256) {
    uint256 c = a * b;
    assert(a == 0 || c / a == b);
    return c;
  }
 
  function div(uint256 a, uint256 b) internal pure returns (uint256) {
    // assert(b > 0); // Solidity automatically throws when dividing by 0
    uint256 c = a / b;
    // assert(a == b * c + a % b); // There is no case in which this doesn't hold
    return c;
  }
 
  function sub(uint256 a, uint256 b) internal pure returns (uint256) {
    assert(b <= a);
    return a - b;
  }
 
  function add(uint256 a, uint256 b) internal pure returns (uint256) {
    uint256 c = a + b;
    assert(c >= a);
    return c;
  }

}
contract gameDicePk is safeApi,usingOraclize{
mapping(bytes32=>uint256) private validQueryId;
    struct player
    {
        uint256 id;
        uint256 balance;//wei
        uint256 timeStamp;
        address addr;
    }

    struct  gameConfig
    {
        uint256 buyDrawScale;
        uint256 minBetWei;
        uint256 maxBetWei;
        uint256 countdown;
        uint256 pushWei;
        uint8 cancelFeePct;
        uint8 winnerFeePct;
    }

    struct  table
    {
        uint256 betAmount;
        uint256 endTime;
        uint8  openIndex;
        uint8  status;//1 has opened the table, 2 has started, 3 is waiting for the draw, 4 has been completed, 5 canceled
        uint8   diceA;
        uint8   diceB;
        mapping(uint8=>uint256) position;
    }
   
   event Bet(
       address indexed _addr,
       uint256 _tableId,
       uint256 _amount, 
       uint8 _position,
       uint8 indexed _status,
       uint8 _posStatus,
       uint256 _endTime
     );

  event Cancel( uint256 indexed _tableId);

  event Finish(
        uint256 _tableId,
        uint256 _amount,
        uint256 indexed pos1,
        uint256 indexed pos2,
        uint256 indexed pos3,
        uint8 _position,//Marked position is occupied 1 1-2, 2 2-3, 3 1-3, 4,123
        uint8 _diceA,
        uint8 _diceB
      );

    mapping (uint256 => player) public player_;
    mapping (address => uint256) public playAddr_;
    mapping (uint256 => uint256) public playAff_;
    mapping(uint256 => table) private tables_;
     uint256[200] openTable_;//Maximum free table number 200
     gameConfig public gameConfig_;
     address public admin__;
     uint256 private autoPlayId_=123456;
     uint256 private autoTableId_=0;
     uint32 private  CUSTOM_GASLIMIT =150000;//Cost of payment calculation gas
    
     constructor() public {
            admin__ = msg.sender;
            uint256 gasPrice=10100000000;//10.1GWEI
            oraclize_setCustomGasPrice(gasPrice);//Sent to the draw absenteeism Gwei
            gameConfig_=gameConfig(
          3,//Buy a draw bid 1/3
          60000000000000000,//0.06 eth Minimum 
          12000000000000000000,//12 eth Maximum
           300 seconds,//Delayed Draw 5 minutes
          gasPrice*CUSTOM_GASLIMIT,//Total cost of Mining to be paid
          3//Cancellation fees% - Partially used to pay the Mining fee
          ,6//Winner handling Fee% - Partially used to pay the Mining fee
         );
        getPlayId(admin__);
    }
 

 function withdraw(uint256 pid) safe() external{
        require(playAddr_[msg.sender] == pid,'Error Action');
        require(player_[pid].addr == msg.sender,'Error Action');
        require(player_[pid].balance >0,'Insufficient balance');
        uint256 balance =player_[pid].balance;
        player_[pid].balance=0;
        return player_[pid].addr.transfer(balance);
    }
    
    //Lottery callback
    function __callback(bytes32 myid, string result) public  {
            require (validQueryId[myid] > 0,'Error!');
            uint256 _tableId=validQueryId[myid];
            delete validQueryId[myid];
            require(msg.sender == oraclize_cbAddress(),'Error 1');
            __lottery(_tableId,result);
    }
     
     
     //Lottery program core code   
      function __lottery(uint256 _tableId,string strNumber) private safe(){
                table storage _t=tables_[_tableId];
                require(_t.status==2 || _t.status==3,'Error 2');
                require(now > _t.endTime,'Error3');
                uint256  diceA=parseInt(subStr(strNumber,0,1));
                require(diceA >=1 && diceA <=6,'Error4');
                uint256  diceB=parseInt(subStr(strNumber,2,3));
                require(diceB >=1 && diceB <=6,'Error5');
                _t.status=4;
                _t.diceA=uint8(diceA);
                _t.diceB=uint8(diceB);
               
                openTable_[_t.openIndex]=0;//Mark the table as free
              
            winnerTransfer(_tableId,_t);
            
            emit Finish(
            _tableId,_t.betAmount,
            tables_[_tableId].position[1],
            tables_[_tableId].position[2],
            tables_[_tableId].position[3],
            getPosStatus(_tableId),_t.diceA,_t.diceB
            );
      }
      
      //Transfer eth to the winner
      function winnerTransfer(uint256 _tableId,table storage _t) private{
          
            uint8 winPos=0;
              //Verification winner
              if(_t.diceA>_t.diceB){
                  winPos=1;
              }else if(_t.diceB>_t.diceA){
                  winPos=2;
              }else{
                  winPos=3;
              }
              
              //The total prize ETH 
              uint256 _balance=0;
                
              if(_t.position[1]>0){
                  _balance=_t.betAmount;
              }
              if(_t.position[2]>0){
                   _balance=add(_balance,_t.betAmount);
              }
              if(_t.position[3]>0){
                     _balance=add(_balance,div(_t.betAmount,gameConfig_.buyDrawScale));
              }
              //winner player
              uint256 _winPid=_t.position[winPos];
              uint256 _systemFee=0;
         
              //Give the winner ETH
              if(_winPid>0){
               _systemFee=safePercent(gameConfig_.winnerFeePct,_balance);
               player_[_winPid].balance=add(player_[_winPid].balance,sub(_balance,_systemFee));
              }else{
                //No winners, return ETH
                 uint256 _fee=0;
              if(_t.position[1]>0){
                 uint256 _pos1Pid=_t.position[1];
                _fee=safePercent(gameConfig_.cancelFeePct,_t.betAmount);
                 _systemFee=_fee;
                 player_[_pos1Pid].balance=add(player_[_pos1Pid].balance,sub(_t.betAmount,_fee));
              }
              if(_t.position[2]>0){
                 uint256 _pos2Pid=_t.position[2];
                 _fee=safePercent(gameConfig_.cancelFeePct,_t.betAmount);
                 _systemFee=add(_systemFee,_fee);
                 player_[_pos2Pid].balance=add(player_[_pos2Pid].balance,sub(_t.betAmount,_fee));
              }
              
              if(_t.position[3]>0){
                 uint256 _pos3Pid=_t.position[3];
                 uint256 _pos3Amount=div(_t.betAmount,gameConfig_.buyDrawScale);
                 _fee=safePercent(gameConfig_.cancelFeePct,_pos3Amount);
                 _systemFee=add(_systemFee,_fee);
                 player_[_pos3Pid].balance=add(player_[_pos3Pid].balance,sub(_pos3Amount,_fee));
              }
           }
              uint256 _adminId=playAddr_[admin__];
              _systemFee=sub(_systemFee,gameConfig_.pushWei);//The admin bears the Fees of the mining
              player_[_adminId].balance= add(player_[_adminId].balance,_systemFee);
      }
      
      //Marked position is occupied
      function getPosStatus(uint256 _tableId) private view returns(uint8){
           table storage  _t=tables_[_tableId];
            if(_t.status==1)
                    return 0;
                uint8 _posStatus=3;
                // 1 1-2, 2 2-3, 3 1-3, 4 123
                if(_t.position[1]>0 && _t.position[2]>0 && _t.position[3]>0){
                    _posStatus=4;
                }else if(_t.position[1]>0 && _t.position[2]>0){
                      _posStatus=1;
                }else if(_t.position[2]>0 && _t.position[3]>0){
                       _posStatus=2;
                }
          return _posStatus;
      }

    //Considering the failure of the draw HTTP request, there will be a table with no results to manually cancel the return to ETH
    function closeTable(uint256 _tableId) safe() external{
            require(msg.sender == admin__,'Error 1');
             table storage _t=tables_[_tableId];
             //Must have passed the draw time
             require(now > _t.endTime,'Error 2');
             require(_t.status>=1 && _t.status <=3,'Error 3');
          
                 _t.status=5;//Set to cancel
                openTable_[_t.openIndex]=0;
      
                uint256 _fee=0;
                uint256 _systemFee=0;
              if(_t.position[1]>0){
                 uint256 _pos1Pid=_t.position[1];
                _fee=safePercent(gameConfig_.cancelFeePct,_t.betAmount);
                 _systemFee=_fee;
                 player_[_pos1Pid].balance=add(player_[_pos1Pid].balance,sub(_t.betAmount,_fee));
              }
           
              if(_t.position[2]>0){
                 uint256 _pos2Pid=_t.position[2];
                 _fee=safePercent(gameConfig_.cancelFeePct,_t.betAmount);
                 _systemFee=add(_systemFee,_fee);
                 player_[_pos2Pid].balance=add(player_[_pos2Pid].balance,sub(_t.betAmount,_fee));
              }
              
              if(_t.position[3]>0){
                 uint256 _pos3Pid=_t.position[3];
                 uint256 _pos3Amount=div(_t.betAmount,gameConfig_.buyDrawScale);
                 _fee=safePercent(gameConfig_.cancelFeePct,_pos3Amount);
                 _systemFee=add(_systemFee,_fee);
                 player_[_pos3Pid].balance=add(player_[_pos3Pid].balance,sub(_pos3Amount,_fee));
              }
              require(_systemFee>=gameConfig_.pushWei,'Error 4');
              uint256 _adminId=playAddr_[admin__];
              _systemFee=sub(_systemFee,gameConfig_.pushWei);//The admin bears the lottery fee
              player_[_adminId].balance= add(player_[_adminId].balance,_systemFee);
              emit Cancel(_tableId);
    }
    
       function bet(uint256 _tableId,uint8 _position) safe() external payable{
        uint256 _value=msg.value;
        uint256 _valueTemp=_value;
        require(_position >=1 && _position<=3,'Error1');   
        uint256 _tid=_tableId;
        table storage _t=tables_[_tid];
        uint256 _now=now;
        
        //If the location is already or has already won the prize or the number is full, reopen the table. 
        // If there are already 2 people, judge whether the purchase time is exceeded.
         uint256 _pid= getPlayId(msg.sender);
        if(_tid==0 || _tableId>autoTableId_ ||  _t.position[_position] >0  || _t.status >=3 || (_t.status==2 && _now > _t.endTime)){
            //The bid for a draw is 1/3;
            _valueTemp= _position==3?mul(_value,gameConfig_.buyDrawScale):_value;
            require(_valueTemp >=gameConfig_.minBetWei && 
            _valueTemp<=gameConfig_.maxBetWei,'The amount of bet is in the range of 0.06-12 ETH');   
            require(_valueTemp%gameConfig_.minBetWei==0,'The amount of bet is in the range of 0.06-12 ETH');
            autoTableId_++;
            _tid=autoTableId_;
            _t=tables_[_tid];
           
           //The first person to bet determines the ETH of the bet
           _t.betAmount=_valueTemp;
           uint8 openIndex= getOpenTableIndex();
           require(openIndex<200,'Error 8');
           openTable_[openIndex]=_tid;
           _t.openIndex=openIndex;
            
       }else{
        //Only one bet is allowed per table per person
        require(_t.position[1]!=_pid &&  _t.position[2]!=_pid  && _t.position[3]!=_pid,'Error7'); 
              //Buy flat bid validation
              if(_position==3){
                require (_value == div(_t.betAmount,gameConfig_.buyDrawScale),'Error5');
              }else{
                //Buy a winning bid
                require (_value ==_t.betAmount,'Error6');
              }
       }
       _t.status++;
        //A 2-person game starts the countdown.
      if(_t.status==2){
         _t.endTime=add(_now,gameConfig_.countdown);
         
      //Verify that the balance is sufficient for the draw absenteeism lottery
      require(address(this).balance>=gameConfig_.pushWei,'Oraclize query was NOT sent, please add some ETH to cover for the query fee');
      //Countdown Draw
      bytes32 queryId =
        oraclize_query(gameConfig_.countdown, "URL", 
        "html(https://www.random.org/dice/?num=2).xpath(concat((//p/img[@alt>0]/@alt)[1],'|',(//p/img[@alt>0]/@alt)[last()]))",
        CUSTOM_GASLIMIT);
       validQueryId[queryId]=_tid;
     }
        _t.position[_position]=_pid;//Put the user on the seat
        emit Bet(msg.sender,_tid,_value,_position,_t.status,getPosStatus(_tid),_t.endTime);
}
    
    
    function getTableInfo(uint256 _tableId) view external  returns(
        uint256,uint256,uint256,uint256,uint8,uint8,uint8,uint256
        ){
         table storage _t=tables_[_tableId];
          return (
             _t.betAmount,
           _t.position[1],
           _t.position[2],
           _t.position[3],
            _t.status,
            _t.diceA,
            _t.diceB,
           _t.endTime
         );
    }
    

  function getOpenTableIndex() view private returns(uint8){
       for(uint8 i=0;i<openTable_.length;i++){
           if(openTable_[i]==0)
            return i;
       }
       return 201;
   }
   
   //Get a list of available tables
    function getOpenTableList() external view  returns(uint256[200]){
       return openTable_;
   }
     //2020.01.01 Close Game Used to update the game
   function closeGame() external safe() {
        uint256 closeTime=1577808000;
        require(now > closeTime,'Time has not arrived');
        require(msg.sender == admin__,'Error');
        selfdestruct(admin__);
    }
   
    function getPlayId(address addr) private returns(uint256){
        require (address(0)!=addr,'Error Addr');
        if(playAddr_[addr] >0){
         return playAddr_[addr];
        }
              autoPlayId_++;
              playAddr_[addr]=autoPlayId_;
              player memory _p;
              _p.id=autoPlayId_;
              _p.addr=addr;
              _p.timeStamp=now;
              player_[autoPlayId_]=_p;
              return autoPlayId_;
   }

}
