pragma solidity ^0.4.25;

contract FOMOEvents {
    // fired whenever a player registers a name
    event onNewName
    (
        uint256 indexed playerID,
        address indexed playerAddress,
        bytes32 indexed playerName,
        bool isNewPlayer,
        uint256 affiliateID,
        address affiliateAddress,
        bytes32 affiliateName,
        uint256 amountPaid,
        uint256 timeStamp
    );

    // fired at end of buy or reload
    event onEndTx
    (
        uint256 compressedData,
        uint256 compressedIDs,
        bytes32 playerName,
        address playerAddress,
        uint256 ethIn,
        uint256 keysBought,
        address winnerAddr,
        bytes32 winnerName,
        uint256 amountWon,
        uint256 newPot,
        uint256 tokenAmount,
        uint256 genAmount,
        uint256 potAmount,
        uint256 seedAdd
    );

    // fired whenever theres a withdraw
    event onWithdraw
    (
        uint256 indexed playerID,
        address playerAddress,
        bytes32 playerName,
        uint256 ethOut,
        uint256 timeStamp
    );

    // fired whenever a withdraw forces end round to be ran
    event onWithdrawAndDistribute
    (
        address playerAddress,
        bytes32 playerName,
        uint256 ethOut,
        uint256 compressedData,
        uint256 compressedIDs,
        address winnerAddr,
        bytes32 winnerName,
        uint256 amountWon,
        uint256 newPot,
        uint256 tokenAmount,
        uint256 genAmount,
        uint256 seedAdd
    );

    // fired whenever a player tries a buy after round timer
    // hit zero, and causes end round to be ran.
    event onBuyAndDistribute
    (
        address playerAddress,
        bytes32 playerName,
        uint256 ethIn,
        uint256 compressedData,
        uint256 compressedIDs,
        address winnerAddr,
        bytes32 winnerName,
        uint256 amountWon,
        uint256 newPot,
        uint256 tokenAmount,
        uint256 genAmount,
        uint256 seedAdd
    );

    // fired whenever a player tries a reload after round timer
    // hit zero, and causes end round to be ran.
    event onReLoadAndDistribute
    (
        address playerAddress,
        bytes32 playerName,
        uint256 compressedData,
        uint256 compressedIDs,
        address winnerAddr,
        bytes32 winnerName,
        uint256 amountWon,
        uint256 newPot,
        uint256 tokenAmount,
        uint256 genAmount,
        uint256 seedAdd
    );

    // fired whenever an affiliate is paid
    event onAffiliatePayout
    (
        uint256 indexed affiliateID,
        address affiliateAddress,
        bytes32 affiliateName,
        uint256 indexed roundID,
        uint256 indexed buyerID,
        uint256 amount,
        uint256 timeStamp
    );
}

//==============================================================================
//   _ _  _ _|_ _ _  __|_   _ _ _|_    _   .
//  (_(_)| | | | (_|(_ |   _\(/_ | |_||_)  .
//====================================|=========================================

contract FFEIF is FOMOEvents {
    using SafeMath for *;
    using NameFilter for string;
   
    PlayerBookInterface  private PlayerBook;

//==============================================================================
//     _ _  _  |`. _     _ _ |_ | _  _  .
//    (_(_)| |~|~|(_||_|| (_||_)|(/__\  .  (game settings)
//=================_|===========================================================
    PoEIF public PoEIFContract;
    address private admin = msg.sender;
    string constant public name = "Fomo Forever EIF";
    string constant public symbol = "FFEIF";
    uint256 private rndExtra_ = 1 minutes;     // length of the very first ICO
    uint256 public rndGap_ = 1 minutes;        // length of ICO phase, set to 1 year for EOS.
    uint256 public rndInit_ = 60 minutes;      // round timer starts at this
    uint256 public rndInc_ = 1 seconds;        // every full FFEIF purchased adds this much to the timer
    uint256 public rndIncDivisor_ = 1;         // divides the above by this amount (useful for less than 1 second per FFEIF when setting shorter rounds)
	
	uint256 public potSeedRate = 100;		   // pot increase per hour from seedingPot (divided into seedingPot) - default 100 means 1% per hour is added
	uint256 public potNextSeedTime = 0;        // time for the next increase (increases by an hour each time it is processed)		
	uint256 public seedingPot  = 0;            // this keeps track of the amount left to feed the pot - initially has nothing but can be fed by anyone
	uint256 public seedingThreshold = 0 ether; // for the first x ETH (or however it is set) of each round, all pot increases go to the seeding rather than the actual pot
	uint256 public seedingDivisor = 2;         // after the threshold, the pot amount is divided by this and added to the seedingPot i.e. half - a higher divisor means less goes into seeding
	uint256 public seedRoundEnd = 1;           // if nextRoundPercentage will also add to seeding pot as normal or instead fully goes to the pot
	
	uint256 public linearPrice = 75000000000000;            // if non-zero will set a fixed price - otherwise normal fomo scaling which is fairly linear still
	
	uint256 public multPurchase = 0;	      // number of FFEIF needed to purchase for the multiplier to go up.... 0 means it has to be a qualifying purchase of the multiplier amount of FFEIF i.e. enough to make the user the winner if nobody else buys enough afterwards...
	uint256 public multAllowLast = 1;         // Whether the current winner (last qualifying purchaser) can increase the multiplier - this could be used as a tactic to win
	uint256 public multLinear = 2;            // multiplier increases linearly, if not the multiplier is multiplied rather than added to each time a sufficient amount is purchased
	                                          // NOTE: If the value is 2 then it is linear until multStart is reached.....
	
	uint256 public maxMult = 1000000;		  // The maximum value of multiplier (remember it is 10 times the real value);			
	uint256 public multInc_ = 0;              // every purchase (any amount greater than multPurchase) adds this much to the multiplier but divided by 10 - a value of 10 means a real increase of 1 per purchase
											  // NOTE: For non-linear increases, this real value is added to 1 and multiplied - i.e. 10 would mean it doubles each time
											  // NOTE2: If multInc_ is 0, the amount of FFEIF purchased is used as the value (which is still ten times more than the real value)
	uint256 public multIncFactor_ = 10;		  // Further factor to multiply multInc_ by before processing (this is for finer adjustments when multInc_ is set to 0)
	uint256 public multLastChange = now;      // stores timestamp of the last increase/decrease so the next decay can by calculated - note that for non-linear decay, the timestamp only changes to the next minute or multiple of minutes that have passed
	uint256 public multDecayPerMinute = 1;    // every minute the multiplier reduces by this amount (isn't X10 this time so is the true decrease per minute  - i.e. 1)
											  // NOTE: For non-linear decay, a value of 1 means it halves every minute (1 is added to the amount and divided rather than subtracted)
	uint256 public multStart = 24 hours;       // the max time that should be left for the multiplier to increase through more purchases - won't increase when timer is above this
	uint256 public multCurrent = 10;	      // the current multiplier times 10 - defaults to a real value of 1 and this is also the minimum allowed
	
    uint256 public rndMax_ = 24 hours;      // max length a round timer can be
    uint256 public earlyRoundLimit = 1e18;        // limit of 1ETH per player (not per purchase) until earlyRoundLimitUntil
    uint256 public earlyRoundLimitUntil = 100e18; // when limiter ends
    
    uint256 public divPercentage = 65;        // max 75% and can be configured - potPercentage is implied from the rest - 5% each for PoEIF and EIF is fixed
    uint256 public affFee = 5;                // aff fee default is 5% and max is 15%
    uint256 public potPercentage = 20;        // 90 - divPercentage - affFee  (seeding divisor applies to this)
    
    uint256 public divPotPercentage = 15;     // max 50% and can be configured - winnerPercentage is implied from the rest - 5% each for PoEIF and EIF is fixed
    uint256 public nextRoundPercentage = 25;  // max 40% and is the amount carried to the next round (since it is going to the pot, the seeding divisor ratio also applies to this if seedRoundEnd is 1)
    uint256 public winnerPercentage = 50;     // 90 - divPotPercentage - nextRoundPercentatge
    
    uint256 public fundEIF = 0;               // the EasyInvestForever accumulated fund yet to be sent (5% of incoming ETH)
    uint256 public totalEIF = 0;              // total EasyInvestForever fund already sent
    uint256 public seedDonated = 0;           // total sent to seedingPot payable function (doesn't keep track of msg.sender)
    address public FundEIF = 0x0111E8A755a4212E6E1f13e75b1EABa8f837a213; // Usual fund address to send EIF funds too - updateable
    


//==============================================================================
//     _| _ _|_ _    _ _ _|_    _   .
//    (_|(_| | (_|  _\(/_ | |_||_)  .  (data used to store game info that changes)
//=============================|================================================
    uint256 public rID_;    // round id number / total rounds that have happened
//****************
// PLAYER DATA
//****************
    mapping (address => uint256) public pIDxAddr_;          // (addr => pID) returns player id by address
    mapping (bytes32 => uint256) public pIDxName_;          // (name => pID) returns player id by name
    mapping (uint256 => FFEIFDatasets.Player) public plyr_;   // (pID => data) player data
    mapping (uint256 => mapping (uint256 => FFEIFDatasets.PlayerRounds)) public plyrRnds_;    // (pID => rID => data) player round data by player id & round id
    mapping (uint256 => mapping (bytes32 => bool)) public plyrNames_; // (pID => name => bool) list of names a player owns.  (used so you can change your display name amongst any name you own)
//****************
// ROUND DATA
//****************
    mapping (uint256 => FFEIFDatasets.Round) public round_;   // (rID => data) round data
    mapping (uint256 => mapping(uint256 => uint256)) public rndTmEth_;      // (rID => tID => data) eth in per team, by round id and team id
//****************
// TEAM FEE DATA
//****************
    mapping (uint256 => FFEIFDatasets.TeamFee) public fees_;          // (team => fees) fee distribution by team
    mapping (uint256 => FFEIFDatasets.PotSplit) public potSplit_;     // (team => fees) pot split distribution by team
//==============================================================================
//     _ _  _  __|_ _    __|_ _  _  .
//    (_(_)| |_\ | | |_|(_ | (_)|   .  (initial data setup upon contract deploy)
//==============================================================================
    constructor()
        public
    {
        PoEIFContract = PoEIF(0xFfB8ccA6D55762dF595F21E78f21CD8DfeadF1C8);
        PlayerBook = PlayerBookInterface(0xd80e96496cd0B3F95bB4941b1385023fBCa1E6Ba);
        
    }

//==============================================================================
//     _  _ _  _  .
//    | |(/_|/\|  .  (these are for multiplier and new functions)
//==============================================================================
    
function updateFundAddress(address _newAddress)
        onlyAdmin()
        public
    {
        FundEIF = _newAddress;
    }


    /**
     * @dev calculates number of FFEIF received given X eth
     * @param _curEth current amount of eth in contract
     * @param _newEth eth being spent
     * @return amount of ticket purchased
     */
    function keysRec(uint256 _curEth, uint256 _newEth)
        internal
        view
        returns (uint256)
    {
        return(keys((_curEth).add(_newEth)).sub(keys(_curEth)));
    }

    /**
     * @dev calculates amount of eth received if you sold X FFEIF
     * @param _curKeys current amount of FFEIF that exist
     * @param _sellKeys amount of FFEIF you wish to sell
     * @return amount of eth received
     */
    function ethRec(uint256 _curKeys, uint256 _sellKeys)
        internal
        view
        returns (uint256)
    {
        return((eth(_curKeys)).sub(eth(_curKeys.sub(_sellKeys))));
    }

    /**
     * @dev calculates how many FFEIF would exist with given an amount of eth
     * @param _eth eth "in contract"
     * @return number of FFEIF that would exist
     */
    function keys(uint256 _eth)
        internal
        view
        returns(uint256)
    {
        if (linearPrice==0)
        {return ((((((_eth).mul(1000000000000000000)).mul(312500000000000000000000000)).add(5624988281256103515625000000000000000000000000000000000000000000)).sqrt()).sub(74999921875000000000000000000000)) / (156250000);}
        else
        {return 1e18.mul(_eth) / linearPrice;}
    }

    /**
     * @dev calculates how much eth would be in contract given a number of FFEIF
     * @param _keys number of FFEIF "in contract"
     * @return eth that would exists
     */
    function eth(uint256 _keys)
        internal
        view
        returns(uint256)
    {
         if (linearPrice==0)
        {return ((78125000).mul(_keys.sq()).add(((149999843750000).mul(_keys.mul(1000000000000000000))) / (2))) / ((1000000000000000000).sq());}
        else
        {return _keys.mul(linearPrice)/1e18;}
    }


function payFund() public {   //Registration fee goes to EIF - function must be called manually so enough gas is sent
    if(!FundEIF.call.value(fundEIF)()) {
        revert();
    }
    totalEIF = totalEIF.add(fundEIF); fundEIF = 0; 
}


function calcMult(uint256 keysBought, bool validIncrease) internal returns (bool)
{
    uint256 _now = now; //make local variable
	uint256 secondsPassed = _now - multLastChange;
	
	//stores boolean for whether time left has reached the threshold for multiplier to be active - may still be active in special case
	bool thresholdReached = (multStart > round_[rID_].end - _now);
	
	// work out if linear and update the last change time depending on this 
	// multLastChange updates all the time even when multiplier inactive or at minimum and doesn't change
	bool currentlyLinear = false;
	if (multLinear == 1 || (multLinear == 2 && !thresholdReached)) { currentlyLinear = true; multLastChange = _now;}
	else  multLastChange = multLastChange.add((secondsPassed/60).mul(60)); //updates every 60 seconds only
	// includes special case is where it changes from linear to non-linear when multLinear is set to 2 and thresholdReached
	
	//first apply the decay even when not active
	if (multCurrent >= 10) {
	    if (currentlyLinear) multCurrent = (multCurrent.mul(10).sub(multDecayPerMinute.mul(secondsPassed).mul(100)/60))/10; else multCurrent = multCurrent / (1+(multDecayPerMinute.mul(secondsPassed)/60));
		if (multCurrent < 10) multCurrent = 10;
	}
	
	
	// returnValue true if enough bought to be new winner - note that it is set before the increase but after the decay 
	bool returnValue = ((keysBought / 1e17) >= multCurrent);
	
	// now increase the multiplier if active
	if ((thresholdReached || multLinear == 2) && validIncrease) {
	    uint256 wholeKeysBought = keysBought / 1e18;
	    uint256 actualMultInc = multIncFactor_.mul(wholeKeysBought);
	    if (multInc_ != 0) actualMultInc = multInc_;
	    
	    // have enough FFEIF/keys been bought to increase multiplier
	    if ((wholeKeysBought >= multPurchase && multPurchase > 0) || ((wholeKeysBought >= (multCurrent / 10)) && multPurchase == 0) ) {  
	    //now apply increase
	        if (currentlyLinear) multCurrent = multCurrent.add(actualMultInc); else multCurrent = multCurrent.mul((1+(actualMultInc/10)));
	        if (multCurrent > maxMult) multCurrent = maxMult;
	    }
    }
	
	return returnValue;
	
}


function viewMult() public view returns (uint256) // since multiplier only increases when keys are bought, this will only estimate the decay
{
    uint256 _now = now; //make local variable
	uint256 secondsPassed = _now - multLastChange; 
	
	//stores boolean for whether time left has reached the threshold for multiplier to be active - may still be active in special case
	bool thresholdReached = (multStart > round_[rID_].end - _now);
	
	// work out if linear
	bool currentlyLinear = false;
	if (multLinear == 1 || (multLinear == 2 && !thresholdReached)) currentlyLinear = true;
	// includes special case is where it changes from linear to non-linear when multLinear is set to 2 and thresholdReached
	
	//first apply the decay even when not active
	uint256 _multCurrent = multCurrent; //create local 
	if (_multCurrent >= 10) {
	    if (currentlyLinear) _multCurrent = (_multCurrent.mul(10).sub(multDecayPerMinute.mul(secondsPassed).mul(100)/60))/10; else
	        {
	            //make approximation for display reasons 
	            uint256 proportion = secondsPassed % 60;
	            _multCurrent = _multCurrent / (1+(multDecayPerMinute.mul(secondsPassed)/60));
	            uint256 _multCurrent2 = multCurrent / (1+(multDecayPerMinute.mul(secondsPassed+60)/60));
	            _multCurrent = _multCurrent - proportion.mul(_multCurrent - _multCurrent2)/60;
	        }
	}
	
	
    if (_multCurrent < 10) _multCurrent = 10;	
    return _multCurrent;
}

function viewPot() public view returns (uint256) // shows pot in realtime including proportion of seeding pot (so it rises every second)
{
    uint256 _now = now;
    uint256 _pot = round_[rID_].pot;
    uint256 _seedingPot = seedingPot;
    uint256 _potSeedRate = potSeedRate;
    uint256 _potNextSeedTime = potNextSeedTime;
    
    // emulate seeding to also make sure potNextSeedTime is not before _now
    while (_potNextSeedTime<now) {_pot = _pot.add(_seedingPot/_potSeedRate); _seedingPot = _seedingPot.sub(_seedingPot/_potSeedRate); _potNextSeedTime += 3600;}
    
    //time left for hourly real update
    uint256 timeLeft = potNextSeedTime - _now;
    
   // return calculated estimate - the usually negligible sub-hour extra is not actually won at round end (waste of gas to implement) but is for display reasons
   return ((3600-timeLeft).mul(_seedingPot/_potSeedRate)/3600 ).add(_pot);
    
}


uint numElements = 0;
uint256[] varvalue;
string[] varname;

function insert(string _var, uint256 _value) internal  {
    if(numElements == varvalue.length) {
        varvalue.length ++; varname.length ++;
    }
    varvalue[numElements] = _value;
    varname[numElements] = _var;
	numElements++;
}


function setStore(string _variable, uint256 _value) public  {  // we used uint256 for everything here

    // add any new configuration change to array list - admin only and ignore the dummy values sent by endround function
    if (keccak256(bytes(_variable))!=keccak256("endround") && msg.sender == admin) insert(_variable,_value);
    
    // if round has ended, update all variables and reset array index afterwards to effectively clear it
    if (round_[rID_].ended || activated_ == false)  {
       //now loop through all elements 
    	for (uint i=0; i<numElements; i++) {
    	   bytes32 _varname = keccak256(bytes(varname[i])); 
	   if (_varname==keccak256('rndGap_')) rndGap_=varvalue[i]; else 
	   if (_varname==keccak256('rndInit_')) rndInit_=varvalue[i]; else
	   if (_varname==keccak256('rndInc_')) rndInc_=varvalue[i]; else
	   if (_varname==keccak256('rndIncDivisor_')) rndIncDivisor_=varvalue[i]; else
	   if (_varname==keccak256('potSeedRate')) potSeedRate=varvalue[i]; else
	   if (_varname==keccak256('potNextSeedTime')) potNextSeedTime=varvalue[i]; else
	   if (_varname==keccak256('seedingThreshold')) seedingThreshold=varvalue[i]; else
	   if (_varname==keccak256('seedingDivisor')) seedingDivisor=varvalue[i]; else
	   if (_varname==keccak256('seedRoundEnd')) seedRoundEnd=varvalue[i]; else
	   if (_varname==keccak256('linearPrice')) linearPrice=varvalue[i]; else
	   if (_varname==keccak256('multPurchase')) multPurchase=varvalue[i]; else
	   if (_varname==keccak256('multAllowLast')) multAllowLast=varvalue[i]; else
	   if (_varname==keccak256('maxMult')) maxMult=varvalue[i]; else
	   if (_varname==keccak256('multInc_')) multInc_=varvalue[i]; else
	   if (_varname==keccak256('multIncFactor_')) multIncFactor_=varvalue[i]; else
	   if (_varname==keccak256('multLastChange')) multLastChange=varvalue[i]; else
	   if (_varname==keccak256('multDecayPerMinute')) multDecayPerMinute=varvalue[i]; else
	   if (_varname==keccak256('multStart')) multStart=varvalue[i]; else
	   if (_varname==keccak256('multCurrent')) multCurrent=varvalue[i]; else
	   if (_varname==keccak256('rndMax_')) rndMax_=varvalue[i]; else
	   if (_varname==keccak256('earlyRoundLimit')) earlyRoundLimit=varvalue[i]; else
	   if (_varname==keccak256('earlyRoundLimitUntil')) earlyRoundLimitUntil=varvalue[i]; else
	   if (_varname==keccak256('divPercentage')) {divPercentage=varvalue[i]; if (divPercentage>75) divPercentage=75;} else
	   if (_varname==keccak256('divPotPercentage')) {divPotPercentage=varvalue[i]; if (divPotPercentage>50) divPotPercentage=50;} else
	   if (_varname==keccak256('nextRoundPercentage')) {nextRoundPercentage=varvalue[i]; if (nextRoundPercentage>40) nextRoundPercentage=40;} else
	   if (_varname==keccak256('affFee')) {affFee=varvalue[i]; if (affFee>15) affFee=15;}
		}
		//clear elements by resetting index
		numElements = 0;
		// recalculate pot and winner percentages - assume SafeMath not needed due to max values being enforced
		winnerPercentage = 90 - divPotPercentage - nextRoundPercentage;
		potPercentage = 90 - divPercentage - affFee;  
		// reset multiplier to minimum
		multCurrent = 10;
		// finally update legacy arrays
		fees_[0] = FFEIFDatasets.TeamFee(divPercentage,10);   // rest to aff (affFee) and potPercentage
        potSplit_[0] = FFEIFDatasets.PotSplit(divPotPercentage,10);  // also winnerPercentage to winner, nextRoundPercentage to next round
    
	}
}

    
    
//==============================================================================
//     _ _  _  _|. |`. _  _ _  .
//    | | |(_)(_||~|~|(/_| _\  .  (these are safety checks)
//==============================================================================
    /**
     * @dev used to make sure no one can interact with contract until it has
     * been activated.
     */
    modifier isActivated() {
        require(activated_ == true);
        // quick one-line hack to feed pot from seedingPot - adds one hour each time so may loop a few times if contract has been dormant
        while (potNextSeedTime<now)  {round_[rID_].pot = round_[rID_].pot.add(seedingPot/potSeedRate); seedingPot = seedingPot.sub(seedingPot/potSeedRate); potNextSeedTime += 3600; }
        _;
    }

    /**
     * @dev prevents other contracts from interacting with this one
     */
    modifier isHuman() {
        address _addr = msg.sender;
        uint256 _codeLength;
        require (msg.sender == tx.origin);
        assembly {_codeLength := extcodesize(_addr)}
        require(_codeLength == 0);
        
        _;
    }

    /**
     * @dev sets boundaries for incoming tx
     */
    modifier isWithinLimits(uint256 _eth) {
        require(_eth >= 1000000000);
        require(_eth <= 100000000000000000000000);
        _;
    }

 modifier onlyAdmin()
    {
        require(msg.sender == admin);
        _;
    }

//==============================================================================
//     _    |_ |. _   |`    _  __|_. _  _  _  .
//    |_)|_||_)||(_  ~|~|_|| |(_ | |(_)| |_\  .  (use these to interact with contract)
//====|=========================================================================
    /**
     * @dev emergency buy uses last stored affiliate ID
     */
    function()
        isActivated()
        isHuman()
        isWithinLimits(msg.value)
        public
        payable
    {
        // set up our tx event data and determine if player is new or not
        FFEIFDatasets.EventReturns memory _eventData_ = determinePID(_eventData_);

        // fetch player id
        uint256 _pID = pIDxAddr_[msg.sender];

        // buy core
        buyCore(_pID, plyr_[_pID].laff, _eventData_);
    }
    
    
    function seedDeposit()
        isWithinLimits(msg.value)
        public
        payable
    {
        // add to seedingPot
        seedingPot = seedingPot.add(msg.value);
        seedDonated = seedDonated.add(msg.value);
    }

    /**
     * @dev converts all incoming ethereum to FFEIF.
     * -functionhash- 0x8f38f309 (using ID for affiliate)
     * -functionhash- 0x98a0871d (using address for affiliate)
     * -functionhash- 0xa65b37a1 (using name for affiliate)
     * @param _affCode the ID/address/name of the player who gets the affiliate fee
     */
    function buyXid(uint256 _affCode)
        isActivated()
        isHuman()
        isWithinLimits(msg.value)
        public
        payable
    {
        // set up our tx event data and determine if player is new or not
        FFEIFDatasets.EventReturns memory _eventData_ = determinePID(_eventData_);

        // fetch player id
        uint256 _pID = pIDxAddr_[msg.sender];

        // manage affiliate residuals
        // if no affiliate code was given or player tried to use their own, lolz
        if (_affCode == 0 || _affCode == _pID)
        {
            // use last stored affiliate code
            _affCode = plyr_[_pID].laff;

        // if affiliate code was given & its not the same as previously stored
        } else if (_affCode != plyr_[_pID].laff) {
            // update last affiliate
            plyr_[_pID].laff = _affCode;
        }

        // buy core
        buyCore(_pID, _affCode, _eventData_);
    }

    function buyXaddr(address _affCode)
        isActivated()
        isHuman()
        isWithinLimits(msg.value)
        public
        payable
    {
        // set up our tx event data and determine if player is new or not
        FFEIFDatasets.EventReturns memory _eventData_ = determinePID(_eventData_);

        // fetch player id
        uint256 _pID = pIDxAddr_[msg.sender];

        // manage affiliate residuals
        uint256 _affID;
        // if no affiliate code was given or player tried to use their own, lolz
        if (_affCode == address(0) || _affCode == msg.sender)
        {
            // use last stored affiliate code
            _affID = plyr_[_pID].laff;

        // if affiliate code was given
        } else {
            // get affiliate ID from aff Code
            _affID = pIDxAddr_[_affCode];

            // if affID is not the same as previously stored
            if (_affID != plyr_[_pID].laff)
            {
                // update last affiliate
                plyr_[_pID].laff = _affID;
            }
        }
        // buy core
        buyCore(_pID, _affID, _eventData_);
    }

    function buyXname(bytes32 _affCode)
        isActivated()
        isHuman()
        isWithinLimits(msg.value)
        public
        payable
    {
        // set up our tx event data and determine if player is new or not
        FFEIFDatasets.EventReturns memory _eventData_ = determinePID(_eventData_);

        // fetch player id
        uint256 _pID = pIDxAddr_[msg.sender];

        // manage affiliate residuals
        uint256 _affID;
        // if no affiliate code was given or player tried to use their own, lolz
        if (_affCode == '' || _affCode == plyr_[_pID].name)
        {
            // use last stored affiliate code
            _affID = plyr_[_pID].laff;

        // if affiliate code was given
        } else {
            // get affiliate ID from aff Code
            _affID = pIDxName_[_affCode];

            // if affID is not the same as previously stored
            if (_affID != plyr_[_pID].laff)
            {
                // update last affiliate
                plyr_[_pID].laff = _affID;
            }
        }

        // buy core
        buyCore(_pID, _affID, _eventData_);
    }

