//====================/=========================================================
    /** upon contract deploy, it will be deactivated.  this is a one time
     * use function that will activate the contract.  we do this so devs
     * have time to set things up on the web end                            **/
    bool public activated_ = false;
    function activate()
        public
    {
        // only team just can activate
        require(msg.sender == admin, "Only admin can activate");


        // can only be ran once
        require(activated_ == false, "FFEIF already activated");

        // update any configuration changes
        setStore("endround",0);
        
        // activate the contract
        activated_ = true;

        // lets start first round
        rID_ = 1;
            round_[1].strt = now + rndExtra_ - rndGap_;
            round_[1].end = now + rndInit_ + rndExtra_;
            
        //set potNextSeedTime to current block timestamp plus one hour 
        potNextSeedTime = now + 3600;
    }
    
    
    function removeAdmin()   //stops any further updates happening to make it completely autonomous
        public
    {
        require(msg.sender == admin, "Only admin can remove himself");
        admin =  address(0);  // dummy 0x000... address 
    }
    
    
}
 
