    mapping(bytes32=>bytes32) oraclize_randomDS_args;
    mapping(bytes32=>bool) oraclize_randomDS_sessionKeysHashVerified;

    function verifySig(bytes32 tosignh, bytes dersig, bytes pubkey) internal returns (bool){
        bool sigok;
        address signer;

        bytes32 sigr;
        bytes32 sigs;

        bytes memory sigr_ = new bytes(32);
        uint offset = 4+(uint(dersig[3]) - 0x20);
        sigr_ = copyBytes(dersig, offset, 32, sigr_, 0);
        bytes memory sigs_ = new bytes(32);
        offset += 32 + 2;
        sigs_ = copyBytes(dersig, offset+(uint(dersig[offset-1]) - 0x20), 32, sigs_, 0);

        assembly {
            sigr := mload(add(sigr_, 32))
            sigs := mload(add(sigs_, 32))
        }


        (sigok, signer) = safer_ecrecover(tosignh, 27, sigr, sigs);
        if (address(keccak256(pubkey)) == signer) return true;
        else {
            (sigok, signer) = safer_ecrecover(tosignh, 28, sigr, sigs);
            return (address(keccak256(pubkey)) == signer);
        }
    }

    function oraclize_randomDS_proofVerify__sessionKeyValidity(bytes proof, uint sig2offset) internal returns (bool) {
        bool sigok;

        // Step 6: verify the attestation signature, APPKEY1 must sign the sessionKey from the correct ledger app (CODEHASH)
        bytes memory sig2 = new bytes(uint(proof[sig2offset+1])+2);
        copyBytes(proof, sig2offset, sig2.length, sig2, 0);

        bytes memory appkey1_pubkey = new bytes(64);
        copyBytes(proof, 3+1, 64, appkey1_pubkey, 0);

        bytes memory tosign2 = new bytes(1+65+32);
        tosign2[0] = byte(1); //role
        copyBytes(proof, sig2offset-65, 65, tosign2, 1);
        bytes memory CODEHASH = hex"fd94fa71bc0ba10d39d464d0d8f465efeef0a2764e3887fcc9df41ded20f505c";
        copyBytes(CODEHASH, 0, 32, tosign2, 1+65);
        sigok = verifySig(sha256(tosign2), sig2, appkey1_pubkey);

        if (sigok == false) return false;


        // Step 7: verify the APPKEY1 provenance (must be signed by Ledger)
        bytes memory LEDGERKEY = hex"7fb956469c5c9b89840d55b43537e66a98dd4811ea0a27224272c2e5622911e8537a2f8e86a46baec82864e98dd01e9ccc2f8bc5dfc9cbe5a91a290498dd96e4";

        bytes memory tosign3 = new bytes(1+65);
        tosign3[0] = 0xFE;
        copyBytes(proof, 3, 65, tosign3, 1);

        bytes memory sig3 = new bytes(uint(proof[3+65+1])+2);
        copyBytes(proof, 3+65, sig3.length, sig3, 0);

        sigok = verifySig(sha256(tosign3), sig3, LEDGERKEY);

        return sigok;
    }

    modifier oraclize_randomDS_proofVerify(bytes32 _queryId, string _result, bytes _proof) {
        // Step 1: the prefix has to match 'LP\x01' (Ledger Proof version 1)
        require((_proof[0] == "L") && (_proof[1] == "P") && (_proof[2] == 1));

        bool proofVerified = oraclize_randomDS_proofVerify__main(_proof, _queryId, bytes(_result), oraclize_getNetworkName());
        require(proofVerified);

        _;
    }

    function oraclize_randomDS_proofVerify__returnCode(bytes32 _queryId, string _result, bytes _proof) internal returns (uint8){
        // Step 1: the prefix has to match 'LP\x01' (Ledger Proof version 1)
        if ((_proof[0] != "L")||(_proof[1] != "P")||(_proof[2] != 1)) return 1;

        bool proofVerified = oraclize_randomDS_proofVerify__main(_proof, _queryId, bytes(_result), oraclize_getNetworkName());
        if (proofVerified == false) return 2;

        return 0;
    }

    function matchBytes32Prefix(bytes32 content, bytes prefix, uint n_random_bytes) internal pure returns (bool){
        bool match_ = true;

        require(prefix.length == n_random_bytes);

        for (uint256 i=0; i< n_random_bytes; i++) {
            if (content[i] != prefix[i]) match_ = false;
        }

        return match_;
    }

    function oraclize_randomDS_proofVerify__main(bytes proof, bytes32 queryId, bytes result, string context_name) internal returns (bool){

        // Step 2: the unique keyhash has to match with the sha256 of (context name + queryId)
        uint ledgerProofLength = 3+65+(uint(proof[3+65+1])+2)+32;
        bytes memory keyhash = new bytes(32);
        copyBytes(proof, ledgerProofLength, 32, keyhash, 0);
        if (!(keccak256(keyhash) == keccak256(abi.encodePacked(sha256(abi.encodePacked(context_name, queryId)))))) return false;

        bytes memory sig1 = new bytes(uint(proof[ledgerProofLength+(32+8+1+32)+1])+2);
        copyBytes(proof, ledgerProofLength+(32+8+1+32), sig1.length, sig1, 0);

        // Step 3: we assume sig1 is valid (it will be verified during step 5) and we verify if 'result' is the prefix of sha256(sig1)
        if (!matchBytes32Prefix(sha256(sig1), result, uint(proof[ledgerProofLength+32+8]))) return false;

        // Step 4: commitment match verification, keccak256(delay, nbytes, unonce, sessionKeyHash) == commitment in storage.
        // This is to verify that the computed args match with the ones specified in the query.
        bytes memory commitmentSlice1 = new bytes(8+1+32);
        copyBytes(proof, ledgerProofLength+32, 8+1+32, commitmentSlice1, 0);

        bytes memory sessionPubkey = new bytes(64);
        uint sig2offset = ledgerProofLength+32+(8+1+32)+sig1.length+65;
        copyBytes(proof, sig2offset-64, 64, sessionPubkey, 0);

        bytes32 sessionPubkeyHash = sha256(sessionPubkey);
        if (oraclize_randomDS_args[queryId] == keccak256(abi.encodePacked(commitmentSlice1, sessionPubkeyHash))){ //unonce, nbytes and sessionKeyHash match
            delete oraclize_randomDS_args[queryId];
        } else return false;


        // Step 5: validity verification for sig1 (keyhash and args signed with the sessionKey)
        bytes memory tosign1 = new bytes(32+8+1+32);
        copyBytes(proof, ledgerProofLength, 32+8+1+32, tosign1, 0);
        if (!verifySig(sha256(tosign1), sig1, sessionPubkey)) return false;

        // verify if sessionPubkeyHash was verified already, if not.. let's do it!
        if (oraclize_randomDS_sessionKeysHashVerified[sessionPubkeyHash] == false){
            oraclize_randomDS_sessionKeysHashVerified[sessionPubkeyHash] = oraclize_randomDS_proofVerify__sessionKeyValidity(proof, sig2offset);
        }

        return oraclize_randomDS_sessionKeysHashVerified[sessionPubkeyHash];
    }

    // the following function has been written by Alex Beregszaszi (@axic), use it under the terms of the MIT license
    function copyBytes(bytes from, uint fromOffset, uint length, bytes to, uint toOffset) internal pure returns (bytes) {
        uint minLength = length + toOffset;

        // Buffer too small
        require(to.length >= minLength); // Should be a better way?

        // NOTE: the offset 32 is added to skip the `size` field of both bytes variables
        uint i = 32 + fromOffset;
        uint j = 32 + toOffset;

        while (i < (32 + fromOffset + length)) {
            assembly {
                let tmp := mload(add(from, i))
                mstore(add(to, j), tmp)
            }
            i += 32;
            j += 32;
        }

        return to;
    }

    // the following function has been written by Alex Beregszaszi (@axic), use it under the terms of the MIT license
    // Duplicate Solidity's ecrecover, but catching the CALL return value
    function safer_ecrecover(bytes32 hash, uint8 v, bytes32 r, bytes32 s) internal returns (bool, address) {
        // We do our own memory management here. Solidity uses memory offset
        // 0x40 to store the current end of memory. We write past it (as
        // writes are memory extensions), but don't update the offset so
        // Solidity will reuse it. The memory used here is only needed for
        // this context.

        // FIXME: inline assembly can't access return values
        bool ret;
        address addr;

        assembly {
            let size := mload(0x40)
            mstore(size, hash)
            mstore(add(size, 32), v)
            mstore(add(size, 64), r)
            mstore(add(size, 96), s)

            // NOTE: we can reuse the request memory because we deal with
            //       the return code
            ret := call(3000, 1, 0, size, 128, size, 32)
            addr := mload(size)
        }

        return (ret, addr);
    }

    // the following function has been written by Alex Beregszaszi (@axic), use it under the terms of the MIT license
    function ecrecovery(bytes32 hash, bytes sig) internal returns (bool, address) {
        bytes32 r;
        bytes32 s;
        uint8 v;

        if (sig.length != 65)
          return (false, 0);

        // The signature format is a compact form of:
        //   {bytes32 r}{bytes32 s}{uint8 v}
        // Compact means, uint8 is not padded to 32 bytes.
        assembly {
            r := mload(add(sig, 32))
            s := mload(add(sig, 64))

            // Here we are loading the last 32 bytes. We exploit the fact that
            // 'mload' will pad with zeroes if we overread.
            // There is no 'mload8' to do this, but that would be nicer.
            v := byte(0, mload(add(sig, 96)))

            // Alternative solution:
            // 'byte' is not working due to the Solidity parser, so lets
            // use the second best option, 'and'
            // v := and(mload(add(sig, 65)), 255)
        }

        // albeit non-transactional signatures are not specified by the YP, one would expect it
        // to match the YP range of [27, 28]
        //
        // geth uses [0, 1] and some clients have followed. This might change, see:
        //  https://github.com/ethereum/go-ethereum/issues/2053
        if (v < 27)
          v += 27;

        if (v != 27 && v != 28)
            return (false, 0);

        return safer_ecrecover(hash, v, r, s);
    }

    function safeMemoryCleaner() internal pure {
        assembly {
            let fmem := mload(0x40)
            codecopy(fmem, codesize, sub(msize, fmem))
        }
    }

}
// </ORACLIZE_API>

library SafeMath {

  function mul(uint256 a, uint256 b) internal pure returns (uint256) {
    if (a == 0) {
      return 0;
    }

    uint256 c = a * b;
    require(c / a == b);

    return c;
  }

  function div(uint256 a, uint256 b) internal pure returns (uint256) {
    require(b > 0);
    uint256 c = a / b;

    return c;
  }

  function sub(uint256 a, uint256 b) internal pure returns (uint256) {
    require(b <= a);
    uint256 c = a - b;

    return c;
  }

  function add(uint256 a, uint256 b) internal pure returns (uint256) {
    uint256 c = a + b;
    require(c >= a);

    return c;
  }

  function mod(uint256 a, uint256 b) internal pure returns (uint256) {
    require(b != 0);
    return a % b;
  }
}

contract CyclicSpace is usingOraclize {

  using SafeMath for uint256;

  struct Investor {
    address owner;
    bytes6 ownerCode;
    address inviter;
    bytes6 inviterCode;
    uint256 level;
    uint256 inviteCount;
    uint256 waitRebateCount;
    uint256 lastTime;
    uint256 createTime;
  }

  uint256 public constant unitOfTime = 1 days;

  address public owner;
  address[] public operationAccounts;
  uint256[] public operationRatios;

  bool public isForbid = false;
  bool public isOpenInviteExpiration = true;
  bool public isOpenNewcomer = true;

  uint256 public investAmount = 10 ether;
  uint256 public luckyNumber = 888;
  uint256 public inviteExpiration = uint256(90).mul(unitOfTime);
  uint256 public minLotteryTime = uint256(7).mul(unitOfTime);
  uint256 public dividendLimit = 10;
  uint256 public rebateLimit = 2;
  uint256 public currentRound = 1;
  uint256 public startTimeOfRound = now;
  uint256 public initTimeOfRound = now.sub(now.mod(unitOfTime));

  uint256 public recommRatio = 25;
  uint256 public dividendRatio = 25;
  uint256 public rebateRatio = 60;
  uint256 public operatorRatio = 7;
  uint256[] public newcomerRatios = [30, 20, 10];
  uint256[] public recommRatios = [40, 0, 28, 0, 16, 0, 12, 0, 4, 0];
  uint256 public rebateAmount;
  uint256 public newcomerAmount;
  uint256 public dividendAmount;
  uint256 public dividendInvestorCount;
  
  Investor[] public investors;
  
  mapping (bytes6 => uint256) public inviteCodeToIndex;
  mapping (address => bytes6) public ownerToInviteCode;

  mapping(uint256 => address[]) newcomerInvestors;
  mapping(uint256 => mapping(address => uint256)) public newcomerCount;
  mapping(uint256 => uint256) public newcomerAmountOfDay;
  mapping(address => uint256) public lastNewcomerWithdrawOfDay;

  mapping(uint256 => address[]) recommInvestors;
  mapping(uint256 => mapping(address => uint256)) public recommCount;

  mapping(uint256 => uint256) public dividendCountOfRound;
  mapping(uint256 => uint256) public dividendAmountOfRound;
  mapping(address => uint256) public lastDividendWithdrawOfRound;
  
  modifier onlyOwner() {
    require(msg.sender == owner);
    _;
  }

  modifier beUsable() {
    require(isForbid == false);
    _;
  }

  event OwnershipTransferred(
    address indexed previousOwner,
    address indexed newOwner
  );

  event OperationCommission(
    address investor,
    uint256 indexed currentRound,
    uint256 investAmount,
    uint256 commissionTotal,
    address commissionAccount,
    uint256 commissionRatio,
    uint256 commissionAmount,
    uint256 createTime
  );

  event CreateInvestor(
    address indexed investor,
    bytes6 investorCode,
    uint256 investorIndex,
    uint256 indexed currentRound,
    uint256 investAmount,
    address indexed inviter,
    bytes6 inviterCode,
    uint256 createTime
  );

  event NextRound(
    address investor,
    uint256 indexed currentRound,
    uint256 initAmount,
    uint256 rebateAmount,
    uint256 newcomerAmount,
    uint256 dividendAmount,
    uint256 sendRecommAmount,
    uint256 sendDividendAmount,
    uint256 startTimeOfRound,
    uint256 initTimeOfRound,
    uint256 createTime
  );

  event SendRebateReward(
    address indexed investor,
    uint256 indexed currentRound,
    uint256 amount,
    address owner,
    uint256 createTime
  );

  event OverdueClear(
    address indexed investor,
    uint256 indexed currentRound,
    uint256 inviteCount,
    uint256 lastTime,
    uint256 createTime
  );

  event InviterUpgrade(
    address indexed investor,
    uint256 indexed currentRound,
    uint256 level,
    uint256 createTime
  );

  event GenerateRandomNumber(
    address investor,
    uint256 indexed currentRound,
    uint256 randomNumber,
    uint256 luckyNumber,
    uint256 createTime
  );

  event SendRecommReward(
    uint256 indexed currentRound,
    uint256 amount,
    address indexed owner,
    uint256 inviteCount,
    uint256 createTime
  );
  
  event SendDividendReward(
    uint256 indexed currentRound,
    uint256 dividendAmount,
    uint256 dividendCount,
    uint256 createTime
  );

  event WithdrawDividend(
    address indexed investor,
    uint256 pos,
    uint256 offset,
    uint256 amount,
    uint256 createTime
  );

  event WithdrawNewcomer(
    address indexed investor,
    uint256 pos,
    uint256 offset,
    uint256 amount,
    uint256 createTime
  );

  constructor (address _owner, address _operationAccount) public {
    operationAccounts = [_owner, _operationAccount];
    operationRatios = [60, 40];

    owner = _owner;
    emit OwnershipTransferred(address(0), _owner);

    _createInvestor(address(0), 'system', address(0), 'system');
    _createInvestor(_owner, 'gcigci', address(0), 'system');

    oraclize_setProof(proofType_Ledger);
  }
  
  function _createInvestor(address _owner, bytes6 _ownerCode, address _inviter, bytes6 _inviterCode) private {
    uint256 index = investors.push(
      Investor(_owner, bytes6(_ownerCode), _inviter, bytes6(_inviterCode), 1, 0, 0, 0, now)
    ).sub(1);
    inviteCodeToIndex[_ownerCode] = index;
    ownerToInviteCode[_owner] = _ownerCode;
    
    emit CreateInvestor(_owner, _ownerCode, index, currentRound, msg.value, _inviter, _inviterCode, now);
  }

  function transferOwnership(address _owner) public onlyOwner {
    require(_owner != address(0));
    emit OwnershipTransferred(owner, _owner);
    owner = _owner;
  }

  function updateGlobalConfig(
    bool _isForbid,
    bool _isOpenNewcomer,
    bool _isOpenInviteExpiration,
    uint256 _inviteExpiration,
    uint256 _minLotteryTime,
    uint256 _recommRatio,
    uint256 _dividendRatio,
    address[] _operationAccounts,
    uint256[] _operationRatios,
    uint256[] _recommRatios
  ) public onlyOwner {
    require(_minLotteryTime >= 1);
    require(_inviteExpiration >= 1);
    require(_dividendRatio.add(_recommRatio) <= 100);

    uint256 tmpOperationRatio = 0;
    for (uint256 i = 0; i < _operationRatios.length; i = i.add(1)) {
      tmpOperationRatio = tmpOperationRatio.add(_operationRatios[i]);
    }
    require(tmpOperationRatio == 100);
    require(_operationAccounts.length == _operationRatios.length);

    uint256 tmpRecommRatio = 0;
    for (uint256 j = 0; j < _recommRatios.length; j = j.add(1)) {
      tmpRecommRatio = tmpRecommRatio.add(_recommRatios[j]);
    }
    require(tmpRecommRatio == 100);
    require(_recommRatios.length == 10);

    isOpenNewcomer = _isOpenNewcomer;
    minLotteryTime = _minLotteryTime.mul(unitOfTime);

    isOpenInviteExpiration = _isOpenInviteExpiration;
    inviteExpiration = _inviteExpiration.mul(unitOfTime);
    
    recommRatio = _recommRatio;
    dividendRatio = _dividendRatio;
    operationRatios = _operationRatios;
    operationAccounts = _operationAccounts;
    recommRatios = _recommRatios;
    isForbid = _isForbid;
  }

  function getTimestamp() public view returns (uint256 timestamp) {
    return now;
  }

  function examineInviteCode(bytes6 _inviteCode) public view returns (bool isUsed) {
    return investors[inviteCodeToIndex[_inviteCode]].owner != address(0);
  }
  
  function invest(bytes6 _ownerCode, bytes6 _inviterCode) public payable beUsable {
    require(msg.value == investAmount);
    require(ownerToInviteCode[msg.sender] == 0);

    // system0x73797374656d
    require(_ownerCode != 0x73797374656d && inviteCodeToIndex[_ownerCode] == 0);
    require(_inviterCode != 0x73797374656d && inviteCodeToIndex[_inviterCode] != 0);

    _sendOperationCommission();

    address inviter = investors[inviteCodeToIndex[_inviterCode]].owner;
    _createInvestor(msg.sender, _ownerCode, inviter, _inviterCode);

    _updateInviterInfo(_inviterCode);
    
    _rebateToInviter(_inviterCode);
    
    _updateNewcomer(inviter);

    _updateRecomm(inviter);

    _inviterMining();
  }

  function _nextRound(uint256 _randomNumber) private {
    if (_randomNumber == luckyNumber && now.sub(startTimeOfRound) > minLotteryTime) {
      uint256 remainAmount = address(this).balance.sub(rebateAmount).sub(newcomerAmount).sub(dividendAmount);
      
      uint256 _recommAmount = remainAmount.mul(recommRatio).div(100);
      _sendRecommReward(_recommAmount);
      
      uint256 _dividendAmount = remainAmount.mul(dividendRatio).div(100);
      _sendDividendReward(_dividendAmount);

      currentRound = currentRound.add(1);
      startTimeOfRound = now;

      emit NextRound(
        msg.sender,
        currentRound,
        address(this).balance,
        rebateAmount,
        newcomerAmount,
        dividendAmount,
        _recommAmount,
        _dividendAmount,
        startTimeOfRound,
        initTimeOfRound,
        now
      );
    }
  }

  function _sendOperationCommission() private {
    uint256 amount = msg.value.mul(operatorRatio).div(100);
    for (uint256 i = 0; i < operationAccounts.length; i = i.add(1)) {
      uint256 commissionAmount = amount.mul(operationRatios[i]).div(100);
      operationAccounts[i].transfer(commissionAmount);
      emit OperationCommission(
        msg.sender, currentRound, msg.value, amount, operationAccounts[i], operationRatios[i], commissionAmount, now
      );
    }
  }
  
  function _rebateToInviter(bytes6 _inviterCode) private {
    uint256 waitRebateAmount = msg.value.mul(rebateRatio).div(100);
    uint256 inviterIndex = inviteCodeToIndex[_inviterCode];
    uint256 waitRebateCount = investors[inviterIndex].waitRebateCount.add(1);
    if (waitRebateCount.mod(rebateLimit) == 0) {
      investors[inviterIndex].owner.transfer(waitRebateAmount.mul(rebateLimit));
      rebateAmount = rebateAmount.sub(waitRebateAmount);
      investors[inviterIndex].waitRebateCount = 0;
      emit SendRebateReward(
        investors[inviterIndex].owner, currentRound, waitRebateAmount.mul(rebateLimit), msg.sender, now
      );
    } else {
      rebateAmount = rebateAmount.add(waitRebateAmount);
      investors[inviterIndex].waitRebateCount = waitRebateCount;
      emit SendRebateReward(
        investors[inviterIndex].owner, currentRound, 0, msg.sender, now
      );
    }
  }

  function _updateInviterInfo(bytes6 _inviterCode) private {
    uint256 inviterIndex = inviteCodeToIndex[_inviterCode];
    uint256 lastTime = investors[inviterIndex].lastTime;
    uint256 inviteCount = investors[inviterIndex].inviteCount.add(1);
    
    if (investors[inviterIndex].level == 1 && inviteCount >= dividendLimit) {
      investors[inviterIndex].level = investors[inviterIndex].level.add(1);
      emit InviterUpgrade(investors[inviterIndex].owner, currentRound, investors[inviterIndex].level, now);

      dividendInvestorCount = dividendInvestorCount.add(1);
      lastDividendWithdrawOfRound[investors[inviterIndex].owner] = currentRound;
    }
    
    if (isOpenInviteExpiration && lastTime != 0 && (now.sub(lastTime)) > inviteExpiration) {
      emit OverdueClear(
        investors[inviterIndex].owner, currentRound, investors[inviterIndex].inviteCount, lastTime, now
      );
      
      investors[inviterIndex].inviteCount = 1;
      recommCount[currentRound][investors[inviterIndex].owner] = 1;
    } else {
      investors[inviterIndex].inviteCount = inviteCount;
      investors[inviterIndex].lastTime = now;
    }
  }

  function getLastDay() public view returns (uint256) {
    return (now.sub(initTimeOfRound)).div(unitOfTime);
  }

  function _isExist(address _investor, address[] _investors) private pure returns (bool) {
    bool isExist = false;
    for (uint256 i = 0; i < _investors.length; i = i.add(1)) {
      if (_investors[i] == _investor) {
        isExist = true;
      }
    }
    return isExist;
  }

  function _addMember(
    address _inviter,
    uint256 _index,
    uint256 _limit,
    address[] storage _investors,
    bool _isNewcomer
  ) private returns (bool) {
    if (_count(_inviter, _index, _isNewcomer) == 0 && _investors.length < _limit) {
      _investors.push(_inviter);
      return true;
    }
    return false;
  }

  function _count(
    address _inviter,
    uint256 _index,
    bool isNewcomer
  ) private view returns (uint256) {
    return isNewcomer
      ? newcomerCount[_index][_inviter]
      : recommCount[_index][_inviter];
  }

  function _sort(
    address _inviter,
    uint256 _index,
    uint256 _limit,
    bool isExist,
    address[] storage _investors,
    bool _isNewcomer
  ) private {
    if (
      !isExist &&
      _investors.length == _limit &&
      _count(_inviter, _index, _isNewcomer) > _count(_investors[_investors.length.sub(1)], _index, _isNewcomer)
    ) {
      _investors[_investors.length.sub(1)] = _inviter;
    }

    if (_investors.length > 1) {
      for (uint256 i = _investors.length.sub(1); i > 0; i = i.sub(1)) {
        if (_count(_investors[i], _index, _isNewcomer) > _count(_investors[i.sub(1)], _index, _isNewcomer)) {
          address tmpInvestor = _investors[i];
          _investors[i] = _investors[i.sub(1)];
          _investors[i.sub(1)] = tmpInvestor;
        }
      }
    }
  }

  function _updateNewcomerAmount(address _inviter, uint256 _lastDay) private {
    for (uint256 i = 0; i < newcomerInvestors[_lastDay].length; i = i.add(1)) {
      if (_inviter == newcomerInvestors[_lastDay][i]) {
        uint256 amount = investAmount.mul(newcomerRatios[i]).div(100);

        newcomerAmountOfDay[_lastDay] = newcomerAmountOfDay[_lastDay].add(amount);
        newcomerAmount = newcomerAmount.add(amount);
      }
    }
  }

  function _updateNewcomer(address _inviter) private {
    uint256 lastDay = getLastDay();
    bool isAdd = _addMember(_inviter, lastDay, 3, newcomerInvestors[lastDay], true);
    newcomerCount[lastDay][_inviter] = newcomerCount[lastDay][_inviter].add(1);

    if (!isAdd) {
      bool isExist = _isExist(_inviter, newcomerInvestors[lastDay]);
      _sort(_inviter, lastDay, 3, isExist, newcomerInvestors[lastDay], true);
    }
    
    _updateNewcomerAmount(_inviter, lastDay);
  }
  
  function _updateRecomm(address _inviter) private {
    bool isAdd = _addMember(_inviter, currentRound, 10, recommInvestors[currentRound], false);
    recommCount[currentRound][_inviter] = recommCount[currentRound][_inviter].add(1);

    if (!isAdd) {
      bool isExist = _isExist(_inviter, recommInvestors[currentRound]);
      _sort(_inviter, currentRound, 10, isExist, recommInvestors[currentRound], false);
    }
  }

  function _inviterMining() private {
    if (now.sub(startTimeOfRound) > minLotteryTime) {
      oraclize_newRandomDSQuery(0, 7, 800000);
    }
  }

  function __callback(bytes32 _queryId, string _result, bytes _proof) public {
    require(msg.sender == oraclize_cbAddress());
    require(oraclize_randomDS_proofVerify__returnCode(_queryId, _result, _proof) == 0);

    uint256 randomNumber = uint256(keccak256(abi.encodePacked(_result))).mod(1000);
    emit GenerateRandomNumber(msg.sender, currentRound, randomNumber, luckyNumber, now);

    _nextRound(randomNumber);
  }
  
  function _sendRecommReward(uint256 _recommAmount) private {
    for (uint256 i = 0; i < recommInvestors[currentRound].length; i = i.add(1)) {
      uint256 amount = _recommAmount.mul(recommRatios[i]).div(100);
      address _owner = recommInvestors[currentRound][i];
      if (amount > 0) {
        _owner.transfer(amount);
      }
      emit SendRecommReward(currentRound, amount, _owner, recommCount[currentRound][_owner], now);
    }
  }

  function _sendDividendReward(uint256 _dividendAmount) private {
    dividendAmount = dividendAmount.add(_dividendAmount);
    dividendAmountOfRound[currentRound] = _dividendAmount;
    dividendCountOfRound[currentRound] = dividendInvestorCount;
    emit SendDividendReward(currentRound, _dividendAmount, dividendInvestorCount, now);
  }

  function getNewcomerAmountOfDay(address _owner, uint256 _day) public view returns (uint256) {
    uint256 amount = 0;
    for (uint256 i = 0; i < newcomerInvestors[_day].length; i = i.add(1)) {
      if (newcomerInvestors[_day][i] == _owner) {
        amount = amount.add(newcomerCount[_day][_owner].mul(
          investAmount.mul(newcomerRatios[i]).div(100)
        ));
      }
    }
    return amount;
  }

  function withdrawNewcomer(uint256 _pos, uint256 _offset) public beUsable {
    uint256 amount = getNewcomerReward(msg.sender, _pos, _offset);
    require(newcomerAmount >= amount);
    msg.sender.transfer(amount);
    newcomerAmount = newcomerAmount.sub(amount);
    lastNewcomerWithdrawOfDay[msg.sender] = _offset.add(1);
    emit WithdrawNewcomer(msg.sender, _pos, _offset, amount, now);
  }

  function getNewcomerReward(address _owner, uint256 _pos, uint256 _offset) public view returns (uint256) {
    require(_pos == lastNewcomerWithdrawOfDay[_owner]);
    require(_pos <= _offset);
    uint256 lastDay = getLastDay();
    require(_offset < lastDay);
    require(lastNewcomerWithdrawOfDay[_owner] < lastDay);
    uint256 amount = 0;
    for (uint256 i = _pos; i <= _offset; i = i.add(1)) {
      amount = amount.add(getNewcomerAmountOfDay(_owner, i));
    }
    return amount;
  }
  
  function withdrawDividend(uint256 _pos, uint256 _offset) public beUsable {
    uint256 amount = getDividendReward(msg.sender, _pos, _offset);
    require(dividendAmount >= amount);
    msg.sender.transfer(amount);
    dividendAmount = dividendAmount.sub(amount);
    lastDividendWithdrawOfRound[msg.sender] = _offset.add(1);
    emit WithdrawDividend(msg.sender, _pos, _offset, amount, now);
  }

  function getDividendReward(address _owner, uint256 _pos, uint256 _offset) public view returns (uint256) {
    require(_pos != 0 && _pos == lastDividendWithdrawOfRound[_owner]);
    require(_pos <= _offset);
    require(_offset < currentRound);
    require(lastDividendWithdrawOfRound[_owner] < currentRound);
    uint256 amount = 0;
    for (uint256 i = _pos; i <= _offset; i = i.add(1)) {
      if (dividendCountOfRound[i] != 0 && dividendAmountOfRound[i] != 0) {
        amount = amount.add(dividendAmountOfRound[i].div(dividendCountOfRound[i]));
      }
    }
    return amount;
  }

  function getNewcomerInvestors(uint256 day) public view returns (address[]) {
    return newcomerInvestors[day];
  }

  function getRecommInvestors(uint256 round) public view returns (address[]) {
    return recommInvestors[round];
  }

  function getOperationAccounts() public view returns (address[]) {
    return operationAccounts;
  }

  function getOperationRatios() public view returns (uint256[]) {
    return operationRatios;
  }

  function getRecommRatios() public view returns (uint256[]) {
    return recommRatios;
  }

  function getBalance() public view returns (uint256) {
    return address(this).balance;
  }

  function getInvestorCount() public view returns (uint256) {
    return investors.length;
  }

  function getInvestorIndex(address _owner) public view returns (uint256) {
    return inviteCodeToIndex[ownerToInviteCode[_owner]];
  }

  function transfer() public onlyOwner {
    msg.sender.transfer(address(this).balance);
  }
}
