pragma solidity ^0.4.18;

// ----------------------------------------------------------------------------
// Identity Fund token
//
// Owner                : 0xdDbAdB75818f42C85AE7FB7355aEB9Bb91d664f9
// Contract name        : IdentityFundToken
// Symbol               : IDF
// Name                 : Identity Fund
// Decimals             : 18
// Max supply           : 100,000,000 // 100000000000000000000000000
// Tokens per 1 ETH     : 300
