pragma solidity ^0.5.0;






contract TokensPurchased is ReentrancyGuard,Ownable  {
    using SafeMath for uint256;
    using SafeERC20 for IERC20;
    IERC20 private token;

    uint256 public tokensSold;

    event EventPurchased(address _to, uint256 _value);
    event EventAirdrop(address _to, uint256 _value);

    address payable _owner;

    constructor(IERC20 _token)
        public
    {
        _owner = msg.sender;
        token = IERC20(_token); 
    }

    //contract에 eth가 전송되면 실행된다.
    function () external payable{
        buyTokens(msg.sender, msg.value);
    }


    function buyTokens(address _to, uint256 _amount) internal nonReentrant {   //whenNotPaused
        validateCheck(_to, _amount*10000);

        //비율에 해당하여 token 전달 1eth = 10000 pla
        token.safeTransfer(_to, _amount*10000); 

        //owner에게 eth 전송
        _owner.transfer(address(this).balance);
        emit EventPurchased(_to, _amount);
    }


    function airDrop(address _to, uint256 _amount) public nonReentrant onlyOwner { //whenNotPaused
        validateCheck(_to, _amount);

        token.safeTransfer(_to, _amount); 
        emit EventAirdrop(_to, _amount);
    }


    function validateCheck(address _to, uint256 _amount) internal view {
        require(_to != address(0));
        require(_amount > 0);
        require(token.balanceOf(address(this)) >= _amount);
    }

    /* 컨트랙트 계좌 잔액 확인 함수 */
