contract EcommerceStore {
    // --------------------------------- Events
    event NewProduct(uint _productId, string _name, string _verification, string _descLink, uint _userId, string _gallery);

    // --------------------------------- Contract variables
    uint public productIndex;

    /// Mapping configs
    mapping (address => mapping(uint => Product)) stores;
    mapping (uint => address) productIdInStore;

    // Payout data
    address private _token;
    address private _wallet;
    uint256 private _price;

    /// Product definition
    enum ProductStatus {Open, Sold, Unsold}
    struct Product {
        uint id;
        string name;
        string verification;
        string descLink;
        ProductStatus status;
        uint userId;
        string gallery;
    }

    // --------------------------------- Ctor
    constructor(address token, address wallet, uint256 price) 
        public
    {
        require (price >= 0, "Invalid post price");
        require (wallet != address(0), "Invalit wallet");

        _price = price;
        _token = token;
        _wallet = wallet;

        productIndex = 0;
    }


    // --------------------------------------------------------
    // --------------------------------- Attributes
    // --------------------------------------------------------
    /**
        @dev Returns the post item price.
     */
    function postPrice()
        public view returns (uint256)
    {
        return _price;
    }

    /**
        @dev Get item data base on id.
        * @param _productId Id of searched product
        * @return Data for given product
     */
    function getProduct(uint _productId) public view 
        returns (uint, string memory, string memory, string memory, ProductStatus, uint, string memory, address) 
    {
        address productAddress = productIdInStore[_productId];
        Product memory product = stores[productAddress][_productId];
        return (
            product.id, 
            product.name, 
            product.verification, 
            product.descLink, 
            product.status, 
            product.userId, 
            product.gallery, 
            productAddress
        );
    }

    // --------------------------------------------------------
    // --------------------------------- Methods
    // --------------------------------------------------------
    /**
     * @dev Add product to store.
       * @return event 
    */
    function addProductToStore(
        string memory _name, 
        string memory _verification, 
        string memory _descLink, 
        uint _userId, 
        string memory _gallery)
    public payable {
        /// Transfer fee from user to wallet
        require(CBToken(_token).transferFrom(msg.sender, address(_wallet), _price), "Transaction failed");

        // Add product
        productIndex += 1;
        Product memory product = Product(
            productIndex, 
            _name, 
            _verification, 
            _descLink, 
            ProductStatus.Open, 
            _userId,
            _gallery
        );

        stores[msg.sender][productIndex] = product;
        productIdInStore[productIndex] = msg.sender;

        emit NewProduct(productIndex, _name, _verification, _descLink, _userId, _gallery);
    }
}
