contract LGX is owned, ERC20_token {

    uint256 public sellPrice;
    uint256 public buyPrice;
    address public AddressOfTeamTokenVault;
    address public AddressOfMarketingTokenVault;
    address public AddressOfAirdropTokenVault;
    address public AddressOfBonusTokenVault;
    address public AddressOfSellTokenVault;
    
    mapping (address => bool) public frozenAccount;
    mapping (address => uint256) public frozenTime;
    
    /* This generates a public event on the blockchain that will notify clients */
    event FrozenFunds(address target, bool frozen);

    /* Initializes contract with initial supply tokens to the creator of the contract */
    constructor(
        uint256 initialSupply,
        string memory tokenName,
        string memory tokenSymbol,
        address teamTokenVault,
        address marketingTokenVault,
        address airdropTokenVault,
        address bonusTokenVault,
        address sellTokenVault
    ) ERC20_token(initialSupply, tokenName, tokenSymbol) public {
        // This is for LiongateX team. 15% of totalSupply
        AddressOfTeamTokenVault = teamTokenVault;
        balanceOf[teamTokenVault] = totalSupply / 100 * 15;
        
        // This is for marketing. 10% of totalSupply
        AddressOfMarketingTokenVault = marketingTokenVault;
        balanceOf[marketingTokenVault] = totalSupply / 10;
        
        // This is for airdrop. 10% of totalSupply
        AddressOfAirdropTokenVault = airdropTokenVault;
        balanceOf[airdropTokenVault] = totalSupply / 10;
        
        // This is for bonus. 10% of totalSupply
        AddressOfBonusTokenVault = bonusTokenVault;
        balanceOf[bonusTokenVault] = totalSupply / 10;
        
        // This is for selling. 55% of totalSupply
        AddressOfSellTokenVault = sellTokenVault;
        balanceOf[sellTokenVault] = totalSupply / 100 * 55;
    }
    
    /* Internal transfer, only can be called by this contract */
    function _transfer(address _from, address _to, uint _value) internal {
        require (_to != address(0x0)); // Prevent transfer to 0x0 address. Use burn() instead
        require (balanceOf[_from] >= _value); // Check if the sender has enough
        require (balanceOf[_to] + _value >= balanceOf[_to]); // Check for overflows
        require(!frozenAccount[_from]); // Check if sender is frozen
        require(!frozenAccount[_to]); // Check if recipient is frozen
        balanceOf[_from] -= _value; // Subtract from the sender
        balanceOf[_to] += _value; // Add the same to the recipient
        emit Transfer(_from, _to, _value);
    }
    
    /// @notice Create `mintedAmount` tokens and send it to `target`
    /// @param target Address to receive the tokens
    /// @param mintedAmount the amount of tokens it will receive
    function mintToken(address target, uint256 mintedAmount) onlyOwner public {
        balanceOf[target] += mintedAmount;
        totalSupply += mintedAmount;
        emit Transfer(address(0), address(this), mintedAmount);
        emit Transfer(address(this), target, mintedAmount);
    }
    
    /// @notice Prevent `target` from sending & receiving tokens
    /// @param target Address to be frozen
    function freezeAccount(address target) onlyOwner public {
        frozenAccount[target] = true;
        frozenTime[target] = now;
        emit FrozenFunds(target, true);
    }
    
    /// @notice Allow `target` from sending & receiving tokens
    /// @param target Address to be frozen
    function meltAccount(address target) onlyOwner public {
        require(now > frozenTime[target] + 182 days);
        frozenAccount[target] = false;
        emit FrozenFunds(target, false);
    }
    
    function LockTeamVault() onlyOwner public {
        freezeAccount(AddressOfTeamTokenVault);
    }
    
    function UnlockTeamVault() onlyOwner public {
        meltAccount(AddressOfTeamTokenVault);
    }
    
    /// @notice Allow users to buy tokens for `newBuyPrice` eth and sell tokens for `newSellPrice` eth
    /// @param newSellPrice Price the users can sell to the contract
    /// @param newBuyPrice Price users can buy from the contract
    function setPrices(uint256 newSellPrice, uint256 newBuyPrice) onlyOwner public {
        sellPrice = newSellPrice;
        buyPrice = newBuyPrice;
    }
    
    /// @notice Buy tokens from contract by sending ether
    function buy() payable public {
        uint amount = msg.value / buyPrice; // calculates the amount
        _transfer(address(this), msg.sender, amount); // makes the transfers
    }
    
    /// @notice Sell `amount` tokens to contract
    /// @param amount amount of tokens to be sold
    function sell(uint256 amount) public {
        address myAddress = address(this);
        require(myAddress.balance >= amount * sellPrice); // checks if the contract has enough ether to buy
        _transfer(msg.sender, address(this), amount); // makes the transfers
        msg.sender.transfer(amount * sellPrice); // sends ether to the seller. It's important to do this last to avoid recursion attacks
    }
}
