// File: contracts/rewards/RewardsFaucet.sol

pragma solidity 0.5.4;








contract RewardsFaucet is Pausable, ReentrancyGuard {
    event Released(address indexed from, uint amount);

    using SafeERC20 for IERC20;
    using SafeMath for uint;

    IRewards public rewards; // contract
    IERC20 public rewardsToken; // PAY tokens

    address public fundingSource; // Where PAY tokens are approved from
    uint public start; // Vesting period start timestamp in seconds since the epoch
    uint public duration; // Vesting period duration timestamp in seconds
    uint public totalAmount; // 4M PAY tokens
    uint public totalReleased;

    constructor(IRewards _rewards, IERC20 _rewardsToken, address _fundingSource, uint _totalAmount, uint _start, uint _duration) public {
        require(_fundingSource != address(0), "Funding source cannot be zero address.");
        require(_totalAmount > 0, "Total Amount cannot be zero.");

        rewards = _rewards;
        rewardsToken = _rewardsToken;
        fundingSource = _fundingSource;
        totalAmount = _totalAmount;
        start = _start;
        duration = _duration;
    }

    function () external payable { // Ether fallback function
        require(msg.value == 0, "Received non-zero msg.value.");
        release(); // solhint-disable-line
    }

    /**
    * @notice Release pending rewards
    */
    function release() public nonReentrant whenNotPaused {
        uint amount = releasableAmount();
        rewardsToken.safeTransferFrom(fundingSource, address(this), amount); // Top up this contract

        totalReleased = totalReleased.add(amount);
        emit Released(msg.sender, amount);
        rewardsToken.safeIncreaseAllowance(address(rewards), amount); // Approve funds to be taken from this contract
        rewards.deposit(amount); // TransferFrom funds to the Rewards pool
    }

    /**
    * @notice Returns amount that can be released so far
    */
    function releasableAmount() public view returns (uint) {
        return vestedAmount().sub(totalReleased);
    }

    /**
    * @notice Calculate amount of rewards vested so far
    */
    function vestedAmount() public view returns (uint) {
        if (block.timestamp <= start) {
            return 0;
        } else if (block.timestamp >= start.add(duration)) {
            return totalAmount;
        } else {
            return totalAmount.mul(vestedTime()).div(duration);
        }
    }

    /**
    * @notice Returns amount of time passed since start
    */
    function vestedTime() public view returns (uint) {
        uint currentTime = block.timestamp;
        return currentTime.sub(start);
    }

    function allowance() public view returns (uint) {
        return rewardsToken.allowance(fundingSource, address(this));
    }
}
