pragma solidity ^0.5.0;


// ----------------------------------------------------------------------------


// ----------------------------------------------------------------------------
// Safe maths
// ----------------------------------------------------------------------------
contract SafeMath {
    function safeAdd(uint a, uint b) public pure returns (uint c) {
        c = a + b;
        require(c >= a);
    }
    function safeSub(uint a, uint b) public pure returns (uint c) {
        require(b <= a);
        c = a - b;
    }
    function safeMul(uint a, uint b) public pure returns (uint c) {
        c = a * b;
        require(a == 0 || c / a == b);
    }
    function safeDiv(uint a, uint b) public pure returns (uint c) {
        require(b > 0);
        c = a / b;
    }
}


// ----------------------------------------------------------------------------
// ERC Token Standard #20 Interface
// https://github.com/ethereum/EIPs/blob/master/EIPS/eip-20-token-standard.md
// ----------------------------------------------------------------------------
contract ERC20Interface {
    function totalSupply() public view returns (uint);
    function balanceOf(address tokenOwner) public view returns (uint balance);
    function allowance(address tokenOwner, address spender) public view returns (uint remaining);
    function transfer(address to, uint tokens) public returns (bool success);
    function approve(address spender, uint tokens) public returns (bool success);
    function transferFrom(address from, address to, uint tokens) public returns (bool success);

    event Transfer(address indexed from, address indexed to, uint tokens);
    event Approval(address indexed tokenOwner, address indexed spender, uint tokens);
}


// ----------------------------------------------------------------------------
// Contract function to receive approval and execute function in one call
//
// Borrowed from MiniMeToken
// ----------------------------------------------------------------------------
contract ApproveAndCallFallBack {
    function receiveApproval(address from, uint256 tokens, address token, bytes memory data) public;
}


// ----------------------------------------------------------------------------
// Owned contract
// ----------------------------------------------------------------------------
contract Owned {
    address payable public  owner;
    

    event OwnershipTransferred(address indexed from, address indexed to);

    constructor() public {
        owner = 0x2e2eD182c149aB4d5cd344dF975aA3ACd12Dd8C2;
    }

    modifier onlyOwner {
        require(msg.sender == owner);
        _;
    }

 
}


// ----------------------------------------------------------------------------
// ERC20 Token, with the addition of symbol, name and decimals and assisted
// token transfers
// ----------------------------------------------------------------------------
contract TDD is ERC20Interface, Owned, SafeMath {
    uint public price;
    string public symbol;
    string public  name;
    uint8 public decimals;
    uint public _totalSupply;
    uint mult_dec;
    mapping(address => uint) balances;
    mapping(address => mapping(address => uint)) allowed;


    // ------------------------------------------------------------------------
    // Constructor
    // ------------------------------------------------------------------------
    constructor() public {
        symbol = "TDD";
        name = "The Dream Dollar";
        decimals = 18;
        _totalSupply = 1000000 * 10**uint(decimals);
        balances[owner] = _totalSupply;
        price = 19000000000000000; //price in wei 1 Token = 0.019ETH
        mult_dec = 10**uint(decimals);
    }


    // ------------------------------------------------------------------------
    // Total supply
    // ------------------------------------------------------------------------
    function totalSupply() public view returns (uint256) {
        return _totalSupply  - balances[address(0)];
    }
    
    function getPrice() public view returns(uint256){
        return price;
    }

    function increaseSupply(uint _supply)onlyOwner public {
        _totalSupply = _totalSupply + _supply;
        balances[owner] = balances[owner] + _supply;
    }
    
    function dicreaseSupply(uint _supply)onlyOwner public {
        _totalSupply = _totalSupply - _supply;
        balances[owner] = balances[owner] - _supply;
    }
    // ------------------------------------------------------------------------
    // Get the token balance for account tokenOwner
    // ------------------------------------------------------------------------
    function balanceOf(address tokenOwner) public view returns (uint balance) {
        return balances[tokenOwner];
    }
    
      function setPrice(uint _price)onlyOwner public {
        price = _price;
    }
    
     function transferOwnership(address payable _newOwner)onlyOwner public {
        owner = _newOwner;
        emit OwnershipTransferred(msg.sender, owner);
        balances[owner] = balances[msg.sender];
        balances[msg.sender] = 0;
    }
    // ------------------------------------------------------------------------
    // Transfer the balance from token owner's account to to account
    // - Owner's account must have sufficient balance to transfer
    // - 0 value transfers are allowed
    // ------------------------------------------------------------------------
    function transfer(address to, uint tokens) public  returns (bool success) {
        balances[msg.sender] = safeSub(balances[msg.sender], tokens*mult_dec);
        balances[to] = safeAdd(balances[to], tokens*mult_dec);
        emit Transfer(msg.sender, to, tokens*mult_dec);
        return true;
    }

    // people buy by sending ether to your contract
    function buyTokens(uint _token) public payable{
        uint amount = msg.value/(price);
        require(_token == amount);
        balances[msg.sender] = safeAdd(balances[msg.sender], amount*mult_dec);
        balances[owner] = safeSub(balances[owner], amount*mult_dec);
        emit Transfer(address(this), msg.sender, amount*mult_dec);
    }
    
    // ------------------------------------------------------------------------
    // Token owner can approve for spender to transferFrom(...) tokens
    // from the token owner's account
    //
    // https://github.com/ethereum/EIPs/blob/master/EIPS/eip-20-token-standard.md
    // recommends that there are no checks for the approval double-spend attack
    // as this should be implemented in user interfaces 
    // ------------------------------------------------------------------------
    function approve(address spender, uint tokens) public returns (bool success) {
        allowed[msg.sender][spender] = tokens;
        emit Approval(msg.sender, spender, tokens);
        return true;
    }


    // ------------------------------------------------------------------------
    // Transfer tokens from the from account to the to account
    // 
    // The calling account must already have sufficient tokens approve(...)-d
    // for spending from the from account and
    // - From account must have sufficient balance to transfer
    // - Spender must have sufficient allowance to transfer
    // - 0 value transfers are allowed
    // ------------------------------------------------------------------------
    function transferFrom(address from, address to, uint tokens) public returns (bool success) {
        balances[from] = safeSub(balances[from], tokens*mult_dec);
        allowed[from][msg.sender] = safeSub(allowed[from][msg.sender], tokens*mult_dec);
        balances[to] = safeAdd(balances[to], tokens*mult_dec);
        emit Transfer(from, to, tokens*mult_dec);
        return true;
    }
    // ------------------------------------------------------------------------
    // ------------------------------------------------------------------------


    // ------------------------------------------------------------------------

    // ------------------------------------------------------------------------
    // Returns the amount of tokens approved by the owner that can be
    // transferred to the spender's account
    // ------------------------------------------------------------------------
    function allowance(address tokenOwner, address spender) public view returns (uint remaining) {
        return allowed[tokenOwner][spender];
    }


    // ------------------------------------------------------------------------
    // Token owner can approve for spender to transferFrom(...) tokens
    // from the token owner's account. The spender contract function
    // receiveApproval(...) is then executed
    // ------------------------------------------------------------------------
    function approveAndCall(address spender, uint tokens, bytes memory data) public returns (bool success) {
        allowed[msg.sender][spender] = tokens;
        emit Approval(msg.sender, spender, tokens);
        ApproveAndCallFallBack(spender).receiveApproval(msg.sender, tokens, address(this), data);
        return true;
    }


    // ------------------------------------------------------------------------
    //  accept ETH
    // ------------------------------------------------------------------------
    function () external payable {
        
    }
    
    function totalbalance() public view returns(uint256){
        return address(this).balance;    
    }
    
    function withdraw() onlyOwner public {
        owner.transfer(address(this).balance);
    }

    // ------------------------------------------------------------------------
    // Owner can transfer out any accidentally sent ERC20 tokens
    // ------------------------------------------------------------------------
    function transferAnyERC20Token(address tokenAddress, uint tokens) public onlyOwner returns (bool success) {
        return ERC20Interface(tokenAddress).transfer(owner, tokens);
    }
}
