    string public constant ONLY_OWNER = "Only owner is allowed";
    string public constant RESTRICTED_AREA = "You are in a restricted area";

    function initialize(
        string memory name, string memory symbol, uint8 decimals, uint256 initialSupply, address initialHolder,
        address[] memory minters, address[] memory pausers
    ) public initializer
    {
        Ownable.initialize(initialHolder);
        ERC20Detailed.initialize(name, symbol, decimals);

        // Mint the initial supply
        uint256 computedValue = initialSupply * 10**uint256(decimals);
        _mint(initialHolder, computedValue);

        // Initialize the minter and pauser roles, and renounce them
        MaharlikaERC20Mintable.initialize(address(this));
        _removeMinter(address(this));

        ERC20Pausable.initialize(address(this));
        _removePauser(address(this));

        // Add the requested minters and pausers (this can be done after renouncing since
        // these are the internal calls)
        for (uint256 i = 0; i < minters.length; ++i) {
            _addMinter(minters[i]);
        }

        for (uint256 i = 0; i < pausers.length; ++i) {
            _addPauser(pausers[i]);
        }
    }
    function removeMinter(address _address) public onlyOwner {
        _removeMinter(_address);
    }

    function removePauser(address _address) public onlyOwner {
        _removePauser(_address);
    }

