contract MUZGameContract is owned {
    using strings for *;
    uint nonce = 0;
    string public name;
    event eventOpenLottery(address _gameId);
    struct Game{
        address addr;
        mapping (uint8 => uint256)  levelPersons;
        mapping (uint8 => uint256)  levelRewards;
        uint256 poolTokens ;
        uint8 index;
        uint256 leftAmount;
        uint8 isEnd;
        
        mapping (uint8 => string[])  winPersons;
    }
    
    struct PoolUsers{
        string addr;
        string userId;
        uint256 tokens;
    }
    
    mapping (address => Game) public gameList;

    mapping (address => PoolUsers[])  gameUserList;

    /* Initializes contract to the creator of the contract */
    function MUZGameContract (
        string gameName
    ) payable public {
		name = gameName;
    }

   
    function setGame(address _gameId,uint8 level,uint256 persons,uint256 rewardsnumber,uint256 poolrewards) onlyOwner public  returns(bool){

        Game _game = gameList[_gameId];
        _game.addr = _gameId;
        _game.poolTokens = poolrewards;
        _game.index = 1;
        _game.levelPersons[level] = persons;  
        _game.levelRewards[level] = rewardsnumber;
        _game.poolTokens = poolrewards;
        _game.leftAmount = 0;
        _game.isEnd = 0;
        gameList[_gameId] = _game;
        return true;
    }
    
    function sendUsers(address _gameId,string gamer,string uid,uint256 amount) onlyOwner public  returns(bool){
        require(gameList[_gameId].isEnd == 0); 
        PoolUsers[]  _pooluser = gameUserList[_gameId];
        PoolUsers memory p =  PoolUsers ({addr:gamer,userId:uid,tokens:amount});
        _pooluser.push(p);
        gameList[_gameId].leftAmount += amount;
        startLottery(_gameId);
    }
    
    
    function startLottery(address _gameId) internal{
        if (gameList[_gameId].leftAmount<gameList[_gameId].poolTokens){
            return;
        }
        uint256 totallen= gameUserList[_gameId].length-1; 
        Game _game = gameList[_gameId];
        PoolUsers[]  _pooluser = gameUserList[_gameId];
        
        uint256 one = _game.levelPersons[1];
        uint256 two = _game.levelPersons[2];
        uint256 three = _game.levelPersons[3];
       
        //first
       for (uint i = 0; i < one; i++) {
       	  uint256 rewardid = rand(totallen);
       	  string uid = _pooluser[rewardid].userId;
       	  string uidaddr = _pooluser[rewardid].addr;
       	  _game.winPersons[1].push(uid);
       	  
       }
       
       //two
       for ( i = 0; i < two; i++) {
       	   rewardid = rand(totallen);
       	    uid = _pooluser[rewardid].userId;
       	      uidaddr = _pooluser[rewardid].addr;
       	     _game.winPersons[2].push(uid);
       	  
       }
       
       //three
      for ( i = 0; i < three; i++) {
       	   rewardid = rand(totallen);
       	    uid = _pooluser[rewardid].userId;
       	    uidaddr = _pooluser[rewardid].addr;
       	    _game.winPersons[3].push(uid);
       }
      
        gameList[_gameId].isEnd = 1;
        
        eventOpenLottery(_gameId);
    }
    
    function queryPrize(address _gameId,uint8 level)  public  returns(string){
        Game _game = gameList[_gameId];
        var s="";
    	for(uint256 i=0;i<_game.winPersons[level].length;i++){
    	    s=s.toSlice().concat(_game.winPersons[level][i].toSlice());
    	    s=s.toSlice().concat(",".toSlice());
    	}
    	return s;
    } 

    function queryParticipant(address _gameId)  public  returns(string){
    	PoolUsers[] uers =  gameUserList[_gameId];
    	 var s="";
    	for(uint i=0;i<uers.length;i++){
    	    s=s.toSlice().concat(uers[i].userId.toSlice());
    	}
    	return s;
    }
   
    function rand(uint256 _length) internal view returns(uint256) {
       nonce ++;
       uint256 random = uint256(keccak256(now, msg.sender,nonce)) % _length;
       return  random;
    }
    
