pragma solidity ^0.5.0;






contract OwnableDelegateProxy { }

contract ProxyRegistry {
    mapping(address => OwnableDelegateProxy) public proxies;
}

contract ArtminerToken is ERC721Full, Ownable, Pausable, GSNRecipient {
    using Strings for string;

    /**
     * @dev Emitted when the base token URI is changed.
     */
    event BaseTokenURIChanged(address indexed account, string newUri);

    address _proxyRegistryAddress;

    string private _baseTokenUri;

    uint256 private _currentTokenId = 0;

    constructor(address proxyRegistryAddress, string memory baseTokenUri) ERC721Full("Artminer", "ARM") public {
        _proxyRegistryAddress = proxyRegistryAddress;
        _baseTokenUri = baseTokenUri;
    }

    function tokenURI(uint256 tokenId) external view returns (string memory) {
        return Strings.strConcat(
            baseTokenURI(),
            Strings.uint2str(tokenId)
        );
    }

    function acceptRelayedCall(
        address relay,
        address from,
        bytes calldata encodedFunction,
        uint256 transactionFee,
        uint256 gasPrice,
        uint256 gasLimit,
        uint256 nonce,
        bytes calldata approvalData,
        uint256 maxPossibleCharge
    ) external view returns (uint256, bytes memory) {
        return _approveRelayedCall();
    }

    /**
    * @dev Mints a token to an address with a tokenURI.
    * @param to address of the future owner of the token
    */
    function mintTo(address to) public onlyOwner whenNotPaused {
        uint256 newTokenId = _getNextTokenId();
        _mint(to, newTokenId);
        _incrementTokenId();
    }

    function changeBaseTokenURI(string memory baseTokenUri) public onlyOwner {
        _baseTokenUri = baseTokenUri;

        emit BaseTokenURIChanged(_msgSender(), _baseTokenUri);
    }

    function baseTokenURI() public view returns (string memory) {
        return _baseTokenUri;
    }

    /**
     * Override isApprovedForAll to whitelist user's OpenSea proxy accounts to enable gas-less listings.
     */
    function isApprovedForAll(address owner, address operator) public view returns (bool) {
        // Whitelist OpenSea proxy contract for easy trading.
        ProxyRegistry proxyRegistry = ProxyRegistry(_proxyRegistryAddress);
        if (address(proxyRegistry.proxies(owner)) == operator) {
            return true;
        }

        return super.isApprovedForAll(owner, operator);
    }

    /**
     * @dev calculates the next token ID based on value of _currentTokenId
     * @return uint256 for the next token ID
     */
    function _getNextTokenId() private view returns (uint256) {
        return _currentTokenId.add(1);
    }

    /**
     * @dev increments the value of _currentTokenId
     */
    function _incrementTokenId() private  {
        _currentTokenId++;
    }
}
