library Lender {
    using SafeMath for uint256;

    struct State {
        address liquidityProvider;
        mapping (address=>uint256) deposits;
    }

    event Lent(address indexed _token, uint256 _amount, uint256 _internalAmount);
    event Redeemed(address indexed _token, uint256 _amount, uint256 _internalAmount);
    event Borrowed(address indexed _token, uint256 _amount);
    event Repaid(address indexed _token, uint256 _amount);
    event UpdatedLiquidityProvider(address indexed _oldLiquidityProvider, address indexed _newLiquidityProvider);

    function init(State storage _state, address _liquidityProvider) public {
        _state.liquidityProvider = _liquidityProvider;
    }

    function updateLiquidityProvider(State storage _state, address _newLiquidityProvider) public {
        emit UpdatedLiquidityProvider(_state.liquidityProvider, _newLiquidityProvider);
        _state.liquidityProvider = _newLiquidityProvider;
    }

    function lend(State storage _state, address _token, uint256 _amount, uint256 _internalAmount) public {
        _state.deposits[_token] = _state.deposits[_token].add(_internalAmount);
        emit Lent(_token, _amount, _internalAmount);
    }

    function redeem(State storage _state, address _token, uint256 _amount, uint256 _internalAmount) public {
        _state.deposits[_token] = _state.deposits[_token].sub(_internalAmount);
        emit Redeemed(_token, _amount, _internalAmount);
    }

    function borrow(address _token, uint256 _amount) public {
        emit Borrowed(_token, _amount);
    }

    function repay(address _token, uint256 _amount) public {
        emit Repaid(_token, _amount);
    }

    function balanceOf(State storage _state, address _token) public view returns (uint256) {
        return _state.deposits[_token];
    }
}

library Liquidator {
    using SafeMath for uint256;

    struct State {
        address rebalancer;
        mapping (address=>uint256) collateral;
    }

    event Liquidated(address indexed _liquidatedToken, address indexed _collateralToken, uint256 _liquidatedAmount, uint256 _collateralAmount);
    event Taken(address indexed _unbalancedToken, uint256 _unbalancedAmount);
    event UpdatedRebalancer(address indexed _oldRebalancer, address indexed _newRebalancer);

    function init(State storage _state, address _rebalancer) public {
        _state.rebalancer = _rebalancer;
    }

    function updateRebalancer(State storage _state, address _newRebalancer) public {
        emit UpdatedRebalancer(_state.rebalancer, _newRebalancer);
        _state.rebalancer = _newRebalancer;
    }

    function liquidate(State storage _state,
    address _liquidatedToken, uint256 _liquidatedAmount, address _collateralToken,  uint256 _collateralAmount) public {
        _state.collateral[_collateralToken] = _state.collateral[_collateralToken].add(_collateralAmount);
        emit Liquidated(_liquidatedToken, _collateralToken, _liquidatedAmount, _collateralAmount);
    }

    function take(State storage _state, address _unbalancedToken, uint256 _unbalancedAmount) public {
        _state.collateral[_unbalancedToken] = _state.collateral[_unbalancedToken].sub(_unbalancedAmount);
        emit Taken(_unbalancedToken, _unbalancedAmount);
    }

    function balanceOf(State storage _state, address _token) public view returns (uint256) {
        return _state.collateral[_token];
    }
}

interface ILender {
    modifier isLiquidityProvider(Lender.State storage _state, address _caller) {
        if (_caller != _state.liquidityProvider) revert("Caller is not the liquidity provider");
        _;
    }

    function lend(address _token, uint256 _amount) external payable returns (uint256);
    function redeem(address payable _to, address _token, uint256 _amount) external returns (uint256);
    function borrow(address payable _to, address _token, uint256 _amount) external;
    function repay(address _token, uint256 _amount) external payable;
    function balanceOf(address _token) external view returns (uint256);
}

interface ILiquidator {
    modifier isRebalancer(Liquidator.State storage _state, address _caller) {
        if (_caller != _state.rebalancer) revert("Caller is not the rebalancer");
        _;
    }

    function liquidate(
        address _borrower,
        address _collateralMarket,
        address _borrowedMarket,
        uint256 _minAmount,
        bytes calldata _extraData
    ) external payable;

    function take(address _unbalancedToken, uint256 _unbalancedAmount) external returns (uint256);

    function unbalancedBalance(address _token) external view returns (uint256);
}

